import { useState, FormEvent } from "react";
import { Link, useNavigate } from "react-router-dom";
import { useAuth } from "../contexts/AuthContext";

function Signup() {
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  const [confirmPassword, setConfirmPassword] = useState("");
  const [isLoading, setIsLoading] = useState(false);
  const [error, setError] = useState("");
  const { signup } = useAuth();
  const navigate = useNavigate();

  const handleSubmit = async (e: FormEvent) => {
    e.preventDefault();
    setError("");

    if (username.length < 3) {
      setError("Username must be at least 3 characters");
      return;
    }

    if (password !== confirmPassword) {
      setError("Passwords do not match");
      return;
    }

    if (password.length < 6) {
      setError("Password must be at least 6 characters");
      return;
    }

    setIsLoading(true);

    try {
      await signup(username, password);
      navigate("/");
    } catch (err) {
      setError("Failed to create account. Please try again.");
    } finally {
      setIsLoading(false);
    }
  };

  return (
    <div className="bg-white dark:bg-gray-800 rounded-lg shadow-xl p-8">
      <h2 className="text-2xl font-bold text-gray-900 dark:text-white mb-6">
        Create Account
      </h2>

      {error && (
        <div className="mb-4 p-3 bg-red-100 border border-red-400 text-red-700 rounded">
          {error}
        </div>
      )}

      <form onSubmit={handleSubmit} className="space-y-4">
        <div>
          <label
            htmlFor="username"
            className="block text-sm font-medium text-gray-700 dark:text-gray-300 mb-2"
          >
            Username
          </label>
          <input
            id="username"
            type="text"
            required
            value={username}
            onChange={(e) => setUsername(e.target.value)}
            className="w-full px-4 py-2 border border-gray-300 dark:border-gray-600 rounded-md focus:ring-2 focus:ring-blue-500 focus:border-transparent dark:bg-gray-700 dark:text-white"
            placeholder="username"
            minLength={3}
          />
        </div>

        <div>
          <label
            htmlFor="password"
            className="block text-sm font-medium text-gray-700 dark:text-gray-300 mb-2"
          >
            Password
          </label>
          <input
            id="password"
            type="password"
            required
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            className="w-full px-4 py-2 border border-gray-300 dark:border-gray-600 rounded-md focus:ring-2 focus:ring-blue-500 focus:border-transparent dark:bg-gray-700 dark:text-white"
            placeholder="••••••••"
          />
        </div>

        <div>
          <label
            htmlFor="confirmPassword"
            className="block text-sm font-medium text-gray-700 dark:text-gray-300 mb-2"
          >
            Confirm Password
          </label>
          <input
            id="confirmPassword"
            type="password"
            required
            value={confirmPassword}
            onChange={(e) => setConfirmPassword(e.target.value)}
            className="w-full px-4 py-2 border border-gray-300 dark:border-gray-600 rounded-md focus:ring-2 focus:ring-blue-500 focus:border-transparent dark:bg-gray-700 dark:text-white"
            placeholder="••••••••"
          />
        </div>

        <button
          type="submit"
          disabled={isLoading}
          className="w-full py-2 px-4 bg-blue-600 hover:bg-blue-700 text-white font-medium rounded-md transition-colors disabled:bg-gray-400 disabled:cursor-not-allowed"
        >
          {isLoading ? "Creating account..." : "Sign Up"}
        </button>
      </form>

      <div className="mt-6 text-center">
        <p className="text-gray-600 dark:text-gray-400">
          Already have an account?{" "}
          <Link
            to="/login"
            className="text-blue-600 hover:text-blue-700 font-medium"
          >
            Sign in
          </Link>
        </p>
      </div>
    </div>
  );
}

export default Signup;

import { createContext, useContext, useState, ReactNode } from "react";
import { toast } from "sonner";
import { useNavigate } from "react-router-dom";

interface User {
  id: number;
  username: string;
}

interface AuthContextType {
  user: User | null;
  isAuthenticated: boolean;
  login: (username: string, password: string) => Promise<void>;
  signup: (username: string, password: string) => Promise<void>;
  logout: () => void;
  handleInvalidToken: () => void;
}

const AuthContext = createContext<AuthContextType | undefined>(undefined);

export function AuthProvider({ children }: { children: ReactNode }) {
  const navigate = useNavigate();
  const [user, setUser] = useState<User | null>(() => {
    // Check if user is stored in localStorage
    const storedUser = localStorage.getItem("user");
    return storedUser ? JSON.parse(storedUser) : null;
  });

  const login = async (username: string, password: string) => {
    try {
      const response = await fetch("/api/auth/login", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify({ username, password }),
      });

      if (!response.ok) {
        const error = await response.json();
        throw new Error(error.error || "Login failed");
      }

      const data = await response.json();

      // Store token and user in localStorage
      localStorage.setItem("token", data.token);
      localStorage.setItem("user", JSON.stringify(data.user));
      setUser(data.user);

      toast.success("Logged in successfully");
    } catch (error) {
      const message = error instanceof Error ? error.message : "Login failed";
      toast.error(message);
      throw error;
    }
  };

  const signup = async (username: string, password: string) => {
    try {
      const response = await fetch("/api/auth/register", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify({ username, password }),
      });

      if (!response.ok) {
        const error = await response.json();
        throw new Error(error.error || "Registration failed");
      }

      const data = await response.json();

      // Store token and user in localStorage
      localStorage.setItem("token", data.token);
      localStorage.setItem("user", JSON.stringify(data.user));
      setUser(data.user);

      toast.success("Account created successfully");
    } catch (error) {
      const message = error instanceof Error ? error.message : "Registration failed";
      toast.error(message);
      throw error;
    }
  };

  const logout = () => {
    setUser(null);
    localStorage.removeItem("user");
    localStorage.removeItem("token");
    toast.success("Logged out successfully");
  };

  const handleInvalidToken = () => {
    setUser(null);
    localStorage.removeItem("user");
    localStorage.removeItem("token");
    toast.error("Session expired. Please log in again.");
    navigate("/login");
  };

  return (
    <AuthContext.Provider
      value={{
        user,
        isAuthenticated: !!user,
        login,
        signup,
        logout,
        handleInvalidToken,
      }}
    >
      {children}
    </AuthContext.Provider>
  );
}

export function useAuth() {
  const context = useContext(AuthContext);
  if (context === undefined) {
    throw new Error("useAuth must be used within an AuthProvider");
  }
  return context;
}

import type { FastifyInstance } from 'fastify';
import { Prisma } from '@prisma/client';
import bcrypt from 'bcrypt';
import { prisma } from '../../shared/prisma';
import { registerSchema, loginSchema } from '../schemas/auth.schema';
import { authResponseSchema, authStatusResponseSchema, userResponseSchema, errorResponse } from '../schemas/response.schema';

export async function authRoutes(fastify: FastifyInstance) {
  // Check auth status and setup requirements
  fastify.get('/api/auth/status', {
    schema: {
      response: {
        200: authStatusResponseSchema,
      },
    },
  }, async (request, reply) => {
    try {
      const userCount = await prisma.user.count();
      const needsSetup = userCount === 0;

      return reply.send({
        needsSetup,
        isAuthenticated: false, // Will be overridden by frontend if token exists
      });
    } catch (error) {
      fastify.log.error({ err: error }, 'Auth status error');
      return reply.code(500).send({
        error: {
          message: 'Internal server error',
          statusCode: 500,
        },
      });
    }
  });

  // User registration (setup) - only allowed if no users exist
  fastify.post<{
    Body: { username: string; password: string };
  }>('/api/auth/register', {
    config: {
      rateLimit: {
        max: 5,
        timeWindow: '1 minute',
      },
    },
    schema: {
      body: registerSchema,
      response: {
        200: authResponseSchema,
        403: errorResponse,
        409: errorResponse,
      },
    },
  }, async (request, reply) => {
    const { username, password } = request.body;

    // Check if users already exist (only allow one user)
    const existingUserCount = await prisma.user.count();
    if (existingUserCount > 0) {
      return reply.code(403).send({
        error: {
          message: 'User already exists. This is a single-user system.',
          statusCode: 403,
        },
      });
    }

    try {
      // Hash password
      const saltRounds = 12;
      const password_hash = await bcrypt.hash(password, saltRounds);

      // Create user
      const user = await prisma.user.create({
        data: {
          username,
          password_hash,
          last_login: new Date(),
        },
        select: {
          id: true,
          username: true,
        },
      });

      // Generate token (no expiration)
      const token = fastify.jwt.sign(
        {
          userId: user.id,
          username: user.username,
        },
        // No expiration - token lasts forever
      );

      return reply.send({
        success: true,
        user,
        token,
      });
    } catch (error) {
      // Check for unique constraint violation (Prisma error)
      if (error instanceof Prisma.PrismaClientKnownRequestError) {
        if (error.code === 'P2002') {
          return reply.code(409).send({
            error: {
              message: 'Username already exists',
              code: 'DUPLICATE_USERNAME',
              statusCode: 409,
            },
          });
        }
      }

      throw error;
    }
  });

  // User login
  fastify.post<{
    Body: { username: string; password: string };
  }>('/api/auth/login', {
    config: {
      rateLimit: {
        max: 5,
        timeWindow: '1 minute',
      },
    },
    schema: {
      body: loginSchema,
      response: {
        200: authResponseSchema,
        401: errorResponse,
        403: errorResponse,
      },
    },
  }, async (request, reply) => {
    const { username, password } = request.body;

    // Get user from database
    const user = await prisma.user.findUnique({
      where: { username },
    });

    if (!user) {
      return reply.code(401).send({
        error: {
          message: 'Invalid username or password',
          statusCode: 401,
        },
      });
    }

    // Verify password
    const isValidPassword = await bcrypt.compare(password, user.password_hash);
    if (!isValidPassword) {
      return reply.code(401).send({
        error: {
          message: 'Invalid username or password',
          statusCode: 401,
        },
      });
    }

    // Check if user is active
    if (!user.is_active) {
      return reply.code(403).send({
        error: {
          message: 'Account is inactive',
          statusCode: 403,
        },
      });
    }

    // Generate token (no expiration)
    const token = fastify.jwt.sign(
      {
        userId: user.id,
        username: user.username,
      },
      // No expiration - token lasts forever
    );

    // Update last login
    await prisma.user.update({
      where: { id: user.id },
      data: { last_login: new Date() },
    });

    return reply.send({
      success: true,
      user: {
        id: user.id,
        username: user.username,
      },
      token,
    });
  });

  // Get current user (protected route)
  fastify.get('/api/auth/user', {
    preHandler: fastify.authenticate,
    schema: {
      response: {
        200: userResponseSchema,
      },
    },
  }, async (request, reply) => {
    return reply.send({
      data: request.user,
    });
  });

  // Logout (client-side token removal, but this endpoint exists for consistency)
  fastify.post('/api/auth/logout', {
    preHandler: fastify.authenticate,
  }, async (request, reply) => {
    // In a simple JWT system, logout is mainly client-side
    // This endpoint exists for consistency and potential future logging
    return reply.send({
      success: true,
      message: 'Logged out successfully',
    });
  });
}
