     1→/**
     2→ * Clickable file reference badge
     3→ * Displays file path and optional line number
     4→ */
     5→
     6→import { FileText, FileCode } from 'lucide-react';
     7→import { Badge } from '../ui/badge';
     8→
     9→interface FileReferenceProps {
    10→  filePath: string;
    11→  lineNumber?: number;
    12→  className?: string;
    13→}
    14→
    15→export function FileReference({ filePath, lineNumber, className = '' }: FileReferenceProps) {
    16→  // Extract filename from path
    17→  const filename = filePath.split('/').pop() || filePath;
    18→  const extension = filename.split('.').pop()?.toLowerCase() || '';
    19→
    20→  // Determine icon based on file type
