/**
 * Chat UI types aligned with agent-cli-sdk StreamEvent format
 * Based on Anthropic's Claude API message structure
 */

// Re-export StreamEvent from agent-cli-sdk for consistency
export type { StreamEvent } from '@repo/agent-cli-sdk';

// Re-export Claude CLI session types
export type {
  ClaudeSessionRow,
  ClaudeSessionData,
  ClaudeFileHistorySnapshotRow,
  ClaudeUserMessageRow,
  ClaudeAssistantMessageRow,
  ClaudeMessage,
  ClaudeContentBlock,
  ClaudeFileBackup,
  ClaudeFileHistorySnapshot,
  ClaudeThinkingMetadata,
  ClaudeToolUseResult,
} from './claude-session.types';

export {
  isFileHistorySnapshot,
  isUserMessage,
  isAssistantMessage,
} from './claude-session.types';

/**
 * Message role types
 */
export type MessageRole = 'user' | 'assistant' | 'system';

/**
 * Content block base type
 */
export interface ContentBlockBase {
  type: string;
}

/**
 * Text content block
 */
export interface TextBlock extends ContentBlockBase {
  type: 'text';
  text: string;
}

/**
 * Thinking/reasoning content block
 */
export interface ThinkingBlock extends ContentBlockBase {
  type: 'thinking';
  thinking: string;
}

/**
 * Tool use (function call) content block
 */
export interface ToolUseBlock extends ContentBlockBase {
  type: 'tool_use';
  id: string;
  name: string;
  input: Record<string, unknown>;
}

/**
 * Tool result content block
 */
export interface ToolResultBlock extends ContentBlockBase {
  type: 'tool_result';
  tool_use_id: string;
  content: string;
  is_error?: boolean;
}

/**
 * Union type for all content blocks
 */
export type ContentBlock = TextBlock | ThinkingBlock | ToolUseBlock | ToolResultBlock;

/**
 * Chat message structure
 */
export interface ChatMessage {
  id: string;
  role: MessageRole;
  content: ContentBlock[];
  timestamp: number;
  isStreaming?: boolean;
}

/**
 * Tool call with linked result (for rendering)
 */
export interface ToolCall {
  id: string;
  name: string;
  input: Record<string, unknown>;
  result?: {
    content: string;
    is_error?: boolean;
  };
}

/**
 * Tool-specific input types
 */

export interface EditToolInput {
  file_path: string;
  old_string: string;
  new_string: string;
  replace_all?: boolean;
}

export interface WriteToolInput {
  file_path: string;
  content: string;
}

export interface ReadToolInput {
  file_path: string;
  offset?: number;
  limit?: number;
}

export interface BashToolInput {
  command: string;
  description?: string;
  timeout?: number;
}

export interface GlobToolInput {
  pattern: string;
  path?: string;
}

export interface GrepToolInput {
  pattern: string;
  path?: string;
  output_mode?: 'content' | 'files_with_matches' | 'count';
}

/**
 * Thinking/reasoning block
 * Collapsible display of Claude's internal reasoning
 */

import { useState } from 'react';
import { Brain, ChevronDown, ChevronRight } from 'lucide-react';
import { Collapsible, CollapsibleContent, CollapsibleTrigger } from '../ui/collapsible';
import { Button } from '../ui/button';

interface ThinkingBlockProps {
  thinking: string;
  className?: string;
}

export function ThinkingBlock({ thinking, className = '' }: ThinkingBlockProps) {
  const [isOpen, setIsOpen] = useState(false);

  return (
    <Collapsible open={isOpen} onOpenChange={setIsOpen} className={className}>
      <div className="rounded-lg border border-purple-200 dark:border-purple-800 bg-purple-50/50 dark:bg-purple-950/20">
        {/* Trigger */}
        <CollapsibleTrigger asChild>
          <Button
            variant="ghost"
            className="w-full justify-start px-4 py-3 hover:bg-purple-100 dark:hover:bg-purple-900/20"
          >
            <div className="flex items-center gap-2 text-sm">
              {isOpen ? (
                <ChevronDown className="h-4 w-4 text-purple-600 dark:text-purple-400" />
              ) : (
                <ChevronRight className="h-4 w-4 text-purple-600 dark:text-purple-400" />
              )}
              <Brain className="h-4 w-4 text-purple-600 dark:text-purple-400" />
              <span className="font-medium text-purple-700 dark:text-purple-300">
                Thinking...
              </span>
            </div>
          </Button>
        </CollapsibleTrigger>

        {/* Content */}
        <CollapsibleContent>
          <div className="px-4 pb-3 pt-1 border-t border-purple-200 dark:border-purple-800">
            <pre className="whitespace-pre-wrap break-words text-sm italic text-purple-900 dark:text-purple-200 font-sans">
              {thinking}
            </pre>
          </div>
        </CollapsibleContent>
      </div>
    </Collapsible>
  );
}

