/**
 * Renderer for Write tool input
 * Shows file path and new file content
 */

import type { WriteToolInput } from '../../../shared/types/chat';
import { FileReference } from '../FileReference';
import { CodeBlock } from '../CodeBlock';
import { getLanguageFromPath } from '../../../utils/getLanguageFromPath';

interface WriteToolRendererProps {
  input: WriteToolInput;
}

export function WriteToolRenderer({ input }: WriteToolRendererProps) {
  const language = getLanguageFromPath(input.file_path);
  const lineCount = input.content.split('\n').length;

  return (
    <div className="space-y-3">
      <div className="flex items-center gap-2">
        <span className="text-sm text-muted-foreground">New file:</span>
        <FileReference filePath={input.file_path} />
      </div>
      <CodeBlock
        code={input.content}
        language={language}
        collapsedByDefault={lineCount > 20}
      />
    </div>
  );
}

/**
 * Code block with syntax highlighting, copy button, and collapse support
 */

import { useState } from 'react';
import { Copy, Check, ChevronDown, ChevronRight } from 'lucide-react';
import { Button } from '../ui/button';
import { Badge } from '../ui/badge';
import { Collapsible, CollapsibleContent, CollapsibleTrigger } from '../ui/collapsible';
import { SyntaxHighlighter } from '../../utils/syntaxHighlighter';
import { getLanguageDisplayName } from '../../utils/getLanguageFromPath';

interface CodeBlockProps {
  code: string;
  language: string;
  showLineNumbers?: boolean;
  collapsedByDefault?: boolean;
  className?: string;
}

const MAX_LINES_BEFORE_COLLAPSE = 20;

export function CodeBlock({
  code,
  language,
  showLineNumbers = false,
  collapsedByDefault = false,
  className = ''
}: CodeBlockProps) {
  const [copied, setCopied] = useState(false);
  const lineCount = code.split('\n').length;
  const shouldCollapse = collapsedByDefault && lineCount > MAX_LINES_BEFORE_COLLAPSE;
  const [isOpen, setIsOpen] = useState(!shouldCollapse);

  const handleCopy = async () => {
    try {
      await navigator.clipboard.writeText(code);
      setCopied(true);
      setTimeout(() => setCopied(false), 2000);
    } catch (error) {
      console.error('Failed to copy code:', error);
    }
  };

  const content = (
    <div className={`rounded-lg border bg-muted/50 overflow-hidden ${className}`}>
      {/* Header */}
      <div className="flex items-center justify-between px-4 py-2 border-b bg-muted/30">
        <div className="flex items-center gap-2">
          {shouldCollapse && (
            <CollapsibleTrigger asChild>
              <Button variant="ghost" size="sm" className="h-6 w-6 p-0">
                {isOpen ? <ChevronDown className="h-4 w-4" /> : <ChevronRight className="h-4 w-4" />}
              </Button>
            </CollapsibleTrigger>
          )}
          <Badge variant="secondary" className="text-xs font-mono">
            {getLanguageDisplayName(language)}
          </Badge>
          {lineCount > 1 && (
            <span className="text-xs text-muted-foreground">
              {lineCount} lines
            </span>
          )}
        </div>
        <Button
          variant="ghost"
          size="sm"
          className="h-7 px-2 text-xs"
          onClick={handleCopy}
        >
          {copied ? (
            <>
              <Check className="h-3 w-3 mr-1" />
              Copied
            </>
          ) : (
            <>
              <Copy className="h-3 w-3 mr-1" />
              Copy
            </>
          )}
        </Button>
      </div>

      {/* Code content */}
      <div className="overflow-x-auto max-h-[500px] overflow-y-auto">
        <SyntaxHighlighter
          code={code}
          language={language}
          showLineNumbers={showLineNumbers}
          className="text-sm"
        />
      </div>
    </div>
  );

  if (shouldCollapse) {
    return (
      <Collapsible open={isOpen} onOpenChange={setIsOpen}>
        <CollapsibleContent>
          {content}
        </CollapsibleContent>
      </Collapsible>
    );
  }

  return content;
}

/**
 * Assistant message component
 * Left-aligned with AI avatar
 */

import { Bot, Loader2 } from 'lucide-react';
import { Avatar, AvatarFallback } from '../ui/avatar';
import type { ChatMessage } from '../../shared/types/chat';
import { ContentBlockRenderer } from './ContentBlockRenderer';

interface AssistantMessageProps {
  message: ChatMessage;
  toolResults?: Map<string, { content: string; is_error?: boolean }>;
}

export function AssistantMessage({ message, toolResults }: AssistantMessageProps) {
  // Format timestamp
  const formattedTime = new Date(message.timestamp).toLocaleTimeString('en-US', {
    hour: '2-digit',
    minute: '2-digit'
  });

  return (
    <div className="flex gap-3 mb-4">
      {/* Avatar */}
      <Avatar className="h-10 w-10 flex-shrink-0">
        <AvatarFallback className="bg-primary text-primary-foreground">
          <Bot className="h-5 w-5" />
        </AvatarFallback>
      </Avatar>

      {/* Content */}
      <div className="flex-1 space-y-3 min-w-0">
        {/* Header */}
        <div className="flex items-center gap-2">
          <span className="text-sm font-medium">Assistant</span>
          <span className="text-xs text-muted-foreground">{formattedTime}</span>
          {message.isStreaming && (
            <div className="flex items-center gap-1 text-xs text-muted-foreground">
              <Loader2 className="h-3 w-3 animate-spin" />
              Streaming...
            </div>
          )}
        </div>

        {/* Content blocks */}
        <div className="space-y-3">
          {message.content.map((block, index) => (
            <ContentBlockRenderer
              key={index}
              block={block}
              toolResults={toolResults}
            />
          ))}
        </div>
      </div>
    </div>
  );
}

/**
 * User message component
 * Right-aligned with blue bubble design
 */

import type { ChatMessage, TextBlock, ToolResultBlock } from '../../shared/types/chat';

interface UserMessageProps {
  message: ChatMessage;
}

export function UserMessage({ message }: UserMessageProps) {
  // Extract text content from content blocks
  const textBlocks = message.content.filter(
    (block): block is TextBlock => block.type === 'text'
  );

  // Extract tool result blocks
  const toolResultBlocks = message.content.filter(
    (block): block is ToolResultBlock => block.type === 'tool_result'
  );

  // Format timestamp
  const formattedTime = new Date(message.timestamp).toLocaleTimeString(
    'en-US',
    {
      hour: '2-digit',
      minute: '2-digit',
    }
  );

  // If message only contains tool results (no text), don't render
  // Tool results are already shown inline with the assistant's tool_use blocks
  const hasText = textBlocks.length > 0;
  const hasToolResults = toolResultBlocks.length > 0;

  // Hide messages that only contain tool results (API plumbing, already shown in assistant message)
  if (!hasText && hasToolResults) {
    return null;
  }

  return (
    <div className="flex justify-end mb-4">
      <div className="max-w-[80%] space-y-2">
        {/* Timestamp */}
        <div className="text-xs text-muted-foreground text-right pr-1">
          {formattedTime}
        </div>

        {/* Text content */}
        {hasText && (
          <div className="rounded-lg bg-primary text-primary-foreground px-4 py-3 shadow-sm">
            <div className="whitespace-pre-wrap wrap-break-word text-sm">
              {textBlocks.map((block: TextBlock) => block.text).join('\n\n')}
            </div>
          </div>
        )}
      </div>
    </div>
  );
}

