/**
 * Code block with syntax highlighting, copy button, and collapse support
 */

import { useState } from 'react';
import { Copy, Check, ChevronDown, ChevronRight } from 'lucide-react';
import { Button } from '../ui/button';
import { Badge } from '../ui/badge';
import { Collapsible, CollapsibleContent, CollapsibleTrigger } from '../ui/collapsible';
import { SyntaxHighlighter } from '../../utils/syntaxHighlighter';
import { getLanguageDisplayName } from '../../utils/getLanguageFromPath';

interface CodeBlockProps {
  code: string;
  language: string;
  showLineNumbers?: boolean;
  collapsedByDefault?: boolean;
  className?: string;
}

const MAX_LINES_BEFORE_COLLAPSE = 20;

export function CodeBlock({
  code,
  language,
  showLineNumbers = false,
  collapsedByDefault = false,
  className = ''
}: CodeBlockProps) {
  const [copied, setCopied] = useState(false);
  const lineCount = code.split('\n').length;
  const shouldCollapse = collapsedByDefault && lineCount > MAX_LINES_BEFORE_COLLAPSE;
  const [isOpen, setIsOpen] = useState(!shouldCollapse);

  const handleCopy = async () => {
    try {
      await navigator.clipboard.writeText(code);
      setCopied(true);
      setTimeout(() => setCopied(false), 2000);
    } catch (error) {
      console.error('Failed to copy code:', error);
    }
  };

  const content = (
    <div className={`rounded-lg border bg-muted/50 overflow-hidden ${className}`}>
      {/* Header */}
      <div className="flex items-center justify-between px-4 py-2 border-b bg-muted/30">
        <div className="flex items-center gap-2">
          {shouldCollapse && (
            <CollapsibleTrigger asChild>
              <Button variant="ghost" size="sm" className="h-6 w-6 p-0">
                {isOpen ? <ChevronDown className="h-4 w-4" /> : <ChevronRight className="h-4 w-4" />}
              </Button>
            </CollapsibleTrigger>
          )}
          <Badge variant="secondary" className="text-xs font-mono">
            {getLanguageDisplayName(language)}
          </Badge>
          {lineCount > 1 && (
            <span className="text-xs text-muted-foreground">
              {lineCount} lines
            </span>
          )}
        </div>
        <Button
          variant="ghost"
          size="sm"
          className="h-7 px-2 text-xs"
          onClick={handleCopy}
        >
          {copied ? (
            <>
              <Check className="h-3 w-3 mr-1" />
              Copied
            </>
          ) : (
            <>
              <Copy className="h-3 w-3 mr-1" />
              Copy
            </>
          )}
        </Button>
      </div>

      {/* Code content */}
      <div className="overflow-x-auto max-h-[500px] overflow-y-auto">
        <SyntaxHighlighter
          code={code}
          language={language}
          showLineNumbers={showLineNumbers}
          className="text-sm"
        />
      </div>
    </div>
  );

  if (shouldCollapse) {
    return (
      <Collapsible open={isOpen} onOpenChange={setIsOpen}>
        <CollapsibleContent>
          {content}
        </CollapsibleContent>
      </Collapsible>
    );
  }

  return content;
}

/**
 * Syntax highlighter component using Shiki
 * Provides code highlighting with theme support
 */

import { useEffect, useState } from 'react';
import { codeToHtml, type BundledLanguage } from 'shiki';

interface SyntaxHighlighterProps {
  code: string;
  language: string;
  showLineNumbers?: boolean;
  className?: string;
}

/**
 * Syntax highlighter component that uses Shiki for code highlighting
 * Automatically adapts to light/dark theme
 */
export function SyntaxHighlighter({
  code,
  language,
  showLineNumbers = false,
  className = ''
}: SyntaxHighlighterProps) {
  const [html, setHtml] = useState<string>('');
  const [isDark, setIsDark] = useState(false);

  // Detect theme changes
  useEffect(() => {
    const mediaQuery = window.matchMedia('(prefers-color-scheme: dark)');
    setIsDark(mediaQuery.matches || document.documentElement.classList.contains('dark'));

    const handler = (e: MediaQueryListEvent) => setIsDark(e.matches);
    mediaQuery.addEventListener('change', handler);

    // Also watch for class changes on document element (for manual theme toggle)
    const observer = new MutationObserver(() => {
      setIsDark(document.documentElement.classList.contains('dark'));
    });

    observer.observe(document.documentElement, {
      attributes: true,
      attributeFilter: ['class']
    });

    return () => {
      mediaQuery.removeEventListener('change', handler);
      observer.disconnect();
    };
  }, []);

  // Highlight code when language, code, or theme changes
  useEffect(() => {
    let cancelled = false;

    const highlight = async () => {
      try {
        // Map common language names to Shiki's language IDs
        const languageMap: Record<string, BundledLanguage> = {
          js: 'javascript',
          ts: 'typescript',
          sh: 'bash',
          yml: 'yaml',
          text: 'txt'
        };

        const shikiLang = (languageMap[language] || language) as BundledLanguage;

        const highlighted = await codeToHtml(code, {
          lang: shikiLang,
          theme: isDark ? 'github-dark' : 'github-light',
          transformers: showLineNumbers
            ? [
                {
                  line(node, line) {
                    node.properties['data-line'] = line;
                  }
                }
              ]
            : []
        });

        if (!cancelled) {
          setHtml(highlighted);
        }
      } catch (error) {
        console.warn('Syntax highlighting failed:', error);
        // Fallback to plain code
        if (!cancelled) {
          setHtml(`<pre><code>${escapeHtml(code)}</code></pre>`);
        }
      }
    };

    highlight();

    return () => {
      cancelled = true;
    };
  }, [code, language, showLineNumbers, isDark]);

  return (
    <div
      className={`syntax-highlighter ${className}`}
      dangerouslySetInnerHTML={{ __html: html }}
    />
  );
}

/**
 * Escape HTML special characters
 */
function escapeHtml(text: string): string {
  const map: Record<string, string> = {
    '&': '&amp;',
    '<': '&lt;',
    '>': '&gt;',
    '"': '&quot;',
    "'": '&#039;'
  };
  return text.replace(/[&<>"']/g, (m) => map[m]);
}

/**
 * Preload highlighter for common languages
 * Call this once during app initialization for better performance
 */
export async function preloadHighlighter() {
  try {
    await codeToHtml('', {
      lang: 'typescript',
      theme: 'github-light'
    });
  } catch (error) {
    console.warn('Failed to preload syntax highlighter:', error);
  }
}

