import * as React from "react";
import { useParams } from "react-router-dom";
import { ChatInterface } from "../components/chat/ChatInterface";
import { ChatPromptInput } from "../components/chat/ChatPromptInput";
import { useClaudeSession } from "../hooks/useClaudeSession";
import { useChatContext } from "../contexts/ChatContext";
import { useSessionMessages } from "../hooks/useSessionMessages";

export default function ProjectChat() {
  const { id, sessionId } = useParams<{ id: string; sessionId?: string }>();
  const { setCurrentSession, activeSessions } = useChatContext();

  // Get session metadata for token count
  const sessionMetadata = sessionId
    ? activeSessions.get(sessionId)?.metadata
    : undefined;

  // Load session with WebSocket if sessionId is present
  const {
    messages,
    toolResults,
    isLoading,
    error,
    isConnected,
    isStreaming,
    sendMessage,
    reconnect,
  } = useClaudeSession({
    sessionId: sessionId || "",
    projectId: id || "",
    enableWebSocket: !!sessionId,
  });

  // Load historical messages from JSONL file
  const {
    data: historicalMessages = [],
    isLoading: isLoadingHistory,
  } = useSessionMessages(id || "", sessionId || "");

  // Merge and deduplicate messages from both sources
  const allMessages = React.useMemo(() => {
    const messageMap = new Map();

    // Add historical messages first
    historicalMessages.forEach((msg) => {
      const key = msg.id || msg.timestamp || JSON.stringify(msg);
      messageMap.set(key, msg);
    });

    // Add/overwrite with WebSocket messages (more recent)
    messages.forEach((msg) => {
      const key = msg.id || msg.timestamp || JSON.stringify(msg);
      messageMap.set(key, msg);
    });

    // Sort by timestamp
    return Array.from(messageMap.values()).sort((a, b) => {
      const timeA = new Date(a.timestamp || 0).getTime();
      const timeB = new Date(b.timestamp || 0).getTime();
      return timeA - timeB;
    });
  }, [historicalMessages, messages]);

  // Set current session in context
  React.useEffect(() => {
    if (sessionId) {
      setCurrentSession(sessionId);
    }
    return () => setCurrentSession(null);
  }, [sessionId, setCurrentSession]);

  const handleSubmit = async (message: string, images?: File[]) => {
    console.log('[ProjectChat] handleSubmit called:', {
      message: message.substring(0, 100),
      imagesCount: images?.length || 0,
      hasSendMessage: !!sendMessage,
      sessionId,
      isConnected
    });

    if (!sendMessage) {
      console.error('[ProjectChat] sendMessage is not available');
      return;
    }

    if (!sessionId) {
      console.error('[ProjectChat] sessionId is missing');
      return;
    }

    // Convert images to base64 before sending via WebSocket
    const imagePaths = images ? await handleImageUpload(images) : undefined;
    console.log('[ProjectChat] Calling sendMessage with processed data');
    sendMessage({ message, images: imagePaths });
  };

  const handleImageUpload = async (files: File[]): Promise<string[]> => {
    // Convert File objects to base64 data URLs for WebSocket transmission
    return Promise.all(
      files.map(
        (file) =>
          new Promise<string>((resolve, reject) => {
            const reader = new FileReader();
            reader.onload = () => resolve(reader.result as string);
            reader.onerror = reject;
            reader.readAsDataURL(file);
          })
      )
    );
  };

  return (
    <div className="absolute inset-0 flex flex-col overflow-hidden">
      {/* Connection status banner */}
      {sessionId && !isConnected && (
        <div className="bg-yellow-100 border-b border-yellow-200 px-4 py-2 text-sm text-yellow-800 flex items-center justify-between">
          <span>Disconnected from chat session</span>
          <button
            onClick={reconnect}
            className="text-yellow-900 underline hover:no-underline"
          >
            Reconnect
          </button>
        </div>
      )}

      {/* Chat Messages Container - takes up remaining space */}
      <div className="flex-1 overflow-hidden">
        <ChatInterface
          projectId={id!}
          sessionId={sessionId}
          messages={allMessages}
          toolResults={toolResults}
          isLoading={isLoading}
          error={error}
          isStreaming={isStreaming}
          isLoadingHistory={isLoadingHistory}
        />
      </div>

      {/* Fixed Input Container at Bottom */}
      <div className="pb-4">
        <div className="mx-auto max-w-4xl">
          {/* Token usage display */}
          {sessionId && sessionMetadata && (
            <div className="px-4 pb-2 text-xs text-muted-foreground text-center">
              <span>
                {sessionMetadata.totalTokens.toLocaleString()} tokens used
              </span>
            </div>
          )}
          <ChatPromptInput
            onSubmit={handleSubmit}
            disabled={!sessionId || !isConnected}
            isStreaming={isStreaming}
          />
        </div>
      </div>
    </div>
  );
}

import {
  useQuery,
  useMutation,
  useQueryClient,
  type UseQueryResult,
  type UseMutationResult,
} from "@tanstack/react-query";
import { toast } from "sonner";
import type {
  Project,
  CreateProjectRequest,
  UpdateProjectRequest,
  ProjectsResponse,
  ProjectResponse,
  ErrorResponse,
} from "../../shared/types/project.types";
import type { SyncProjectsResponse } from "../../shared/types/project-sync.types";
import { useAuth } from "../contexts/AuthContext";

// Query keys factory - centralized key management
export const projectKeys = {
  all: ["projects"] as const,
  lists: () => [...projectKeys.all, "list"] as const,
  list: () => [...projectKeys.lists()] as const,
  details: () => [...projectKeys.all, "detail"] as const,
  detail: (id: string) => [...projectKeys.details(), id] as const,
};

// Helper to get auth token
function getAuthToken(): string | null {
  return localStorage.getItem("token");
}

// Helper to make authenticated API calls
async function fetchWithAuth(
  url: string,
  options: RequestInit = {},
  onUnauthorized?: () => void
) {
  const token = getAuthToken();

  const response = await fetch(url, {
    ...options,
    headers: {
      "Content-Type": "application/json",
      ...(token && { Authorization: `Bearer ${token}` }),
      ...options.headers,
    },
  });

  if (!response.ok) {
    // Handle 401 Unauthorized - invalid or missing token
    if (response.status === 401 && onUnauthorized) {
      onUnauthorized();
      throw new Error("Session expired");
    }

    const error: ErrorResponse = await response.json().catch(() => ({
      error: "An error occurred",
    }));
    throw new Error(error.error || "An error occurred");
  }

  return response.json();
}

/**
 * Fetch all projects
 */
async function fetchProjects(onUnauthorized?: () => void): Promise<Project[]> {
  const data: ProjectsResponse = await fetchWithAuth("/api/projects", {}, onUnauthorized);
  return data.data;
}

/**
 * Fetch a single project by ID
 */
async function fetchProject(id: string, onUnauthorized?: () => void): Promise<Project> {
  const data: ProjectResponse = await fetchWithAuth(`/api/projects/${id}`, {}, onUnauthorized);
  return data.data;
}

/**
 * Create a new project
 */
async function createProject(project: CreateProjectRequest, onUnauthorized?: () => void): Promise<Project> {
  const data: ProjectResponse = await fetchWithAuth("/api/projects", {
    method: "POST",
    body: JSON.stringify(project),
  }, onUnauthorized);
  return data.data;
}

/**
 * Update a project
 */
async function updateProject(id: string, project: UpdateProjectRequest, onUnauthorized?: () => void): Promise<Project> {
  const data: ProjectResponse = await fetchWithAuth(`/api/projects/${id}`, {
    method: "PATCH",
    body: JSON.stringify(project),
  }, onUnauthorized);
  return data.data;
}

/**
 * Delete a project
 */
async function deleteProject(id: string, onUnauthorized?: () => void): Promise<Project> {
  const data: ProjectResponse = await fetchWithAuth(`/api/projects/${id}`, {
    method: "DELETE",
  }, onUnauthorized);
  return data.data;
}

/**
 * Hook to fetch all projects
 */
export function useProjects(): UseQueryResult<Project[], Error> {
  const { handleInvalidToken } = useAuth();

  return useQuery({
    queryKey: projectKeys.list(),
    queryFn: () => fetchProjects(handleInvalidToken),
  });
}

/**
 * Hook to fetch a single project
 */
export function useProject(id: string): UseQueryResult<Project, Error> {
  const { handleInvalidToken } = useAuth();

  return useQuery({
    queryKey: projectKeys.detail(id),
    queryFn: () => fetchProject(id, handleInvalidToken),
    enabled: !!id, // Only run if id is provided
  });
}

/**
 * Hook to create a new project
 */
export function useCreateProject(): UseMutationResult<
  Project,
  Error,
  CreateProjectRequest
> {
  const queryClient = useQueryClient();
  const { handleInvalidToken } = useAuth();

  return useMutation({
    mutationFn: (project) => createProject(project, handleInvalidToken),
    onSuccess: (newProject) => {
      // Invalidate and refetch projects list
      queryClient.invalidateQueries({ queryKey: projectKeys.lists() });

      // Optionally add the new project to cache optimistically
      queryClient.setQueryData<Project[]>(projectKeys.list(), (old) => {
        return old ? [newProject, ...old] : [newProject];
      });

      toast.success("Project created successfully");
    },
    onError: (error) => {
      toast.error(error.message || "Failed to create project");
    },
  });
}

/**
 * Hook to update a project
 */
export function useUpdateProject(): UseMutationResult<
  Project,
  Error,
  { id: string; data: UpdateProjectRequest }
> {
  const queryClient = useQueryClient();
  const { handleInvalidToken } = useAuth();

  return useMutation({
    mutationFn: ({ id, data }) => updateProject(id, data, handleInvalidToken),
    onSuccess: (updatedProject) => {
      // Update the project in the list cache
      queryClient.setQueryData<Project[]>(projectKeys.list(), (old) => {
        if (!old) return [updatedProject];
        return old.map((project) =>
          project.id === updatedProject.id ? updatedProject : project
        );
      });

      // Update the individual project cache
      queryClient.setQueryData(
        projectKeys.detail(updatedProject.id),
        updatedProject
      );

      toast.success("Project updated successfully");
    },
    onError: (error) => {
      toast.error(error.message || "Failed to update project");
    },
  });
}

/**
 * Hook to delete a project
 */
export function useDeleteProject(): UseMutationResult<
  Project,
  Error,
  string
> {
  const queryClient = useQueryClient();
  const { handleInvalidToken } = useAuth();

  return useMutation({
    mutationFn: (id) => deleteProject(id, handleInvalidToken),
    onSuccess: (deletedProject) => {
      // Remove the project from the list cache
      queryClient.setQueryData<Project[]>(projectKeys.list(), (old) => {
        if (!old) return [];
        return old.filter((project) => project.id !== deletedProject.id);
      });

      // Remove the individual project cache
      queryClient.removeQueries({ queryKey: projectKeys.detail(deletedProject.id) });

      toast.success("Project deleted successfully");
    },
    onError: (error) => {
      toast.error(error.message || "Failed to delete project");
    },
  });
}

/**
 * Sync projects from Claude CLI
 */
async function syncProjects(onUnauthorized?: () => void): Promise<SyncProjectsResponse> {
  const data: { data: SyncProjectsResponse } = await fetchWithAuth("/api/projects/sync", {
    method: "POST",
  }, onUnauthorized);
  return data.data;
}

/**
 * Hook to sync projects from Claude CLI
 */
export function useSyncProjects(): UseMutationResult<
  SyncProjectsResponse,
  Error,
  void
> {
  const queryClient = useQueryClient();
  const { handleInvalidToken } = useAuth();

  return useMutation({
    mutationFn: () => syncProjects(handleInvalidToken),
    onSuccess: (data) => {
      // Invalidate projects list to trigger refetch
      queryClient.invalidateQueries({ queryKey: projectKeys.lists() });

      // Show success toast with sync stats
      toast.success(
        `Projects synced: ${data.projectsImported} imported, ${data.projectsUpdated} updated`
      );
    },
    onError: (error) => {
      toast.error(error.message || "Failed to sync projects");
    },
  });
}

