datasource db {
  provider = "sqlite"
  url      = "file:./dev.db"
}

generator client {
  provider = "prisma-client-js"
}

// Placeholder models for future workflow state storage
model Workflow {
  id         String   @id
  name       String
  status     String
  created_at DateTime @default(now())
  updated_at DateTime @updatedAt

  @@map("workflows")
}

model WorkflowStep {
  id          String   @id @default(uuid())
  workflow_id String
  name        String
  status      String
  result      String?  // JSON string
  created_at  DateTime @default(now())
  updated_at  DateTime @updatedAt

  @@map("workflow_steps")
}

model Project {
  id         String   @id @default(cuid())
  name       String
  path       String
  created_at DateTime @default(now())
  updated_at DateTime @updatedAt

  @@map("projects")
}

model User {
  id            Int       @id @default(autoincrement())
  username      String    @unique
  password_hash String
  created_at    DateTime  @default(now())
  last_login    DateTime?
  is_active     Boolean   @default(true)

  @@map("users")
}

/**
 * Unified session class that wraps adapter-specific sessions
 */

import { EventEmitter } from 'node:events';
import type {
  AIAdapter,
  ExecutionResponse,
  SessionOptions,
  SendOptions,
  AdapterSession,
} from '../types';

/**
 * Unified session wrapper
 */
export class Session extends EventEmitter {
  // private adapter: AIAdapter;
  private adapterSession: AdapterSession; // Adapter-specific session (e.g., ClaudeSession)
  private options: SessionOptions;
  private _sessionId?: string;
  private _messageCount = 0;
  readonly startedAt: number;
  lastMessageAt?: number;

  constructor(
    _adapter: AIAdapter,
    adapterSession: AdapterSession,
    options: SessionOptions
  ) {
    super();
    // this.adapter = _adapter;
    this.adapterSession = adapterSession;
    this.options = options;
    this.startedAt = Date.now();

    // Pre-set session ID if provided
    if (options.sessionId) {
      this._sessionId = options.sessionId;
    }

    // Forward events from adapter session
    this.setupEventForwarding();
  }

  /**
   * Send a message in this session
   */
  async send<T = string>(
    message: string,
    options: SendOptions = {}
  ): Promise<ExecutionResponse<T>> {
    const mergedOptions = {
      ...this.options,
      ...options,
    };

    // Delegate to adapter session
    const result = await this.adapterSession.send<T>(message, mergedOptions);

    // Update metadata
    this._messageCount++;
    this.lastMessageAt = Date.now();

    // Capture session ID from first message
    if (!this._sessionId && result.sessionId) {
      this._sessionId = result.sessionId;
    }

    return result;
  }

  /**
   * Abort the session - prevents new messages from being sent
   *
   * IMPORTANT: This does NOT terminate in-flight executions.
   * It only prevents new calls to send() from succeeding.
   * Any ongoing execution will complete normally.
   */
  abort(): void {
    if (this.adapterSession.abort) {
      this.adapterSession.abort();
      // Don't emit 'aborted' here - it will be forwarded from the adapter session
      // to avoid duplicate events
    }
  }

  /**
   * Get session ID (undefined until first message completes)
   */
  getSessionId(): string | undefined {
    return this._sessionId;
  }

  /**
   * Get message count
   */
  getMessageCount(): number {
    return this._messageCount;
  }

  /**
   * Getter for session ID
   */
  get sessionId(): string | undefined {
    return this._sessionId;
  }

  /**
   * Getter for message count
   */
  get messageCount(): number {
    return this._messageCount;
  }

  /**
   * Setup event forwarding from adapter session
   */
  private setupEventForwarding(): void {
    // Forward all events from adapter session
    const eventTypes = ['output', 'event', 'complete', 'error', 'aborted'];

    for (const eventType of eventTypes) {
      this.adapterSession.on(eventType, (...args: unknown[]) => {
        this.emit(eventType, ...args);
      });
    }
  }
}

import type { FastifyInstance } from 'fastify';

export async function registerWebSocket(fastify: FastifyInstance) {
  fastify.register(async (fastify) => {
    fastify.get('/ws', { websocket: true }, (socket) => {
      fastify.log.info('WebSocket client connected');

      // Send welcome message
      socket.send(JSON.stringify({
        type: 'connected',
        message: 'Welcome to Agent Workflows UI',
        timestamp: new Date().toISOString(),
      }));

      // Handle incoming messages
      socket.on('message', (message) => {
        const data = JSON.parse(message.toString());
        fastify.log.info({ data }, 'Received WebSocket message');

        // Echo back for now
        socket.send(JSON.stringify({
          type: 'echo',
          data,
          timestamp: new Date().toISOString(),
        }));
      });

      // Handle disconnection
      socket.on('close', () => {
        fastify.log.info('WebSocket client disconnected');
      });
    });
  });
}

/**
 * Session-related types
 */

import type { ExecutionResponse } from './interfaces.js';
import type { SendOptions } from './config.js';

/**
 * Session event types
 */
export type SessionEventType = 'output' | 'event' | 'complete' | 'error' | 'aborted';

/**
 * Session event data
 */
export interface SessionEventData {
  output?: string;
  event?: import('./interfaces.js').StreamEvent;
  result?: ExecutionResponse;
  error?: Error;
}

/**
 * Interface that adapter sessions must implement
 * Used by the Session wrapper to abstract adapter-specific session implementations
 */
export interface AdapterSession {
  /**
   * Send a message in the session
   */
  send<T>(message: string, options?: SendOptions): Promise<ExecutionResponse<T>>;

  /**
   * Abort the session (optional)
   */
  abort?(): void;

  /**
   * Register event listener
   */
  on(event: string, callback: (...args: unknown[]) => void): void;

  /**
   * Get session ID (optional)
   */
  getSessionId?(): string | undefined;

  /**
   * Get message count (optional)
   */
  messageCount?: number;
}

/**
 * WebSocket server example - Real-time AI CLI execution over WebSocket
 *
 * This example demonstrates how to build a WebSocket API server that:
 * - Accepts client connections
 * - Executes AI CLI commands via AgentClient
 * - Streams real-time output back to clients
 * - Manages multiple concurrent sessions
 *
 * Usage:
 *   npm install ws
 *   node examples/advanced/websocket-server.js
 */

import { WebSocketServer, WebSocket } from 'ws';
import { AgentClient } from '../../src/index.js';
import type { Session } from '../../src/client/session.js';

// Server configuration
const PORT = 8080;
const ADAPTERS = ['claude', 'codex'] as const;

// Client connection tracking
interface ClientConnection {
  ws: WebSocket;
  clientId: string;
  sessions: Map<string, Session>;
  adapter: typeof ADAPTERS[number];
}

const clients = new Map<string, ClientConnection>();

/**
 * Create WebSocket server
 */
const wss = new WebSocketServer({ port: PORT });

console.log(`🚀 WebSocket server started on ws://localhost:${PORT}`);
console.log('Supported adapters:', ADAPTERS.join(', '));
console.log('\nWaiting for connections...\n');

/**
 * Handle new client connection
 */
wss.on('connection', (ws: WebSocket) => {
  const clientId = generateClientId();

  console.log(`[${new Date().toISOString()}] Client connected: ${clientId}`);

  const connection: ClientConnection = {
    ws,
    clientId,
    sessions: new Map(),
    adapter: 'claude', // Default adapter
  };

  clients.set(clientId, connection);

  // Send welcome message
  sendMessage(ws, {
    type: 'connected',
    clientId,
    message: 'Connected to AI CLI WebSocket server',
    adapters: ADAPTERS,
  });

  /**
   * Handle incoming messages
   */
  ws.on('message', async (data: Buffer) => {
    try {
      const message = JSON.parse(data.toString());
      await handleMessage(connection, message);
    } catch (error) {
      sendError(ws, 'Invalid message format', error);
    }
  });

  /**
   * Handle client disconnect
   */
  ws.on('close', () => {
    console.log(`[${new Date().toISOString()}] Client disconnected: ${clientId}`);

    // Cleanup sessions
    connection.sessions.forEach(session => session.abort());
    clients.delete(clientId);
  });

  /**
   * Handle errors
   */
  ws.on('error', (error) => {
    console.error(`[${clientId}] WebSocket error:`, error);
  });
});

/**
 * Handle client messages
 */
async function handleMessage(connection: ClientConnection, message: any) {
  const { type } = message;

  switch (type) {
    case 'set-adapter':
      await handleSetAdapter(connection, message);
      break;

    case 'execute':
      await handleExecute(connection, message);
      break;

    case 'create-session':
      await handleCreateSession(connection, message);
      break;

    case 'send-message':
      await handleSendMessage(connection, message);
      break;

    case 'list-sessions':
      await handleListSessions(connection, message);
      break;

    case 'abort-session':
      await handleAbortSession(connection, message);
      break;

    default:
      sendError(connection.ws, `Unknown message type: ${type}`);
  }
}

/**
 * Set adapter for client
 */
async function handleSetAdapter(connection: ClientConnection, message: any) {
  const { adapter } = message;

  if (!ADAPTERS.includes(adapter)) {
    sendError(connection.ws, `Invalid adapter: ${adapter}. Supported: ${ADAPTERS.join(', ')}`);
    return;
  }

  connection.adapter = adapter;

  sendMessage(connection.ws, {
    type: 'adapter-set',
    adapter,
  });
}

/**
 * Execute a single prompt
 */
async function handleExecute(connection: ClientConnection, message: any) {
  const { prompt, options = {} } = message;

  if (!prompt) {
    sendError(connection.ws, 'Prompt is required');
    return;
  }

  try {
    const client = new AgentClient({ adapter: connection.adapter });

    const result = await client.execute(prompt, {
      ...options,
      onOutput: (data) => {
        sendMessage(connection.ws, {
          type: 'output',
          data: data.raw,
          text: data.text,
          accumulated: data.accumulated,
        });
      },
      onEvent: (event) => {
        sendMessage(connection.ws, {
          type: 'event',
          data: event,
        });
      },
    });

    sendMessage(connection.ws, {
      type: 'execution-complete',
      result,
    });
  } catch (error) {
    sendError(connection.ws, 'Execution failed', error);
  }
}

/**
 * Create a new session
 */
async function handleCreateSession(connection: ClientConnection, message: any) {
  const { options = {} } = message;

  try {
    const client = new AgentClient({ adapter: connection.adapter });
    const session = client.createSession(options);

    // Setup event listeners
    session.on('output', (raw) => {
      sendMessage(connection.ws, {
        type: 'session-output',
        sessionId: session.sessionId,
        data: raw,
      });
    });

    session.on('event', (event) => {
      sendMessage(connection.ws, {
        type: 'session-event',
        sessionId: session.sessionId,
        data: event,
      });
    });

    session.on('complete', (result) => {
      sendMessage(connection.ws, {
        type: 'session-message-complete',
        sessionId: session.sessionId,
        result,
      });
    });

    session.on('error', (error) => {
      sendMessage(connection.ws, {
        type: 'session-error',
        sessionId: session.sessionId,
        error: error.message,
      });
    });

    session.on('aborted', () => {
      sendMessage(connection.ws, {
        type: 'session-aborted',
        sessionId: session.sessionId,
      });
    });

    // Wait for first message to get session ID
    // For now, generate a temporary ID
    const tempId = `temp-${Date.now()}`;
    connection.sessions.set(tempId, session);

    sendMessage(connection.ws, {
      type: 'session-created',
      sessionId: tempId,
      adapter: connection.adapter,
    });
  } catch (error) {
    sendError(connection.ws, 'Failed to create session', error);
  }
}

/**
 * Send message to session
 */
async function handleSendMessage(connection: ClientConnection, message: any) {
  const { sessionId, message: msg, options = {} } = message;

  if (!sessionId || !msg) {
    sendError(connection.ws, 'sessionId and message are required');
    return;
  }

  const session = connection.sessions.get(sessionId);

  if (!session) {
    sendError(connection.ws, `Session not found: ${sessionId}`);
    return;
  }

  try {
    await session.send(msg, options);

    // Update session ID if it changed
    if (session.sessionId && session.sessionId !== sessionId) {
      connection.sessions.delete(sessionId);
      connection.sessions.set(session.sessionId, session);

      sendMessage(connection.ws, {
        type: 'session-id-updated',
        oldId: sessionId,
        newId: session.sessionId,
      });
    }
  } catch (error) {
    sendError(connection.ws, 'Failed to send message', error);
  }
}

/**
 * List active sessions
 */
async function handleListSessions(connection: ClientConnection, message: any) {
  const sessionList = Array.from(connection.sessions.entries()).map(([id, session]) => ({
    sessionId: id,
    messageCount: session.messageCount,
    startedAt: session.startedAt,
  }));

  sendMessage(connection.ws, {
    type: 'sessions-list',
    sessions: sessionList,
  });
}

/**
 * Abort session
 */
async function handleAbortSession(connection: ClientConnection, message: any) {
  const { sessionId } = message;

  if (!sessionId) {
    sendError(connection.ws, 'sessionId is required');
    return;
  }

  const session = connection.sessions.get(sessionId);

  if (!session) {
    sendError(connection.ws, `Session not found: ${sessionId}`);
    return;
  }

  session.abort();
  connection.sessions.delete(sessionId);

  sendMessage(connection.ws, {
    type: 'session-aborted',
    sessionId,
  });
}

/**
 * Send message to client
 */
function sendMessage(ws: WebSocket, data: any) {
  if (ws.readyState === WebSocket.OPEN) {
    ws.send(JSON.stringify(data));
  }
}

/**
 * Send error to client
 */
function sendError(ws: WebSocket, message: string, error?: any) {
  sendMessage(ws, {
    type: 'error',
    message,
    error: error?.message || String(error),
  });
}

/**
 * Generate unique client ID
 */
function generateClientId(): string {
  return `client-${Date.now()}-${Math.random().toString(36).substring(2, 9)}`;
}

/**
 * Graceful shutdown
 */
process.on('SIGINT', () => {
  console.log('\n\nShutting down server...');

  // Abort all sessions
  clients.forEach(connection => {
    connection.sessions.forEach(session => session.abort());
  });

  wss.close(() => {
    console.log('Server shut down');
    process.exit(0);
  });
});

/**
 * PROJECT DISCOVERY AND MANAGEMENT SYSTEM
 * ========================================
 * 
 * This module manages project discovery for both Claude CLI and Cursor CLI sessions.
 * 
 * ## Architecture Overview
 * 
 * 1. **Claude Projects** (stored in ~/.claude/projects/)
 *    - Each project is a directory named with the project path encoded (/ replaced with -)
 *    - Contains .jsonl files with conversation history including 'cwd' field
 *    - Project metadata stored in ~/.claude/project-config.json
 * 
 * 2. **Cursor Projects** (stored in ~/.cursor/chats/)
 *    - Each project directory is named with MD5 hash of the absolute project path
 *    - Example: /Users/john/myproject -> MD5 -> a1b2c3d4e5f6...
 *    - Contains session directories with SQLite databases (store.db)
 *    - Project path is NOT stored in the database - only in the MD5 hash
 * 
 * ## Project Discovery Strategy
 * 
 * 1. **Claude Projects Discovery**:
 *    - Scan ~/.claude/projects/ directory for Claude project folders
 *    - Extract actual project path from .jsonl files (cwd field)
 *    - Fall back to decoded directory name if no sessions exist
 * 
 * 2. **Cursor Sessions Discovery**:
 *    - For each KNOWN project (from Claude or manually added)
 *    - Compute MD5 hash of the project's absolute path
 *    - Check if ~/.cursor/chats/{md5_hash}/ directory exists
 *    - Read session metadata from SQLite store.db files
 * 
 * 3. **Manual Project Addition**:
 *    - Users can manually add project paths via UI
 *    - Stored in ~/.claude/project-config.json with 'manuallyAdded' flag
 *    - Allows discovering Cursor sessions for projects without Claude sessions
 * 
 * ## Critical Limitations
 * 
 * - **CANNOT discover Cursor-only projects**: From a quick check, there was no mention of
 *   the cwd of each project. if someone has the time, you can try to reverse engineer it.
 * 
 * - **Project relocation breaks history**: If a project directory is moved or renamed,
 *   the MD5 hash changes, making old Cursor sessions inaccessible unless the old
 *   path is known and manually added.
 * 
 * ## Error Handling
 * 
 * - Missing ~/.claude directory is handled gracefully with automatic creation
 * - ENOENT errors are caught and handled without crashing
 * - Empty arrays returned when no projects/sessions exist
 * 
 * ## Caching Strategy
 * 
 * - Project directory extraction is cached to minimize file I/O
 * - Cache is cleared when project configuration changes
 * - Session data is fetched on-demand, not cached
 */

