import fs from "fs/promises";
import fsSync from "fs";
import path from "path";
import os from "os";
import readline from "readline";
import { projectService } from "./project.service";
import { agentSessionService } from "./agent-session.service";
import type { SyncProjectsResponse } from "../../shared/types/project-sync.types";

/**
 * Project Sync Service
 * Handles syncing projects from ~/.claude/projects/ directory
 */
export class ProjectSyncService {
  /**
   * Get the Claude projects directory path
   * @returns Path to ~/.claude/projects/
   */
  private getClaudeProjectsDir(): string {
    return path.join(os.homedir(), ".claude", "projects");
  }

  /**
   * Decode filesystem-encoded project path back to real path
   * @param projectName - Encoded project name (e.g., "Users-john-myproject")
   * @returns Decoded path (e.g., "/Users/john/myproject")
   */
  private decodeProjectPath(projectName: string): string {
    const decoded = projectName.replace(/-/g, "/");
    // Add leading slash if not present
    return decoded.startsWith("/") ? decoded : `/${decoded}`;
  }

  /**
   * Extract the actual project directory from JSONL session files
   * @param projectName - Encoded project name from filesystem
   * @returns Extracted project path
   */
  private async extractProjectDirectory(projectName: string): Promise<string> {
    const projectDir = path.join(this.getClaudeProjectsDir(), projectName);
    const cwdCounts = new Map<string, number>();
    let latestTimestamp = 0;
    let latestCwd: string | null = null;
    let extractedPath: string;

    try {
      // Check if the project directory exists
      await fs.access(projectDir);

      const files = await fs.readdir(projectDir);
      const jsonlFiles = files.filter((file) => file.endsWith(".jsonl"));

      if (jsonlFiles.length === 0) {
        // Fall back to decoded project name if no sessions
        extractedPath = this.decodeProjectPath(projectName);
      } else {
        // Process all JSONL files to collect cwd values
        for (const file of jsonlFiles) {
          const jsonlFile = path.join(projectDir, file);
          const fileStream = fsSync.createReadStream(jsonlFile);
          const rl = readline.createInterface({
            input: fileStream,
            crlfDelay: Infinity,
          });

          for await (const line of rl) {
            if (line.trim()) {
              try {
                const entry = JSON.parse(line);

                if (entry.cwd) {
                  // Count occurrences of each cwd
                  cwdCounts.set(entry.cwd, (cwdCounts.get(entry.cwd) || 0) + 1);

                  // Track the most recent cwd
                  const timestamp = new Date(
                    entry.timestamp || 0
                  ).getTime();
                  if (timestamp > latestTimestamp) {
                    latestTimestamp = timestamp;
                    latestCwd = entry.cwd;
                  }
                }
              } catch (parseError) {
                // Skip malformed lines
              }
            }
          }
        }

        // Determine the best cwd to use
        if (cwdCounts.size === 0) {
          // No cwd found, fall back to decoded project name
          extractedPath = this.decodeProjectPath(projectName);
        } else if (cwdCounts.size === 1) {
          // Only one cwd, use it
          extractedPath = Array.from(cwdCounts.keys())[0];
        } else {
          // Multiple cwd values - prefer the most recent one if it has reasonable usage
          const mostRecentCount = cwdCounts.get(latestCwd!) || 0;
          const maxCount = Math.max(...cwdCounts.values());

          // Use most recent if it has at least 25% of the max count
          if (mostRecentCount >= maxCount * 0.25) {
            extractedPath = latestCwd!;
          } else {
            // Otherwise use the most frequently used cwd
            extractedPath = "";
            for (const [cwd, count] of cwdCounts.entries()) {
              if (count === maxCount) {
                extractedPath = cwd;
                break;
              }
            }
          }

          // Fallback (shouldn't reach here)
          if (!extractedPath) {
            extractedPath =
              latestCwd || this.decodeProjectPath(projectName);
          }
        }
      }

      return extractedPath;
    } catch (error: any) {
      // If the directory doesn't exist, just use the decoded project name
      if (error.code === "ENOENT") {
        extractedPath = this.decodeProjectPath(projectName);
      } else {
        console.error(
          `Error extracting project directory for ${projectName}:`,
          error
        );
        // Fall back to decoded project name for other errors
        extractedPath = this.decodeProjectPath(projectName);
      }

      return extractedPath;
    }
  }

  /**
   * Check if a project directory has more than minSessions sessions
   * @param projectName - Encoded project name from filesystem
   * @param minSessions - Minimum session count (default 3)
   * @returns True if project has more than minSessions
   */
  private async hasEnoughSessions(
    projectName: string,
    minSessions: number = 3
  ): Promise<boolean> {
    const projectDir = path.join(this.getClaudeProjectsDir(), projectName);

    try {
      await fs.access(projectDir);
      const files = await fs.readdir(projectDir);
      const jsonlFiles = files.filter((file) => file.endsWith(".jsonl"));

      // Check if project has more than minSessions sessions
      return jsonlFiles.length > minSessions;
    } catch {
      return false;
    }
  }

  /**
   * Sync projects from Claude CLI ~/.claude/projects/ directory
   * Only imports projects with more than 3 sessions
   * @param userId - User ID for session sync
   * @returns Sync statistics
   */
  async syncFromClaudeProjects(
    userId: string
  ): Promise<SyncProjectsResponse> {
    let projectsImported = 0;
    let projectsUpdated = 0;
    let totalSessionsSynced = 0;

    const claudeProjectsDir = this.getClaudeProjectsDir();

    try {
      // Check if directory exists
      await fs.access(claudeProjectsDir);
    } catch (error) {
      // Directory doesn't exist, return empty stats
      return {
        projectsImported: 0,
        projectsUpdated: 0,
        totalSessionsSynced: 0,
      };
    }

    // Read directory entries
    const entries = await fs.readdir(claudeProjectsDir, {
      withFileTypes: true,
    });

    // Filter for directories only
    const projectDirs = entries.filter((entry) => entry.isDirectory());

    // Process each project directory
    for (const projectDir of projectDirs) {
      const projectName = projectDir.name;

      // Skip projects without enough sessions (must have >3 sessions)
      const hasEnoughSessions = await this.hasEnoughSessions(projectName);
      if (!hasEnoughSessions) {
        continue;
      }

      // Extract actual project path
      const actualPath = await this.extractProjectDirectory(projectName);

      // Generate display name from last path segment
      const displayName = path.basename(actualPath);

      // Create or update project
      const project = await projectService.createOrUpdateProject(
        displayName,
        actualPath
      );

      // Determine if project was created or updated
      const isNewProject =
        project.created_at.getTime() === project.updated_at.getTime();

      if (isNewProject) {
        projectsImported++;
      } else {
        projectsUpdated++;
      }

      // Sync sessions for this project
      const sessionsSyncResult = await agentSessionService.syncProjectSessions(
        project.id,
        userId
      );

      totalSessionsSynced += sessionsSyncResult.synced;
    }

    return {
      projectsImported,
      projectsUpdated,
      totalSessionsSynced,
    };
  }
}

// Export singleton instance
export const projectSyncService = new ProjectSyncService();

import {
  useQuery,
  useMutation,
  useQueryClient,
  type UseQueryResult,
  type UseMutationResult,
} from "@tanstack/react-query";
import { toast } from "sonner";
import type {
  Project,
  CreateProjectRequest,
  UpdateProjectRequest,
  ProjectsResponse,
  ProjectResponse,
  ErrorResponse,
} from "../../shared/types/project.types";
import type { SyncProjectsResponse } from "../../shared/types/project-sync.types";
import { useAuth } from "../contexts/AuthContext";

// Query keys factory - centralized key management
export const projectKeys = {
  all: ["projects"] as const,
  lists: () => [...projectKeys.all, "list"] as const,
  list: () => [...projectKeys.lists()] as const,
  details: () => [...projectKeys.all, "detail"] as const,
  detail: (id: string) => [...projectKeys.details(), id] as const,
};

// Helper to get auth token
function getAuthToken(): string | null {
  return localStorage.getItem("token");
}

// Helper to make authenticated API calls
async function fetchWithAuth(
  url: string,
  options: RequestInit = {},
  onUnauthorized?: () => void
) {
  const token = getAuthToken();

  const response = await fetch(url, {
    ...options,
    headers: {
      "Content-Type": "application/json",
      ...(token && { Authorization: `Bearer ${token}` }),
      ...options.headers,
    },
  });

  if (!response.ok) {
    // Handle 401 Unauthorized - invalid or missing token
    if (response.status === 401 && onUnauthorized) {
      onUnauthorized();
      throw new Error("Session expired");
    }

    const error: ErrorResponse = await response.json().catch(() => ({
      error: "An error occurred",
    }));
    throw new Error(error.error || "An error occurred");
  }

  return response.json();
}

/**
 * Fetch all projects
 */
async function fetchProjects(onUnauthorized?: () => void): Promise<Project[]> {
  const data: ProjectsResponse = await fetchWithAuth("/api/projects", {}, onUnauthorized);
  return data.data;
}

/**
 * Fetch a single project by ID
 */
async function fetchProject(id: string, onUnauthorized?: () => void): Promise<Project> {
  const data: ProjectResponse = await fetchWithAuth(`/api/projects/${id}`, {}, onUnauthorized);
  return data.data;
}

/**
 * Create a new project
 */
async function createProject(project: CreateProjectRequest, onUnauthorized?: () => void): Promise<Project> {
  const data: ProjectResponse = await fetchWithAuth("/api/projects", {
    method: "POST",
    body: JSON.stringify(project),
  }, onUnauthorized);
  return data.data;
}

/**
 * Update a project
 */
async function updateProject(id: string, project: UpdateProjectRequest, onUnauthorized?: () => void): Promise<Project> {
  const data: ProjectResponse = await fetchWithAuth(`/api/projects/${id}`, {
    method: "PATCH",
    body: JSON.stringify(project),
  }, onUnauthorized);
  return data.data;
}

/**
 * Delete a project
 */
async function deleteProject(id: string, onUnauthorized?: () => void): Promise<Project> {
  const data: ProjectResponse = await fetchWithAuth(`/api/projects/${id}`, {
    method: "DELETE",
  }, onUnauthorized);
  return data.data;
}

/**
 * Hook to fetch all projects
 */
export function useProjects(): UseQueryResult<Project[], Error> {
  const { handleInvalidToken } = useAuth();

  return useQuery({
    queryKey: projectKeys.list(),
    queryFn: () => fetchProjects(handleInvalidToken),
  });
}

/**
 * Hook to fetch a single project
 */
export function useProject(id: string): UseQueryResult<Project, Error> {
  const { handleInvalidToken } = useAuth();

  return useQuery({
    queryKey: projectKeys.detail(id),
    queryFn: () => fetchProject(id, handleInvalidToken),
    enabled: !!id, // Only run if id is provided
  });
}

/**
 * Hook to create a new project
 */
export function useCreateProject(): UseMutationResult<
  Project,
  Error,
  CreateProjectRequest
> {
  const queryClient = useQueryClient();
  const { handleInvalidToken } = useAuth();

  return useMutation({
    mutationFn: (project) => createProject(project, handleInvalidToken),
    onSuccess: (newProject) => {
      // Invalidate and refetch projects list
      queryClient.invalidateQueries({ queryKey: projectKeys.lists() });

      // Optionally add the new project to cache optimistically
      queryClient.setQueryData<Project[]>(projectKeys.list(), (old) => {
        return old ? [newProject, ...old] : [newProject];
      });

      toast.success("Project created successfully");
    },
    onError: (error) => {
      toast.error(error.message || "Failed to create project");
    },
  });
}

/**
 * Hook to update a project
 */
export function useUpdateProject(): UseMutationResult<
  Project,
  Error,
  { id: string; data: UpdateProjectRequest }
> {
  const queryClient = useQueryClient();
  const { handleInvalidToken } = useAuth();

  return useMutation({
    mutationFn: ({ id, data }) => updateProject(id, data, handleInvalidToken),
    onSuccess: (updatedProject) => {
      // Update the project in the list cache
      queryClient.setQueryData<Project[]>(projectKeys.list(), (old) => {
        if (!old) return [updatedProject];
        return old.map((project) =>
          project.id === updatedProject.id ? updatedProject : project
        );
      });

      // Update the individual project cache
      queryClient.setQueryData(
        projectKeys.detail(updatedProject.id),
        updatedProject
      );

      toast.success("Project updated successfully");
    },
    onError: (error) => {
      toast.error(error.message || "Failed to update project");
    },
  });
}

/**
 * Hook to delete a project
 */
export function useDeleteProject(): UseMutationResult<
  Project,
  Error,
  string
> {
  const queryClient = useQueryClient();
  const { handleInvalidToken } = useAuth();

  return useMutation({
    mutationFn: (id) => deleteProject(id, handleInvalidToken),
    onSuccess: (deletedProject) => {
      // Remove the project from the list cache
      queryClient.setQueryData<Project[]>(projectKeys.list(), (old) => {
        if (!old) return [];
        return old.filter((project) => project.id !== deletedProject.id);
      });

      // Remove the individual project cache
      queryClient.removeQueries({ queryKey: projectKeys.detail(deletedProject.id) });

      toast.success("Project deleted successfully");
    },
    onError: (error) => {
      toast.error(error.message || "Failed to delete project");
    },
  });
}

/**
 * Sync projects from Claude CLI
 */
async function syncProjects(onUnauthorized?: () => void): Promise<SyncProjectsResponse> {
  const data: { data: SyncProjectsResponse } = await fetchWithAuth("/api/projects/sync", {
    method: "POST",
  }, onUnauthorized);
  return data.data;
}

/**
 * Hook to sync projects from Claude CLI
 */
export function useSyncProjects(): UseMutationResult<
  SyncProjectsResponse,
  Error,
  void
> {
  const queryClient = useQueryClient();
  const { handleInvalidToken } = useAuth();

  return useMutation({
    mutationFn: () => syncProjects(handleInvalidToken),
    onSuccess: (data) => {
      // Invalidate projects list to trigger refetch
      queryClient.invalidateQueries({ queryKey: projectKeys.lists() });

      // Show success toast with sync stats
      toast.success(
        `Projects synced: ${data.projectsImported} imported, ${data.projectsUpdated} updated`
      );
    },
    onError: (error) => {
      toast.error(error.message || "Failed to sync projects");
    },
  });
}

import { useEffect } from "react";
import { Navigate, Outlet } from "react-router-dom";
import { useAuth } from "../contexts/AuthContext";
import { ChatProvider } from "../contexts/ChatContext";
import { useSyncProjects } from "../hooks/useProjects";
import { AppSidebar } from "@/components/app-sidebar";
import { SidebarInset, SidebarProvider } from "@/components/ui/sidebar";

function ProtectedLayout() {
  const { isAuthenticated } = useAuth();
  const syncProjects = useSyncProjects();

  // Sync projects from Claude CLI on mount
  useEffect(() => {
    syncProjects.mutate();
  }, []); // Empty dependency array = run once on mount

  if (!isAuthenticated) {
    return <Navigate to="/login" replace />;
  }

  return (
    <ChatProvider>
      <SidebarProvider
        style={
          {
            "--sidebar-width": "350px",
          } as React.CSSProperties
        }
      >
        <AppSidebar />
        <SidebarInset>
          <Outlet />
        </SidebarInset>
      </SidebarProvider>
    </ChatProvider>
  );
}

export default ProtectedLayout;
