     1→/**
     2→ * Hook to load and parse Claude session JSONL data
     3→ * Currently loads from mock files, ready to be replaced with WebSocket streaming
     4→ */
     5→
     6→import { useState, useEffect } from 'react';
     7→import type { ChatMessage } from '../../shared/types/chat';
     8→import { parseJSONLSession, extractToolResults } from '../utils/parseClaudeSession';
     9→
    10→interface UseClaudeSessionReturn {
    11→  messages: ChatMessage[];
    12→  toolResults: Map<string, { content: string; is_error?: boolean }>;
    13→  isLoading: boolean;
    14→  error: Error | null;
    15→}
    16→
    17→/**
    18→ * Load and parse a Claude session from JSONL file
    19→ *
    20→ * @param sessionFile - Filename in /mocks/ directory (default: demo session)
    21→ * @returns Parsed messages, tool results, loading state, and error
    22→ *
    23→ * @example
    24→ * ```tsx
    25→ * const { messages, toolResults, isLoading, error } = useClaudeSession();
    26→ * ```
    27→ *
    28→ * @future
    29→ * Replace this hook with useAgentWebSocket that receives real-time events:
    30→ * - Connect to WebSocket endpoint `/ws/session/{id}`
    31→ * - Listen for StreamEvent messages
    32→ * - Build messages array incrementally as events arrive
    33→ * - Update UI in real-time as assistant responds
    34→ */
    35→export function useClaudeSession(
    36→  sessionFile: string = '8f079ffe-995f-42ba-b089-84de56817b6f.jsonl'
    37→): UseClaudeSessionReturn {
    38→  const [messages, setMessages] = useState<ChatMessage[]>([]);
    39→  const [toolResults, setToolResults] = useState<Map<string, { content: string; is_error?: boolean }>>(
    40→    new Map()
    41→  );
    42→  const [isLoading, setIsLoading] = useState(true);
    43→  const [error, setError] = useState<Error | null>(null);
    44→
    45→  useEffect(() => {
    46→    let cancelled = false;
    47→
    48→    const loadSession = async () => {
    49→      try {
    50→        setIsLoading(true);
    51→        setError(null);
    52→
    53→        // Fetch JSONL file from public/mocks directory
    54→        console.log('Loading session from:', `/mocks/${sessionFile}`);
    55→        const response = await fetch(`/mocks/${sessionFile}`);
    56→
    57→        if (!response.ok) {
    58→          throw new Error(`Failed to load session: ${response.statusText}`);
    59→        }
    60→
    61→        const jsonlContent = await response.text();
    62→        console.log('Loaded JSONL content, length:', jsonlContent.length);
    63→
    64→        if (cancelled) return;
    65→
    66→        // Parse JSONL into messages
    67→        const parsedMessages = parseJSONLSession(jsonlContent);
    68→        const parsedToolResults = extractToolResults(jsonlContent);
    69→
    70→        console.log('Parsed messages:', parsedMessages.length);
    71→        console.log('Parsed tool results:', parsedToolResults.size);
    72→
    73→        setMessages(parsedMessages);
    74→        setToolResults(parsedToolResults);
    75→      } catch (err) {
    76→        if (!cancelled) {
    77→          setError(err instanceof Error ? err : new Error('Unknown error loading session'));
    78→          setMessages([]);
    79→          setToolResults(new Map());
    80→        }
    81→      } finally {
    82→        if (!cancelled) {
    83→          setIsLoading(false);
    84→        }
    85→      }
    86→    };
    87→
    88→    loadSession();
    89→
    90→    return () => {
    91→      cancelled = true;
    92→    };
    93→  }, [sessionFile]);
    94→
    95→  return {
    96→    messages,
    97→    toolResults,
    98→    isLoading,
    99→    error
   100→  };
   101→}
