     1→/**
     2→ * Parse JSONL session data into ChatMessage array
     3→ * Handles Claude Code CLI output format with streaming events
     4→ */
     5→
     6→import type { ChatMessage, ContentBlock, ToolUseBlock, ToolResultBlock } from '../../shared/types/chat';
     7→
     8→interface RawStreamEvent {
     9→  type: string;
    10→  timestamp?: number;
    11→  [key: string]: unknown;
    12→}
    13→
    14→/**
    15→ * Parse JSONL content into an array of ChatMessage objects
    16→ * Links tool_use blocks with their corresponding tool_result blocks
    17→ *
    18→ * @param jsonlContent - Raw JSONL string content
    19→ * @returns Array of parsed ChatMessage objects
    20→ */
    21→export function parseJSONLSession(jsonlContent: string): ChatMessage[] {
    22→  if (!jsonlContent || jsonlContent.trim() === '') {
    23→    return [];
    24→  }
    25→
    26→  const lines = jsonlContent.split('\n').filter(line => line.trim() !== '');
    27→  const messages: ChatMessage[] = [];
    28→  const toolResults = new Map<string, ToolResultBlock>();
    29→
    30→  // Track current message being built
    31→  let currentMessage: ChatMessage | null = null;
    32→
    33→  for (const line of lines) {
    34→    try {
    35→      const event = JSON.parse(line) as RawStreamEvent;
    36→
    37→      // Handle message_start event
    38→      if (event.type === 'message_start') {
    39→        const message = event.message as any;
    40→        currentMessage = {
    41→          id: message?.id || crypto.randomUUID(),
    42→          role: 'assistant',
    43→          content: [],
    44→          timestamp: event.timestamp || Date.now(),
    45→          isStreaming: false
    46→        };
    47→        continue;
    48→      }
    49→
    50→      // Handle content_block_start
    51→      if (event.type === 'content_block_start') {
    52→        const block = (event as any).content_block;
    53→        if (!currentMessage) continue;
    54→
    55→        if (block.type === 'text') {
    56→          currentMessage.content.push({
    57→            type: 'text',
    58→            text: block.text || ''
    59→          } as ContentBlock);
    60→        } else if (block.type === 'thinking') {
    61→          currentMessage.content.push({
    62→            type: 'thinking',
    63→            thinking: block.thinking || ''
    64→          } as ContentBlock);
    65→        } else if (block.type === 'tool_use') {
    66→          currentMessage.content.push({
    67→            type: 'tool_use',
    68→            id: block.id,
    69→            name: block.name,
    70→            input: block.input || {}
    71→          } as ToolUseBlock);
    72→        }
    73→        continue;
    74→      }
    75→
    76→      // Handle content_block_delta for streaming content
    77→      if (event.type === 'content_block_delta') {
    78→        if (!currentMessage) continue;
    79→        const delta = (event as any).delta;
    80→        const index = (event as any).index || 0;
    81→
    82→        if (delta.type === 'text_delta' && currentMessage.content[index]) {
    83→          const textBlock = currentMessage.content[index] as any;
    84→          if (textBlock.type === 'text') {
    85→            textBlock.text += delta.text || '';
    86→          }
    87→        } else if (delta.type === 'thinking_delta' && currentMessage.content[index]) {
    88→          const thinkingBlock = currentMessage.content[index] as any;
    89→          if (thinkingBlock.type === 'thinking') {
    90→            thinkingBlock.thinking += delta.thinking || '';
    91→          }
    92→        } else if (delta.type === 'input_json_delta' && currentMessage.content[index]) {
    93→          const toolBlock = currentMessage.content[index] as any;
    94→          if (toolBlock.type === 'tool_use') {
    95→            // Accumulate input JSON
