  PromptInputModelSelectItem,
  PromptInputModelSelectTrigger,
  PromptInputModelSelectValue,
  PromptInputProvider,
  PromptInputSpeechButton,
  PromptInputSubmit,
  PromptInputTextarea,
  PromptInputFooter,
  PromptInputTools,
  PromptInputButton,
} from "@/client/components/ai-elements/prompt-input";
import { ChatPromptInputFiles } from "@/client/components/chat/ChatPromptInputFiles";
import { GlobeIcon } from "lucide-react";
import { useEffect, useRef, useState } from "react";

const models = [
  { id: "gpt-4", name: "GPT-4" },
  { id: "gpt-3.5-turbo", name: "GPT-3.5 Turbo" },
  { id: "claude-2", name: "Claude 2" },
  { id: "claude-instant", name: "Claude Instant" },
  { id: "palm-2", name: "PaLM 2" },
  { id: "llama-2-70b", name: "Llama 2 70B" },
  { id: "llama-2-13b", name: "Llama 2 13B" },
  { id: "cohere-command", name: "Command" },
  { id: "mistral-7b", name: "Mistral 7B" },
];

const SUBMITTING_TIMEOUT = 200;
const STREAMING_TIMEOUT = 2000;

interface ChatPromptInputProps {
  onSubmit?: (message: string, images?: File[]) => void | Promise<void>;
  disabled?: boolean;
  isStreaming?: boolean;
}

export const ChatPromptInput = ({
  onSubmit,
  disabled = false,
  isStreaming: externalIsStreaming = false,
}: ChatPromptInputProps) => {
  const [text, setText] = useState<string>("");
  const [model, setModel] = useState<string>(models[0].id);
  const [status, setStatus] = useState<
    "submitted" | "streaming" | "ready" | "error"
  >("ready");
  const [isAtMenuOpen, setIsAtMenuOpen] = useState(false);
  const timeoutRef = useRef<NodeJS.Timeout | null>(null);
  const textareaRef = useRef<HTMLTextAreaElement>(null);

  // Update status based on external streaming state
  useEffect(() => {
    if (externalIsStreaming) {
      setStatus("streaming");
    } else if (status === "streaming") {
      setStatus("ready");
    }
  }, [externalIsStreaming, status]);

  // Focus command input when @ menu opens
  useEffect(() => {
    if (isAtMenuOpen && commandInputRef.current) {
      // Small delay to ensure the popover is rendered
      setTimeout(() => {
        commandInputRef.current?.focus();
      }, 0);
    }
  }, [isAtMenuOpen]);

  // Handle text change and detect @ command
  const handleTextChange = (e: React.ChangeEvent<HTMLTextAreaElement>) => {
    const newValue = e.target.value;
    setText(newValue);

    // Check if user just typed "@" at the end
    if (newValue.endsWith("@")) {
      setIsAtMenuOpen(true);
      // Remove the @ from the text
      setText(newValue.slice(0, -1));
    }
  };

  const stop = () => {
    console.log("[ChatPromptInput] Stopping request...");

    // Clear any pending timeouts
    if (timeoutRef.current) {
      clearTimeout(timeoutRef.current);
      timeoutRef.current = null;
    }

    setStatus("ready");
  };

  const handleSubmit = async (message: PromptInputMessage) => {
    // If currently streaming or submitted, stop instead of submitting
    if (status === "streaming" || status === "submitted") {
      stop();
      return;
    }

    const hasText = Boolean(message.text);
    const hasAttachments = Boolean(message.files?.length);

    if (!(hasText || hasAttachments)) {
      console.log("[ChatPromptInput] No text or attachments, skipping submit");
      return;
    }

    if (disabled) {
      console.log("[ChatPromptInput] Submit disabled, skipping");
      return;
    }

    console.log("[ChatPromptInput] Submitting message:", {
      text: message.text,
      filesCount: message.files?.length || 0,
      hasOnSubmit: !!onSubmit,
    });

    setStatus("submitted");

    // If external onSubmit provided, use it
    if (onSubmit) {
      try {
        await onSubmit(message.text || "", message.files);
        console.log("[ChatPromptInput] Message submitted successfully");
      } catch (error) {
        console.error("[ChatPromptInput] Error submitting message:", error);
        setStatus("error");
        return;
      }
    } else {
      console.warn(
        "[ChatPromptInput] No onSubmit handler provided, using mock"
      );
      // Mock behavior for demo
      setTimeout(() => {
        setStatus("streaming");
      }, SUBMITTING_TIMEOUT);

      timeoutRef.current = setTimeout(() => {
        setStatus("ready");
        timeoutRef.current = null;
      }, STREAMING_TIMEOUT);
    }
  };

  return (
    <div className="flex flex-col justify-end size-full">
      <PromptInputProvider>
        <PromptInput globalDrop multiple onSubmit={handleSubmit}>
          <PromptInputBody>
            <PromptInputAttachments>
              {(attachment) => <PromptInputAttachment data={attachment} />}
            </PromptInputAttachments>
            <PromptInputTextarea
              onChange={handleTextChange}
              ref={textareaRef}
              value={text}
            />
          </PromptInputBody>
          <PromptInputFooter>
            <PromptInputTools>
              <PromptInputActionMenu>
                <PromptInputActionMenuTrigger />
                <PromptInputActionMenuContent>
                  <PromptInputActionAddAttachments />
                </PromptInputActionMenuContent>
              </PromptInputActionMenu>
              <Popover open={isAtMenuOpen} onOpenChange={setIsAtMenuOpen}>
                <PopoverTrigger asChild>
                  <PromptInputButton>
                    <AtSignIcon size={16} />
                  </PromptInputButton>
                </PopoverTrigger>
                <PopoverContent align="start" className="w-[400px] p-0">
                  <PromptInputCommand>
                    <PromptInputCommandInput
                      ref={commandInputRef}
                      className="border-none focus-visible:ring-0"
                      placeholder="Add files, folders, docs..."
                    />
                    <PromptInputCommandList>
                      <PromptInputCommandEmpty className="p-3 text-muted-foreground text-sm">
                        No results found.
                      </PromptInputCommandEmpty>
                      <PromptInputCommandGroup heading="Added">
                        <PromptInputCommandItem>
                          <GlobeIcon />
                          <span>Active Tabs</span>
                          <span className="ml-auto text-muted-foreground">✓</span>
                        </PromptInputCommandItem>
                      </PromptInputCommandGroup>
                      <PromptInputCommandSeparator />
                      <PromptInputCommandGroup heading="Other Files">
                        {sampleFiles.added.map((file, index) => (
                          <PromptInputCommandItem key={`${file.path}-${index}`}>
                            <GlobeIcon className="text-primary" />
                            <div className="flex flex-col">
                              <span className="font-medium text-sm">
                                {file.path}
                              </span>
                              <span className="text-muted-foreground text-xs">
                                {file.location}
                              </span>
                            </div>
                          </PromptInputCommandItem>
                        ))}
                      </PromptInputCommandGroup>
                    </PromptInputCommandList>
                  </PromptInputCommand>
                </PopoverContent>
              </Popover>
              <PromptInputSpeechButton
                onTranscriptionChange={setText}
                textareaRef={textareaRef}
              />
              <PromptInputButton>
                <GlobeIcon size={16} />
                <span>Search</span>
              </PromptInputButton>
              <PromptInputModelSelect onValueChange={setModel} value={model}>
                <PromptInputModelSelectTrigger>
                  <PromptInputModelSelectValue />
                </PromptInputModelSelectTrigger>
                <PromptInputModelSelectContent>
                  {models.map((modelOption) => (
                    <PromptInputModelSelectItem
                      key={modelOption.id}
                      value={modelOption.id}
                    >
                      {modelOption.name}
                    </PromptInputModelSelectItem>
                  ))}
                </PromptInputModelSelectContent>
              </PromptInputModelSelect>
            </PromptInputTools>
            <PromptInputSubmit className="!h-8" status={status} />
          </PromptInputFooter>
        </PromptInput>
      </PromptInputProvider>
    </div>
  );
};

