"use client";

import * as React from "react";
import { useNavigate, useParams } from "react-router-dom";
import {
  ChevronRight,
  Folder,
  MoreHorizontal,
  Trash2,
  Star,
  Edit,
  Forward,
} from "lucide-react";
import {
  Sidebar,
  SidebarContent,
  SidebarGroup,
  SidebarGroupLabel,
  SidebarHeader,
  SidebarInput,
  SidebarMenu,
  SidebarMenuItem,
  SidebarMenuButton,
  SidebarMenuAction,
  useSidebar,
} from "@/components/ui/sidebar";
import {
  DropdownMenu,
  DropdownMenuContent,
  DropdownMenuItem,
  DropdownMenuSeparator,
  DropdownMenuTrigger,
} from "@/components/ui/dropdown-menu";
import {
  Collapsible,
  CollapsibleContent,
  CollapsibleTrigger,
} from "@/components/ui/collapsible";
import { useProjects } from "../hooks/useProjects";
import { useAgentSessions } from "../hooks/useAgentSessions";
import { SessionListItem } from "./chat/SessionListItem";
import { NewSessionButton } from "./chat/NewSessionButton";

interface ProjectWithSessions {
  id: string;
  name: string;
  path: string;
  sessionCount: number;
  isActive?: boolean;
}

interface AppInnerSidebarProps {
  title?: string;
  activeProjectId?: string;
  onProjectClick?: (projectId: string) => void;
  onSessionClick?: (projectId: string, sessionId: string) => void;
  onNewSession?: (projectId: string) => void;
}

export function AppInnerSidebar({
  title,
  activeProjectId: activeProjectIdProp,
  onProjectClick,
}: AppInnerSidebarProps) {
  const navigate = useNavigate();
  const params = useParams<{ id: string }>();
  const { data: projectsData, isLoading, error } = useProjects();
  const { isMobile } = useSidebar();

  // Use URL param if available, otherwise use prop
  const activeProjectId = params.id || activeProjectIdProp;

  const [openProjects, setOpenProjects] = React.useState<string[]>(
    activeProjectId ? [activeProjectId] : []
  );
  const [showAllSessions, setShowAllSessions] = React.useState<{
    [projectId: string]: boolean;
  }>({});

  // Fetch sessions for the active project
  const { data: sessionsData } = useAgentSessions({
    projectId: activeProjectId || "",
    enabled: !!activeProjectId,
  });

  // Transform projects data with real session counts and sort alphabetically
  const projects: ProjectWithSessions[] = React.useMemo(() => {
    if (!projectsData) return [];

    return projectsData
      .map((project) => ({
        id: project.id,
        name: project.name,
        path: project.path,
        sessionCount:
          project.id === activeProjectId ? sessionsData?.length || 0 : 0,
      }))
      .sort((a, b) => a.name.localeCompare(b.name));
  }, [projectsData, activeProjectId, sessionsData]);

  // Get active project name for title
  const activeProject = projects.find((p) => p.id === activeProjectId);
  const displayTitle = title || activeProject?.name || "Projects";

  const toggleProject = (projectId: string) => {
    // Always ensure the project is open when navigating to it
    setOpenProjects((prev) =>
      prev.includes(projectId) ? prev : [...prev, projectId]
    );
    onProjectClick?.(projectId);
    navigate(`/projects/${projectId}/chat`);
  };

  // Ensure active project is open on mount or when activeProjectId changes
  React.useEffect(() => {
    if (activeProjectId && !openProjects.includes(activeProjectId)) {
      setOpenProjects((prev) => [...prev, activeProjectId]);
    }
  }, [activeProjectId]);

  return (
    <Sidebar collapsible="none" className="hidden flex-1 md:flex">
      <SidebarHeader className="gap-3.5 border-b p-4">
        <div className="flex w-full items-center justify-between">
          <div className="text-foreground text-base font-medium">
            {displayTitle}
          </div>
        </div>
        <SidebarInput placeholder="Type to search..." />
      </SidebarHeader>
      <SidebarContent>
        <SidebarGroup>
          <SidebarGroupLabel>All Projects</SidebarGroupLabel>
          {isLoading && (
            <div className="px-2 py-1 text-sm text-muted-foreground">
              Loading projects...
            </div>
          )}
          {error && (
            <div className="px-2 py-1 text-sm text-destructive">
              Error loading projects: {error.message}
            </div>
          )}
          {!isLoading && !error && (
            <SidebarMenu>
              {projects.map((project) => {
                const isOpen = openProjects.includes(project.id);
                const isActive = project.id === activeProjectId;

                return (
                  <Collapsible
                    key={project.id}
                    open={isOpen}
                    onOpenChange={() => toggleProject(project.id)}
                  >
                    <SidebarMenuItem>
                      <SidebarMenuButton asChild isActive={isActive}>
                        <CollapsibleTrigger className="w-full">
                          <Folder />
                          <div className="flex flex-1 flex-col items-start gap-0.5">
                            <span className="font-medium text-sm">
                              {project.name}
                            </span>
                          </div>
                          <ChevronRight
                            className={`ml-auto transition-transform ${
                              isOpen ? "rotate-90" : ""
                            }`}
                          />
                        </CollapsibleTrigger>
                      </SidebarMenuButton>
                      <DropdownMenu>
                        <DropdownMenuTrigger asChild>
                          <SidebarMenuAction showOnHover>
                            <MoreHorizontal />
                            <span className="sr-only">More</span>
                          </SidebarMenuAction>
                        </DropdownMenuTrigger>
                        <DropdownMenuContent
                          className="w-48 rounded-lg"
                          side={isMobile ? "bottom" : "right"}
                          align={isMobile ? "end" : "start"}
                        >
                          <DropdownMenuItem>
                            <Star className="text-muted-foreground" />
                            <span>Favorite</span>
                          </DropdownMenuItem>
                          <DropdownMenuItem>
                            <Edit className="text-muted-foreground" />
                            <span>Edit Project</span>
                          </DropdownMenuItem>
                          <DropdownMenuItem>
                            <Forward className="text-muted-foreground" />
                            <span>Share Project</span>
                          </DropdownMenuItem>
                          <DropdownMenuSeparator />
                          <DropdownMenuItem>
                            <Trash2 className="text-muted-foreground" />
                            <span>Delete Project</span>
                          </DropdownMenuItem>
                        </DropdownMenuContent>
                      </DropdownMenu>
                      <CollapsibleContent>
                        <div className="ml-0 space-y-0.5 border-l pl-1 py-1">
                          {isActive &&
                          sessionsData &&
                          sessionsData.length > 0 ? (
                            <>
                              {(showAllSessions[project.id]
                                ? sessionsData
                                : sessionsData.slice(0, 5)
                              ).map((session) => (
                                <SessionListItem
                                  key={session.id}
                                  session={session}
                                  projectId={project.id}
                                  isActive={false}
                                />
                              ))}
                              {sessionsData.length > 5 &&
                                !showAllSessions[project.id] && (
                                  <button
                                    onClick={() =>
                                      setShowAllSessions((prev) => ({
                                        ...prev,
                                        [project.id]: true,
                                      }))
                                    }
                                    className="w-full px-2 py-1.5 text-xs text-muted-foreground hover:text-foreground transition-colors text-left"
                                  >
                                    Show {sessionsData.length - 5} more...
                                  </button>
                                )}
                            </>
                          ) : isActive ? (
                            <div className="px-2 py-2 text-xs text-muted-foreground">
                              No sessions yet
                            </div>
                          ) : null}
                          {isActive && (
                            <div className="px-2 pt-1">
                              <NewSessionButton
                                projectId={project.id}
                                variant="default"
                                size="sm"
                              />
                            </div>
                          )}
                        </div>
                      </CollapsibleContent>
                    </SidebarMenuItem>
                  </Collapsible>
                );
              })}
            </SidebarMenu>
          )}
        </SidebarGroup>
      </SidebarContent>
    </Sidebar>
  );
}

import {
  useQuery,
  useMutation,
  useQueryClient,
  type UseQueryResult,
  type UseMutationResult,
} from "@tanstack/react-query";
import { toast } from "sonner";
import type {
  Project,
  CreateProjectRequest,
  UpdateProjectRequest,
  ProjectsResponse,
  ProjectResponse,
  ErrorResponse,
} from "../../shared/types/project.types";
import type { SyncProjectsResponse } from "../../shared/types/project-sync.types";
import { useAuth } from "../contexts/AuthContext";

// Query keys factory - centralized key management
export const projectKeys = {
  all: ["projects"] as const,
  lists: () => [...projectKeys.all, "list"] as const,
  list: () => [...projectKeys.lists()] as const,
  details: () => [...projectKeys.all, "detail"] as const,
  detail: (id: string) => [...projectKeys.details(), id] as const,
};

// Helper to get auth token
function getAuthToken(): string | null {
  return localStorage.getItem("token");
}

// Helper to make authenticated API calls
async function fetchWithAuth(
  url: string,
  options: RequestInit = {},
  onUnauthorized?: () => void
) {
  const token = getAuthToken();

  const response = await fetch(url, {
    ...options,
    headers: {
      "Content-Type": "application/json",
      ...(token && { Authorization: `Bearer ${token}` }),
      ...options.headers,
    },
  });

  if (!response.ok) {
    // Handle 401 Unauthorized - invalid or missing token
    if (response.status === 401 && onUnauthorized) {
      onUnauthorized();
      throw new Error("Session expired");
    }

    const error: ErrorResponse = await response.json().catch(() => ({
      error: "An error occurred",
    }));
    throw new Error(error.error || "An error occurred");
  }

  return response.json();
}

/**
 * Fetch all projects
 */
async function fetchProjects(onUnauthorized?: () => void): Promise<Project[]> {
  const data: ProjectsResponse = await fetchWithAuth("/api/projects", {}, onUnauthorized);
  return data.data;
}

/**
 * Fetch a single project by ID
 */
async function fetchProject(id: string, onUnauthorized?: () => void): Promise<Project> {
  const data: ProjectResponse = await fetchWithAuth(`/api/projects/${id}`, {}, onUnauthorized);
  return data.data;
}

/**
 * Create a new project
 */
async function createProject(project: CreateProjectRequest, onUnauthorized?: () => void): Promise<Project> {
  const data: ProjectResponse = await fetchWithAuth("/api/projects", {
    method: "POST",
    body: JSON.stringify(project),
  }, onUnauthorized);
  return data.data;
}

/**
 * Update a project
 */
async function updateProject(id: string, project: UpdateProjectRequest, onUnauthorized?: () => void): Promise<Project> {
  const data: ProjectResponse = await fetchWithAuth(`/api/projects/${id}`, {
    method: "PATCH",
    body: JSON.stringify(project),
  }, onUnauthorized);
  return data.data;
}

/**
 * Delete a project
 */
async function deleteProject(id: string, onUnauthorized?: () => void): Promise<Project> {
  const data: ProjectResponse = await fetchWithAuth(`/api/projects/${id}`, {
    method: "DELETE",
  }, onUnauthorized);
  return data.data;
}

/**
 * Hook to fetch all projects
 */
export function useProjects(): UseQueryResult<Project[], Error> {
  const { handleInvalidToken } = useAuth();

  return useQuery({
    queryKey: projectKeys.list(),
    queryFn: () => fetchProjects(handleInvalidToken),
  });
}

/**
 * Hook to fetch a single project
 */
export function useProject(id: string): UseQueryResult<Project, Error> {
  const { handleInvalidToken } = useAuth();

  return useQuery({
    queryKey: projectKeys.detail(id),
    queryFn: () => fetchProject(id, handleInvalidToken),
    enabled: !!id, // Only run if id is provided
  });
}

/**
 * Hook to create a new project
 */
export function useCreateProject(): UseMutationResult<
  Project,
  Error,
  CreateProjectRequest
> {
  const queryClient = useQueryClient();
  const { handleInvalidToken } = useAuth();

  return useMutation({
    mutationFn: (project) => createProject(project, handleInvalidToken),
    onSuccess: (newProject) => {
      // Invalidate and refetch projects list
      queryClient.invalidateQueries({ queryKey: projectKeys.lists() });

      // Optionally add the new project to cache optimistically
      queryClient.setQueryData<Project[]>(projectKeys.list(), (old) => {
        return old ? [newProject, ...old] : [newProject];
      });

      toast.success("Project created successfully");
    },
    onError: (error) => {
      toast.error(error.message || "Failed to create project");
    },
  });
}

/**
 * Hook to update a project
 */
export function useUpdateProject(): UseMutationResult<
  Project,
  Error,
  { id: string; data: UpdateProjectRequest }
> {
  const queryClient = useQueryClient();
  const { handleInvalidToken } = useAuth();

  return useMutation({
    mutationFn: ({ id, data }) => updateProject(id, data, handleInvalidToken),
    onSuccess: (updatedProject) => {
      // Update the project in the list cache
      queryClient.setQueryData<Project[]>(projectKeys.list(), (old) => {
        if (!old) return [updatedProject];
        return old.map((project) =>
          project.id === updatedProject.id ? updatedProject : project
        );
      });

      // Update the individual project cache
      queryClient.setQueryData(
        projectKeys.detail(updatedProject.id),
        updatedProject
      );

      toast.success("Project updated successfully");
    },
    onError: (error) => {
      toast.error(error.message || "Failed to update project");
    },
  });
}

/**
 * Hook to delete a project
 */
export function useDeleteProject(): UseMutationResult<
  Project,
  Error,
  string
> {
  const queryClient = useQueryClient();
  const { handleInvalidToken } = useAuth();

  return useMutation({
    mutationFn: (id) => deleteProject(id, handleInvalidToken),
    onSuccess: (deletedProject) => {
      // Remove the project from the list cache
      queryClient.setQueryData<Project[]>(projectKeys.list(), (old) => {
        if (!old) return [];
        return old.filter((project) => project.id !== deletedProject.id);
      });

      // Remove the individual project cache
      queryClient.removeQueries({ queryKey: projectKeys.detail(deletedProject.id) });

      toast.success("Project deleted successfully");
    },
    onError: (error) => {
      toast.error(error.message || "Failed to delete project");
    },
  });
}

/**
 * Sync projects from Claude CLI
 */
async function syncProjects(onUnauthorized?: () => void): Promise<SyncProjectsResponse> {
  const data: { data: SyncProjectsResponse } = await fetchWithAuth("/api/projects/sync", {
    method: "POST",
  }, onUnauthorized);
  return data.data;
}

/**
 * Hook to sync projects from Claude CLI
 */
export function useSyncProjects(): UseMutationResult<
  SyncProjectsResponse,
  Error,
  void
> {
  const queryClient = useQueryClient();
  const { handleInvalidToken } = useAuth();

  return useMutation({
    mutationFn: () => syncProjects(handleInvalidToken),
    onSuccess: (data) => {
      // Invalidate projects list to trigger refetch
      queryClient.invalidateQueries({ queryKey: projectKeys.lists() });

      // Show success toast with sync stats
      toast.success(
        `Projects synced: ${data.projectsImported} imported, ${data.projectsUpdated} updated`
      );
    },
    onError: (error) => {
      toast.error(error.message || "Failed to sync projects");
    },
  });
}

import type { FastifyInstance } from "fastify";
import { projectService } from "../services/project.service";
import { projectSyncService } from "../services/project-sync.service";
import { FileService } from "../services/file.service";
import {
  createProjectSchema,
  updateProjectSchema,
  projectIdSchema,
  fileContentQuerySchema,
  fileContentBodySchema,
} from "../schemas/project.schema";
import {
  projectsResponseSchema,
  projectResponseSchema,
  errorResponse,
  fileTreeResponseSchema,
  fileContentResponseSchema,
  fileContentSaveResponseSchema,
} from "../schemas/response.schema";
import type {
  CreateProjectRequest,
  UpdateProjectRequest,
} from "../../shared/types/project.types";

export async function projectRoutes(fastify: FastifyInstance) {
  // Create file service with logger
  const fileService = new FileService(fastify.log);

  /**
   * GET /api/projects
   * Get all projects
   */
  fastify.get(
    "/api/projects",
    {
      preHandler: fastify.authenticate,
      schema: {
        response: {
          200: projectsResponseSchema,
        },
      },
    },
    async (request, reply) => {
      const projects = await projectService.getAllProjects();
      return reply.send({ data: projects });
    }
  );

  /**
   * POST /api/projects/sync
   * Sync projects from ~/.claude/projects/ directory
   */
  fastify.post(
    "/api/projects/sync",
    {
      preHandler: fastify.authenticate,
      schema: {
        response: {
          200: {
            type: "object",
            properties: {
              data: {
                type: "object",
                properties: {
                  projectsImported: { type: "number" },
                  projectsUpdated: { type: "number" },
                  totalSessionsSynced: { type: "number" },
                },
              },
            },
          },
          401: errorResponse,
          500: errorResponse,
        },
      },
    },
    async (request, reply) => {
      try {
        const userId = request.user?.id;

        if (!userId) {
          return reply.code(401).send({
            error: {
              message: "Unauthorized",
              statusCode: 401,
            },
          });
        }

        const syncResults = await projectSyncService.syncFromClaudeProjects(
          userId
        );

        return reply.send({ data: syncResults });
      } catch (error) {
        fastify.log.error({ error }, "Error syncing projects");
        return reply.code(500).send({
          error: {
            message: "Failed to sync projects",
            statusCode: 500,
          },
        });
      }
    }
  );

  /**
   * GET /api/projects/:id
   * Get a single project by ID
   */
  fastify.get<{
    Params: { id: string };
  }>(
    "/api/projects/:id",
    {
      preHandler: fastify.authenticate,
      schema: {
        params: projectIdSchema,
        response: {
          200: projectResponseSchema,
          404: errorResponse,
        },
      },
    },
    async (request, reply) => {
      const project = await projectService.getProjectById(request.params.id);

      if (!project) {
        return reply.code(404).send({
          error: {
            message: "Project not found",
            statusCode: 404,
          },
        });
      }

      return reply.send({ data: project });
    }
  );

  /**
   * POST /api/projects
   * Create a new project
   */
  fastify.post<{
    Body: CreateProjectRequest;
  }>(
    "/api/projects",
    {
      preHandler: fastify.authenticate,
      schema: {
        body: createProjectSchema,
        response: {
          201: projectResponseSchema,
          409: errorResponse,
        },
      },
    },
    async (request, reply) => {
      // Check if project with same path already exists
      const exists = await projectService.projectExistsByPath(request.body.path);
      if (exists) {
        return reply.code(409).send({
          error: {
            message: "A project with this path already exists",
            code: "PROJECT_EXISTS",
            statusCode: 409,
          },
        });
      }

      const project = await projectService.createProject(request.body);
      return reply.code(201).send({ data: project });
    }
  );

  /**
   * PATCH /api/projects/:id
   * Update an existing project
   */
  fastify.patch<{
    Params: { id: string };
    Body: UpdateProjectRequest;
  }>(
    "/api/projects/:id",
    {
      preHandler: fastify.authenticate,
      schema: {
        params: projectIdSchema,
        body: updateProjectSchema,
        response: {
          200: projectResponseSchema,
          404: errorResponse,
        },
      },
    },
    async (request, reply) => {
      // Check if body is empty
      if (Object.keys(request.body).length === 0) {
        return reply.code(400).send({
          error: {
            message: "At least one field must be provided for update",
            code: "VALIDATION_ERROR",
            statusCode: 400,
          },
        });
      }

      const project = await projectService.updateProject(
        request.params.id,
        request.body
      );

      if (!project) {
        return reply.code(404).send({
          error: {
            message: "Project not found",
            statusCode: 404,
          },
        });
      }

      return reply.send({ data: project });
    }
  );

  /**
   * DELETE /api/projects/:id
   * Delete a project
   */
  fastify.delete<{
    Params: { id: string };
  }>(
    "/api/projects/:id",
    {
      preHandler: fastify.authenticate,
      schema: {
        params: projectIdSchema,
        response: {
          200: projectResponseSchema,
          404: errorResponse,
        },
      },
    },
    async (request, reply) => {
      const project = await projectService.deleteProject(request.params.id);

      if (!project) {
        return reply.code(404).send({
          error: {
            message: "Project not found",
            statusCode: 404,
          },
        });
      }

      return reply.send({ data: project });
    }
  );

  /**
   * GET /api/projects/:id/files
   * Get file tree for a project
   */
  fastify.get<{
    Params: { id: string };
  }>(
    "/api/projects/:id/files",
    {
      preHandler: fastify.authenticate,
      schema: {
        params: projectIdSchema,
        response: {
          200: fileTreeResponseSchema,
          403: errorResponse,
          404: errorResponse,
        },
      },
    },
    async (request, reply) => {
      try {
        const files = await fileService.getProjectFiles(request.params.id);
        return reply.send({ data: files });
      } catch (error) {
        // Handle specific error messages
        const errorMessage = (error as Error).message;
        if (errorMessage === 'Project not found') {
          return reply.code(404).send({
            error: {
              message: "Project not found",
              statusCode: 404,
            },
          });
        }
        if (errorMessage === 'Project path is not accessible') {
          return reply.code(403).send({
            error: {
              message: "Project path is not accessible",
              statusCode: 403,
            },
          });
        }

        throw error;
      }
    }
  );

  /**
   * GET /api/projects/:id/files/content
   * Get file content
   */
  fastify.get<{
    Params: { id: string };
    Querystring: { path: string };
  }>(
    "/api/projects/:id/files/content",
    {
      preHandler: fastify.authenticate,
      schema: {
        params: projectIdSchema,
        querystring: fileContentQuerySchema,
        response: {
          200: fileContentResponseSchema,
          403: errorResponse,
          404: errorResponse,
        },
      },
    },
    async (request, reply) => {
      try {
        const content = await fileService.readFile(
          request.params.id,
          request.query.path
        );
        return reply.send({ content });
      } catch (error) {
        const errorMessage = (error as Error).message;
        if (errorMessage === "Project not found") {
          return reply.code(404).send({
            error: {
              message: "Project not found",
              statusCode: 404,
            },
          });
        }
        if (
          errorMessage === "File not found or not accessible" ||
          errorMessage === "Access denied: File is outside project directory"
        ) {
          return reply.code(403).send({
            error: {
              message: errorMessage,
              statusCode: 403,
            },
          });
        }

        throw error;
      }
    }
  );

  /**
   * POST /api/projects/:id/files/content
   * Save file content
   */
  fastify.post<{
    Params: { id: string };
    Body: { path: string; content: string };
  }>(
    "/api/projects/:id/files/content",
    {
      preHandler: fastify.authenticate,
      schema: {
        params: projectIdSchema,
        body: fileContentBodySchema,
        response: {
          200: fileContentSaveResponseSchema,
          403: errorResponse,
          404: errorResponse,
        },
      },
    },
    async (request, reply) => {
      try {
        await fileService.writeFile(
          request.params.id,
          request.body.path,
          request.body.content
        );
        return reply.send({ success: true });
      } catch (error) {
        const errorMessage = (error as Error).message;
        if (errorMessage === "Project not found") {
          return reply.code(404).send({
            error: {
              message: "Project not found",
              statusCode: 404,
            },
          });
        }
        if (errorMessage === "Access denied: File is outside project directory") {
          return reply.code(403).send({
            error: {
              message: errorMessage,
              statusCode: 403,
            },
          });
        }

        throw error;
      }
    }
  );
}

// Shared types between frontend and backend for type safety across the stack

export interface Project {
  id: string;
  name: string;
  path: string;
  created_at: Date;
  updated_at: Date;
}

// Request/Response types for API endpoints
export interface CreateProjectRequest {
  name: string;
  path: string;
}

export interface UpdateProjectRequest {
  name?: string;
  path?: string;
}

export interface ProjectResponse {
  data: Project;
}

export interface ProjectsResponse {
