/**
 * Test page to verify chat layout without complex nested structure
 */
import { useState } from "react";
import {
  PromptInput,
  PromptInputBody,
  PromptInputTextarea,
  PromptInputFooter,
  PromptInputTools,
  PromptInputSubmit,
} from "../../components/ai-elements/prompt-input";

export default function ChatTest() {
  const [inputValue, setInputValue] = useState("");

  const handleSubmit = async (message: { text?: string; files?: any[] }) => {
    console.log("Message submitted:", message);
    setInputValue("");
  };

  // Generate dummy messages for testing scroll
  const dummyMessages = Array.from({ length: 50 }, (_, i) => ({
    id: i,
    role: i % 2 === 0 ? "user" : "assistant",
    text: `This is test message ${i + 1}. Lorem ipsum dolor sit amet, consectetur adipiscing elit.`,
  }));

  return (
    <div className="flex flex-col h-screen">
      {/* Scrollable messages area */}
      <div className="flex-1 overflow-y-auto bg-background">
        <div className="mx-auto max-w-3xl p-4 space-y-4">
          {dummyMessages.map((msg) => (
            <div
              key={msg.id}
              className={`p-4 rounded-lg ${
                msg.role === "user"
                  ? "bg-primary text-primary-foreground ml-auto max-w-[80%]"
                  : "bg-muted max-w-[80%]"
              }`}
            >
              <div className="text-xs font-medium mb-1 opacity-70">
                {msg.role === "user" ? "You" : "Assistant"}
              </div>
              <div>{msg.text}</div>
            </div>
          ))}
        </div>
      </div>

      {/* Fixed prompt input at bottom */}
      <div className="shrink-0 border-t bg-background">
        <div className="mx-auto max-w-3xl px-4 py-4">
          <PromptInput onSubmit={handleSubmit}>
            <PromptInputBody>
              <PromptInputTextarea
                onChange={(e) => setInputValue(e.target.value)}
                value={inputValue}
                placeholder="Send a message..."
              />
            </PromptInputBody>
            <PromptInputFooter>
              <PromptInputTools>
                <PromptInputSubmit disabled={!inputValue.trim()} />
              </PromptInputTools>
            </PromptInputFooter>
          </PromptInput>
        </div>
      </div>
    </div>
  );
}

