/**
 * Hook to load and parse Claude session JSONL data with WebSocket streaming support
 */

import { useState, useEffect } from 'react';
import type { ChatMessage } from '../../shared/types/chat';
import { parseJSONLSession, extractToolResults } from '../utils/parseClaudeSession';
import { normalizeMessage } from '../utils/sessionAdapters';
import { useChatWebSocket } from './useChatWebSocket';
import { useAuth } from '../contexts/AuthContext';

interface UseClaudeSessionOptions {
  sessionId: string;
  projectId: string;
  enableWebSocket?: boolean;
}

interface UseClaudeSessionReturn {
  messages: ChatMessage[];
  toolResults: Map<string, { content: string; is_error?: boolean }>;
  isLoading: boolean;
  error: Error | null;
  isConnected?: boolean;
  isStreaming?: boolean;
  sendMessage?: (options: { message: string; images?: string[]; config?: Record<string, any> }) => void;
  reconnect?: () => void;
}

/**
 * Load and parse a Claude session from JSONL file with optional WebSocket streaming
 *
 * @param options - Session options including sessionId, projectId, and enableWebSocket
 * @returns Parsed messages, tool results, loading state, error, and WebSocket controls
 *
 * @example
 * ```tsx
 * const { messages, isLoading, sendMessage } = useClaudeSession({
 *   sessionId: 'abc-123',
 *   projectId: 'project-1',
 *   enableWebSocket: true
 * });
 * ```
 */
export function useClaudeSession(
  options: UseClaudeSessionOptions
): UseClaudeSessionReturn {
  const { sessionId, projectId, enableWebSocket = false } = options;
  const { handleInvalidToken } = useAuth();

  const [messages, setMessages] = useState<ChatMessage[]>([]);
  const [toolResults, setToolResults] = useState<Map<string, { content: string; is_error?: boolean }>>(
    new Map()
  );
  const [isLoading, setIsLoading] = useState(true);
  const [error, setError] = useState<Error | null>(null);

  // Always call useChatWebSocket (Rules of Hooks - must be called unconditionally)
  // We'll conditionally use its return values based on enableWebSocket flag
  const webSocket = useChatWebSocket(sessionId, projectId);

  // Load initial messages from JSONL file
  useEffect(() => {
    let cancelled = false;

    const loadSessionMessages = async () => {
      try {
        setIsLoading(true);
        setError(null);

        const token = localStorage.getItem('token');
        const response = await fetch(`/api/projects/${projectId}/sessions/${sessionId}/messages`, {
          headers: {
            'Authorization': `Bearer ${token}`,
          },
        });

        if (!response.ok) {
          // Handle 401 Unauthorized - invalid or missing token
          if (response.status === 401) {
            handleInvalidToken();
            throw new Error('Session expired');
          }
          // Handle 404 - session doesn't exist yet (new session)
          if (response.status === 404) {
            console.log('Session not found - this is a new session');
            if (cancelled) return;
            setMessages([]);
            setToolResults(new Map());
            if (enableWebSocket && webSocket.setMessages) {
              webSocket.setMessages([]);
            }
            return;
          }
          throw new Error(`Failed to load session messages: ${response.statusText}`);
        }

        const data = await response.json();
        const messagesArray = data.data || [];

        if (cancelled) return;

        // API already returns parsed messages array - normalize them
        const parsedMessages: ChatMessage[] = messagesArray.map((msg: any) => normalizeMessage(msg));

        // Extract tool results from messages
        const toolResultsMap = new Map<string, { content: string; is_error?: boolean }>();
        messagesArray.forEach((msg: any) => {
          if (msg.role === 'assistant' && Array.isArray(msg.content)) {
            msg.content.forEach((block: any) => {
              if (block.type === 'tool_result' && block.tool_use_id) {
                toolResultsMap.set(block.tool_use_id, {
                  content: typeof block.content === 'string' ? block.content : JSON.stringify(block.content),
                  is_error: block.is_error,
                });
              }
            });
          }
        });

        console.log('Loaded session messages:', parsedMessages.length);

        setMessages(parsedMessages);
        setToolResults(toolResultsMap);

        // If WebSocket is enabled, initialize it with existing messages
        if (enableWebSocket && webSocket.setMessages) {
          webSocket.setMessages(parsedMessages.map(msg => ({
            role: msg.role as 'user' | 'assistant',
            content: msg.content,
            timestamp: msg.timestamp,
            images: msg.images,
          })));
        }
      } catch (err) {
        if (!cancelled) {
          const errorMessage = err instanceof Error ? err.message : 'Unknown error loading session';
          setError(new Error(errorMessage));
          console.error('Error loading session:', err);
        }
      } finally {
        if (!cancelled) {
          setIsLoading(false);
        }
      }
    };

    if (sessionId && projectId) {
      loadSessionMessages();
    }

    return () => {
      cancelled = true;
    };
  }, [sessionId, projectId, enableWebSocket]);

  // Merge JSONL messages with WebSocket messages when WebSocket is enabled
  const finalMessages = enableWebSocket ? webSocket.messages : messages;
  const finalError = error || (enableWebSocket && webSocket.error ? new Error(webSocket.error) : null);

  return {
    messages: finalMessages,
    toolResults,
    isLoading,
    error: finalError,
    isConnected: enableWebSocket ? webSocket.isConnected : undefined,
    isStreaming: enableWebSocket ? webSocket.isStreaming : undefined,
    sendMessage: enableWebSocket ? webSocket.sendMessage : undefined,
    reconnect: enableWebSocket ? webSocket.reconnect : undefined,
  };
}

import * as React from "react";
import { useParams } from "react-router-dom";
import { ChatInterface } from "../components/chat/ChatInterface";
import { ChatPromptInput } from "../components/chat/ChatPromptInput";
import { useClaudeSession } from "../hooks/useClaudeSession";
import { useChatContext } from "../contexts/ChatContext";

export default function ProjectChat() {
  const { id, sessionId } = useParams<{ id: string; sessionId?: string }>();
  const { setCurrentSession, activeSessions } = useChatContext();

  // Get session metadata for token count
  const sessionMetadata = sessionId
    ? activeSessions.get(sessionId)?.metadata
    : undefined;

  // Load session with WebSocket if sessionId is present
  const {
    messages,
    toolResults,
    isLoading,
    error,
    isConnected,
    isStreaming,
    sendMessage,
    reconnect,
  } = useClaudeSession({
    sessionId: sessionId || "",
    projectId: id || "",
    enableWebSocket: !!sessionId,
  });

  // Set current session in context
  React.useEffect(() => {
    if (sessionId) {
      setCurrentSession(sessionId);
    }
    return () => setCurrentSession(null);
  }, [sessionId, setCurrentSession]);

  const handleSubmit = async (message: string, images?: File[]) => {
    console.log('[ProjectChat] handleSubmit called:', {
      message: message.substring(0, 100),
      imagesCount: images?.length || 0,
      hasSendMessage: !!sendMessage,
      sessionId,
      isConnected
    });

    if (!sendMessage) {
      console.error('[ProjectChat] sendMessage is not available');
      return;
    }

    if (!sessionId) {
      console.error('[ProjectChat] sessionId is missing');
      return;
    }

    // Convert images to base64 before sending via WebSocket
    const imagePaths = images ? await handleImageUpload(images) : undefined;
    console.log('[ProjectChat] Calling sendMessage with processed data');
    sendMessage({ message, images: imagePaths });
  };

  const handleImageUpload = async (files: File[]): Promise<string[]> => {
    // Convert File objects to base64 data URLs for WebSocket transmission
    return Promise.all(
      files.map(
        (file) =>
          new Promise<string>((resolve, reject) => {
            const reader = new FileReader();
            reader.onload = () => resolve(reader.result as string);
            reader.onerror = reject;
            reader.readAsDataURL(file);
          })
      )
    );
  };

  return (
    <div className="absolute inset-0 flex flex-col overflow-hidden">
      {/* Connection status banner */}
      {sessionId && !isConnected && (
        <div className="bg-yellow-100 border-b border-yellow-200 px-4 py-2 text-sm text-yellow-800 flex items-center justify-between">
          <span>Disconnected from chat session</span>
          <button
            onClick={reconnect}
            className="text-yellow-900 underline hover:no-underline"
          >
            Reconnect
          </button>
        </div>
      )}

      {/* Chat Messages Container - takes up remaining space */}
      <div className="flex-1 overflow-hidden">
        <ChatInterface
          projectId={id!}
          sessionId={sessionId}
          messages={messages}
          toolResults={toolResults}
          isLoading={isLoading}
          error={error}
          isStreaming={isStreaming}
        />
      </div>

      {/* Fixed Input Container at Bottom */}
      <div className="pb-4">
        <div className="mx-auto max-w-4xl">
          {/* Token usage display */}
          {sessionId && sessionMetadata && (
            <div className="px-4 pb-2 text-xs text-muted-foreground text-center">
              <span>
                {sessionMetadata.totalTokens.toLocaleString()} tokens used
              </span>
            </div>
          )}
          <ChatPromptInput
            onSubmit={handleSubmit}
            disabled={!sessionId || !isConnected}
            isStreaming={isStreaming}
          />
        </div>
      </div>
    </div>
  );
}

import React, { createContext, useContext, useState, useCallback } from 'react';
import type { AgentSessionMetadata } from '../../shared/types';

interface ActiveSession {
  sessionId: string;
  wsConnection?: WebSocket;
  metadata?: AgentSessionMetadata;
}

interface ChatContextValue {
  activeSessions: Map<string, ActiveSession>;
  currentSessionId: string | null;
  setCurrentSession: (sessionId: string | null) => void;
  createSession: (sessionId: string) => void;
  updateSessionMetadata: (sessionId: string, metadata: Partial<AgentSessionMetadata>) => void;
  setWebSocketConnection: (sessionId: string, ws: WebSocket) => void;
  removeWebSocketConnection: (sessionId: string) => void;
}

const ChatContext = createContext<ChatContextValue | undefined>(undefined);

export function ChatProvider({ children }: { children: React.ReactNode }) {
  const [activeSessions, setActiveSessions] = useState<Map<string, ActiveSession>>(new Map());
  const [currentSessionId, setCurrentSessionId] = useState<string | null>(null);

  const setCurrentSession = useCallback((sessionId: string | null) => {
    setCurrentSessionId(sessionId);
  }, []);

  const createSession = useCallback((sessionId: string) => {
    setActiveSessions((prev) => {
      const next = new Map(prev);
      if (!next.has(sessionId)) {
        next.set(sessionId, { sessionId });
      }
      return next;
    });
  }, []);

  const updateSessionMetadata = useCallback((sessionId: string, metadata: Partial<AgentSessionMetadata>) => {
    setActiveSessions((prev) => {
      const next = new Map(prev);
      const session = next.get(sessionId);
      if (session) {
        next.set(sessionId, {
          ...session,
          metadata: {
            ...session.metadata,
            ...metadata,
          } as AgentSessionMetadata,
        });
      }
      return next;
    });
  }, []);

  const setWebSocketConnection = useCallback((sessionId: string, ws: WebSocket) => {
    setActiveSessions((prev) => {
      const next = new Map(prev);
      const session = next.get(sessionId) || { sessionId };
      next.set(sessionId, {
        ...session,
        wsConnection: ws,
      });
      return next;
    });
  }, []);

  const removeWebSocketConnection = useCallback((sessionId: string) => {
    setActiveSessions((prev) => {
      const next = new Map(prev);
      const session = next.get(sessionId);
      if (session) {
        // Close existing WebSocket if present
        if (session.wsConnection) {
          session.wsConnection.close();
        }
        next.set(sessionId, {
          ...session,
          wsConnection: undefined,
        });
      }
      return next;
    });
  }, []);

  const value: ChatContextValue = {
    activeSessions,
    currentSessionId,
    setCurrentSession,
    createSession,
    updateSessionMetadata,
    setWebSocketConnection,
    removeWebSocketConnection,
  };

  return <ChatContext.Provider value={value}>{children}</ChatContext.Provider>;
}

export function useChatContext() {
  const context = useContext(ChatContext);
  if (context === undefined) {
    throw new Error('useChatContext must be used within a ChatProvider');
  }
  return context;
}

import { Navigate, Outlet } from "react-router-dom";
import { useAuth } from "../contexts/AuthContext";
import { ChatProvider } from "../contexts/ChatContext";
import { AppSidebar } from "@/components/app-sidebar";
import {
  SidebarInset,
  SidebarProvider,
} from "@/components/ui/sidebar";

function ProtectedLayout() {
  const { isAuthenticated } = useAuth();

  if (!isAuthenticated) {
    return <Navigate to="/login" replace />;
  }

  return (
    <ChatProvider>
      <SidebarProvider
        style={
          {
            "--sidebar-width": "350px",
          } as React.CSSProperties
        }
      >
        <AppSidebar />
        <SidebarInset>
          <Outlet />
        </SidebarInset>
      </SidebarProvider>
    </ChatProvider>
  );
}

export default ProtectedLayout;

/**
 * Agent Session Types
 * Shared type definitions for chat sessions with Claude Code
 */

/**
 * Session metadata stored in AgentSession.metadata JSON field
 */
export interface AgentSessionMetadata {
  totalTokens: number;
  messageCount: number;
  lastMessageAt: string; // ISO 8601 timestamp
  firstMessagePreview: string; // First user message preview (truncated)
}

/**
 * Request body for creating a new session
 */
export interface CreateSessionRequest {
  sessionId: string; // Pre-generated UUID
}

/**
 * Session response from API
 */
export interface SessionResponse {
  id: string;
  projectId: string;
  userId: string;
  metadata: AgentSessionMetadata;
  created_at: Date;
  updated_at: Date;
}

/**
 * Request to update session metadata
 */
export interface UpdateSessionMetadataRequest {
  metadata: Partial<AgentSessionMetadata>;
}

/**
 * Request to sync sessions for a project
 */
export interface SyncSessionsRequest {
  projectId: string;
}

/**
 * Sync sessions response
 */
export interface SyncSessionsResponse {
  synced: number; // Number of sessions synced from filesystem
  created: number; // Number of new sessions created
  updated: number; // Number of existing sessions updated
}

import { useQuery } from '@tanstack/react-query';
import { useAuth } from '../contexts/AuthContext';
import type { SessionResponse } from '../../shared/types';

interface UseAgentSessionsOptions {
  projectId: string;
  enabled?: boolean;
}

async function fetchAgentSessions(projectId: string, onUnauthorized?: () => void): Promise<SessionResponse[]> {
  const response = await fetch(`/api/projects/${projectId}/sessions`, {
    headers: {
      'Authorization': `Bearer ${localStorage.getItem('token')}`,
    },
  });

  if (!response.ok) {
    // Handle 401 Unauthorized - invalid or missing token
    if (response.status === 401 && onUnauthorized) {
      onUnauthorized();
      throw new Error('Session expired');
    }
    throw new Error(`Failed to fetch sessions: ${response.statusText}`);
  }

  const result = await response.json();
  return result.data || [];
}

export function useAgentSessions({ projectId, enabled = true }: UseAgentSessionsOptions) {
  const { handleInvalidToken } = useAuth();

  return useQuery({
    queryKey: ['agentSessions', projectId],
    queryFn: () => fetchAgentSessions(projectId, handleInvalidToken),
    enabled: enabled && !!projectId,
    refetchOnWindowFocus: false,
    staleTime: 30000, // 30 seconds
  });
}

import { useState, useEffect, useCallback, useRef } from "react";
import { useChatContext } from "../contexts/ChatContext";
import type { AgentSessionMetadata } from "../../shared/types";
import type { ContentBlock } from "../../shared/types/chat";

export interface ChatMessage {
  id?: string;
  role: "user" | "assistant";
  content: ContentBlock[];
  timestamp?: string | number;
  images?: string[];
  isStreaming?: boolean;
  isError?: boolean;
}

interface WebSocketMessage {
  type: "stream_event" | "stream_output" | "message_complete" | "error";
  sessionId?: string;
  event?: {
    type: string;
    data?: any;
  };
  metadata?: AgentSessionMetadata;
  response?: {
    output: string;
    sessionId: string;
    status: "success" | "error" | "timeout";
    [key: string]: any;
  };
  events?: Array<{
    type: string;
    [key: string]: any;
  }>;
  data?: any;
  message?: string;
  error?: {
    message: string;
    stack?: string;
    name?: string;
    details?: any;
  };
}

interface SendMessageOptions {
  message: string;
  images?: string[];
  config?: Record<string, any>;
}

export function useChatWebSocket(sessionId: string, projectId: string) {
  const [messages, setMessages] = useState<ChatMessage[]>([]);
  const [isConnected, setIsConnected] = useState(false);
  const [error, setError] = useState<string | null>(null);
  const [isStreaming, setIsStreaming] = useState(false);

  const wsRef = useRef<WebSocket | null>(null);
  const reconnectTimeoutRef = useRef<NodeJS.Timeout | null>(null);
  const reconnectAttemptsRef = useRef(0);
  const maxReconnectAttempts = 5;
  const isReconnectingRef = useRef(false);
  const isMountedRef = useRef(true);
  const isFirstMessageRef = useRef(true);

  const {
    setWebSocketConnection,
    removeWebSocketConnection,
    updateSessionMetadata,
  } = useChatContext();

  const connect = useCallback(() => {
    if (!sessionId || !projectId || !isMountedRef.current) return;

    const token = localStorage.getItem("token");
    if (!token) {
      setError("Authentication token not found");
      return;
    }

    // Close existing connection if any
    if (wsRef.current) {
      // Remove listeners to prevent reconnect loop
      wsRef.current.onclose = null;
      wsRef.current.onerror = null;
      wsRef.current.close();
      wsRef.current = null;
    }

    // Clear any pending reconnect timeout
    if (reconnectTimeoutRef.current) {
      clearTimeout(reconnectTimeoutRef.current);
      reconnectTimeoutRef.current = null;
    }

    const protocol = window.location.protocol === "https:" ? "wss:" : "ws:";
    const wsUrl = `${protocol}//${window.location.host}/ws/chat/${sessionId}?token=${token}`;

    const ws = new WebSocket(wsUrl);

    ws.onopen = () => {
      console.log("WebSocket connected for session:", sessionId);
      setIsConnected(true);
      setError(null);
      reconnectAttemptsRef.current = 0;
      wsRef.current = ws;
      setWebSocketConnection(sessionId, ws);
    };

    ws.onmessage = (event) => {
      try {
        const data: WebSocketMessage = JSON.parse(event.data);
        switch (data.type) {
          case "stream_event":
            console.log("[WebSocket Client] Stream event:", data.event);
            if (data.event) {
              handleStreamEvent(data.event);
            }
            break;

          case "stream_output":
            console.log("[WebSocket Client] Stream output:", data.data);

            // Process streaming events as they arrive
            if (data.data?.events && Array.isArray(data.data.events)) {
              for (const event of data.data.events) {
                console.log("[WebSocket Client] Processing event:", event.type, event);

                // Handle assistant messages with structured content blocks
                if (event.type === "assistant" && event.message?.content) {
                  const content = event.message.content;

                  // Content should be an array of ContentBlocks
                  if (Array.isArray(content)) {
                    setMessages((prev) => {
                      const lastMessage = prev[prev.length - 1];
                      if (
                        lastMessage &&
                        lastMessage.role === "assistant" &&
                        lastMessage.isStreaming
                      ) {
                        // Merge content blocks with existing streaming message
                        const existingContent = Array.isArray(lastMessage.content)
                          ? lastMessage.content
                          : [];

                        // Merge or append content blocks
                        const mergedContent = [...existingContent];

                        for (const newBlock of content) {
                          // Find existing block of same type at same position
                          const existingBlockIndex = mergedContent.findIndex(
                            (b: any) => b.type === newBlock.type && b.id === newBlock.id
                          );

                          if (existingBlockIndex >= 0) {
                            // Merge with existing block (for streaming text/thinking)
                            const existingBlock = mergedContent[existingBlockIndex] as any;
                            if (newBlock.type === 'text' && existingBlock.type === 'text') {
                              mergedContent[existingBlockIndex] = {
                                ...existingBlock,
                                text: newBlock.text,
                              };
                            } else if (newBlock.type === 'thinking' && existingBlock.type === 'thinking') {
                              mergedContent[existingBlockIndex] = {
                                ...existingBlock,
                                thinking: newBlock.thinking,
                              };
                            } else {
                              // Replace for other types
                              mergedContent[existingBlockIndex] = newBlock;
                            }
                          } else {
                            // Append new block
                            mergedContent.push(newBlock);
                          }
                        }

                        return [
                          ...prev.slice(0, -1),
                          {
                            ...lastMessage,
                            content: mergedContent,
                          },
                        ];
                      } else {
                        // Create new streaming message with structured content
                        return [
                          ...prev,
                          {
                            id: crypto.randomUUID(),
                            role: "assistant",
                            content: content,
                            isStreaming: true,
                            timestamp: Date.now(),
                          },
                        ];
                      }
                    });
                  }
                }
              }
            }
            break;

          case "message_complete":
            console.log("[WebSocket Client] Message complete:", data);
            console.log("[WebSocket Client] Events received:", data.events);

            // Check if the response has an error status
            if (data.response?.status === "error") {
              console.error("[WebSocket Client] Error in message_complete response:", {
                response: data.response,
                stderr: data.response.raw?.stderr,
                error: data.response.error,
              });

              // Extract error message from stderr or error object
              const errorMessage =
                data.response.raw?.stderr ||
                data.response.error?.message ||
                data.response.output ||
                "An error occurred while processing your request";

              // Add error message as an assistant message
              setMessages((prev) => {
                // Remove any streaming message
                const filteredPrev = prev.filter(msg => !msg.isStreaming);

                return [
                  ...filteredPrev,
                  {
                    id: crypto.randomUUID(),
                    role: "assistant",
                    content: [{ type: "text", text: errorMessage }],
                    timestamp: Date.now(),
                    isError: true,
                  },
                ];
              });

              setError(errorMessage);
              setIsStreaming(false);
              break;
            }

            setIsStreaming(false);

            // Finalize the streaming message (mark as complete)
            setMessages((prev) => {
              const lastMessage = prev[prev.length - 1];
              if (
                lastMessage &&
                lastMessage.role === "assistant" &&
                lastMessage.isStreaming
              ) {
                // Mark streaming message as complete
                return [
                  ...prev.slice(0, -1),
                  {
                    ...lastMessage,
                    isStreaming: false,
                  },
                ];
              }
              // If no streaming message exists, create one from the response
              // This handles cases where streaming didn't work
              if (data.response?.output) {
                return [
                  ...prev,
                  {
                    id: crypto.randomUUID(),
                    role: "assistant",
                    content: [{ type: "text", text: data.response.output }],
                    timestamp: Date.now(),
                  },
                ];
              }
              return prev;
            });

            if (data.metadata) {
              updateSessionMetadata(sessionId, data.metadata);
            }
            break;

          case "error":
            console.error("[WebSocket Client] Error received from server:", {
              message: data.message,
              error: data.error,
              fullData: data,
            });

            // Log detailed error information
            if (data.error) {
              console.error("[WebSocket Client] Error details:", {
                name: data.error.name,
                message: data.error.message,
                stack: data.error.stack,
                details: data.error.details,
              });
            }

            // Add error message as an assistant message so it can be rendered with AI
            setMessages((prev) => [
              ...prev,
              {
                id: crypto.randomUUID(),
                role: "assistant",
                content: [{
                  type: "text",
                  text: `Error: ${data.message || "An error occurred"}\n\n${data.error?.details ? `Details: ${JSON.stringify(data.error.details, null, 2)}` : ""}`
                }],
                timestamp: Date.now(),
                isError: true,
              },
            ]);

            setError(data.message || "An error occurred");
            setIsStreaming(false);
            break;

          default:
            console.log("[WebSocket Client] Unknown message type:", data.type);
        }
      } catch (err) {
        console.error("Error parsing WebSocket message:", err);
      }
    };

    ws.onerror = (event) => {
      console.error("WebSocket error:", event);
      setError("Connection error occurred");
    };

    ws.onclose = () => {
      console.log("WebSocket closed for session:", sessionId);
      setIsConnected(false);

      // Only clear wsRef if it's the current connection
      if (wsRef.current === ws) {
        wsRef.current = null;
      }
      removeWebSocketConnection(sessionId);

      // Only attempt to reconnect if not already reconnecting and component is still mounted
      if (
        !isReconnectingRef.current &&
        isMountedRef.current &&
        reconnectAttemptsRef.current < maxReconnectAttempts
      ) {
        isReconnectingRef.current = true;
        const delay = Math.min(
          1000 * Math.pow(2, reconnectAttemptsRef.current),
          10000
        );
        reconnectAttemptsRef.current += 1;

        reconnectTimeoutRef.current = setTimeout(() => {
          console.log(
            `Reconnecting... (attempt ${reconnectAttemptsRef.current})`
          );
          isReconnectingRef.current = false;
          if (isMountedRef.current) {
            connect();
          }
        }, delay);
      }
    };
  }, [
    sessionId,
    projectId,
    setWebSocketConnection,
    removeWebSocketConnection,
    updateSessionMetadata,
  ]);

  const handleStreamEvent = useCallback(
    (event: { type: string; data?: any }) => {
      if (event.type === "output" && event.data?.text) {
        // Append streaming text to the last assistant message
        setMessages((prev) => {
          const lastMessage = prev[prev.length - 1];
          if (
            lastMessage &&
            lastMessage.role === "assistant" &&
            lastMessage.isStreaming
          ) {
            // Append to the first text block
            const updatedContent = [...lastMessage.content];
            const textBlockIndex = updatedContent.findIndex((b: any) => b.type === 'text');

            if (textBlockIndex >= 0) {
              const textBlock = updatedContent[textBlockIndex] as any;
              updatedContent[textBlockIndex] = {
                ...textBlock,
                text: textBlock.text + event.data.text,
              };
            } else {
              updatedContent.push({ type: 'text', text: event.data.text });
            }

            return [
              ...prev.slice(0, -1),
              {
                ...lastMessage,
                content: updatedContent,
              },
            ];
          } else {
            // Create new assistant message
            return [
              ...prev,
              {
                id: crypto.randomUUID(),
                role: "assistant",
                content: [{ type: "text", text: event.data.text }],
                isStreaming: true,
                timestamp: Date.now(),
              },
            ];
          }
        });
      }
    },
    []
  );

  const sendMessage = useCallback(
    ({ message, images, config }: SendMessageOptions) => {
      console.log("[useChatWebSocket] sendMessage called:", {
        sessionId,
        message: message.substring(0, 100),
        imagesCount: images?.length || 0,
        wsState: wsRef.current?.readyState,
        wsOpen: wsRef.current?.readyState === WebSocket.OPEN,
        isFirstMessage: isFirstMessageRef.current,
      });

      if (!wsRef.current || wsRef.current.readyState !== WebSocket.OPEN) {
        console.error("[useChatWebSocket] WebSocket not connected:", {
          hasWs: !!wsRef.current,
          readyState: wsRef.current?.readyState,
          CONNECTING: WebSocket.CONNECTING,
          OPEN: WebSocket.OPEN,
          CLOSING: WebSocket.CLOSING,
          CLOSED: WebSocket.CLOSED,
        });
        setError("WebSocket is not connected");
        return;
      }

      // Add user message to local state immediately
      const userMessage: ChatMessage = {
        id: crypto.randomUUID(),
        role: "user",
        content: [{ type: "text", text: message }],
        images,
        timestamp: Date.now(),
      };
      setMessages((prev) => {
        console.log(
          "[useChatWebSocket] Adding user message to state, current count:",
          prev.length
        );
        return [...prev, userMessage];
      });

      setIsStreaming(true);
      setError(null);

      // Merge config with resume flag for subsequent messages
      const mergedConfig = {
        ...config,
        // Add resume: true for all messages after the first
        ...(isFirstMessageRef.current ? {} : { resume: true, sessionId }),
      };

      // Send message via WebSocket
      const payload = {
        type: "send_message",
        sessionId,
        message,
        images,
        config: mergedConfig,
      };
      console.log("[useChatWebSocket] Sending WebSocket message:", payload);
      wsRef.current.send(JSON.stringify(payload));

      // Mark that we've sent the first message
      if (isFirstMessageRef.current) {
        isFirstMessageRef.current = false;
      }
    },
    [sessionId]
  );

  const reconnect = useCallback(() => {
    reconnectAttemptsRef.current = 0;
    connect();
  }, [connect]);

  // Connect on mount and cleanup on unmount
  useEffect(() => {
    isMountedRef.current = true;
    connect();

    return () => {
      isMountedRef.current = false;
      isReconnectingRef.current = false;

      if (reconnectTimeoutRef.current) {
        clearTimeout(reconnectTimeoutRef.current);
        reconnectTimeoutRef.current = null;
      }
      if (wsRef.current) {
        // Remove listeners to prevent reconnect on unmount
        wsRef.current.onclose = null;
        wsRef.current.onerror = null;
        wsRef.current.close();
        wsRef.current = null;
      }
    };
  }, [sessionId, projectId]); // Only depend on sessionId and projectId, not connect

  // Update isFirstMessageRef when messages are set from outside (e.g., loaded from API)
  useEffect(() => {
    if (messages.length > 0) {
      // If we have messages, it means this session already has history
      // So the next message should use resume: true
      isFirstMessageRef.current = false;
    }
  }, [messages.length]);

  return {
    messages,
    isConnected,
    isStreaming,
    error,
    sendMessage,
    reconnect,
    setMessages,
  };
}

