// Set up ResizeObserver for auto-fitting
if (containerRef.current && fitAddonRef.current) {
  let resizeTimeout: ReturnType<typeof setTimeout> | null = null;
  let lastDims = { cols: 0, rows: 0 };

  const resizeObserver = new ResizeObserver(() => {
    // Debounce resize events to prevent infinite loops
    if (resizeTimeout) {
      clearTimeout(resizeTimeout);
    }

    resizeTimeout = setTimeout(() => {
      if (fitAddonRef.current && terminalRef.current && containerRef.current) {
        try {
          // Check if container has valid dimensions
          const rect = containerRef.current.getBoundingClientRect();
          if (rect.width > 0 && rect.height > 0 && rect.height < 10000) {
            fitAddonRef.current.fit();
            const dims = fitAddonRef.current.proposeDimensions();

            // Only send resize if dimensions actually changed
            if (dims && (dims.cols !== lastDims.cols || dims.rows !== lastDims.rows)) {
              lastDims = { cols: dims.cols, rows: dims.rows };
              sendResize(dims.cols, dims.rows);
            }
          }
        } catch (e) {
          console.warn('[Terminal] Fit failed:', e);
        }
      }
    }, 100); // 100ms debounce
  });

  resizeObserver.observe(containerRef.current);
  resizeObserverRef.current = resizeObserver;
}

