const handleResize = () => {
  if (!fitAddonRef.current || !xtermRef.current) return;

  try {
    const dims = fitAddonRef.current.proposeDimensions();
    if (!dims) return;

    // Only resize if dimensions actually changed
    if (lastDimensionsRef.current) {
      if (dims.cols === lastDimensionsRef.current.cols &&
          dims.rows === lastDimensionsRef.current.rows) {
        return; // No change, skip resize
      }
    }

    fitAddonRef.current.fit();
    lastDimensionsRef.current = { cols: dims.cols, rows: dims.rows };

    // Send updated terminal size to backend
    if (sendResize) {
      sendResize(dims.cols, dims.rows);
    }
  } catch (e) {
    console.warn('[Terminal] Fit failed:', e);
  }
};

// Debounced resize handler
let resizeTimeout: ReturnType<typeof setTimeout> | null = null;
const debouncedResize = () => {
  if (resizeTimeout) {
    clearTimeout(resizeTimeout);
  }
  resizeTimeout = setTimeout(handleResize, 100);
};

window.addEventListener('resize', debouncedResize);

return () => {
  window.removeEventListener('resize', debouncedResize);
  if (resizeTimeout) {
    clearTimeout(resizeTimeout);
  }
};

