  }));

  return {
    containerHeight: rect.height,
    containerTop: rect.top,
    children
  };
}

() => {
  const xterm = document.querySelector('.xterm');
  if (!xterm) return 'xterm not found';

  const xtermRect = xterm.getBoundingClientRect();
  const xtermScreen = document.querySelector('.xterm-screen');
  const xtermRows = document.querySelector('.xterm-rows');

  return {
    xterm: {
      top: xtermRect.top,
      height: xtermRect.height,
      paddingTop: window.getComputedStyle(xterm).paddingTop,
      marginTop: window.getComputedStyle(xterm).marginTop,
    },
    xtermScreen: xtermScreen ? {
      top: xtermScreen.getBoundingClientRect().top,
      height: xtermScreen.getBoundingClientRect().height,
      paddingTop: window.getComputedStyle(xtermScreen).paddingTop,
      marginTop: window.getComputedStyle(xtermScreen).marginTop,
    } : 'not found',
    xtermRows: xtermRows ? {
      top: xtermRows.getBoundingClientRect().top,
      height: xtermRows.getBoundingClientRect().height,
      paddingTop: window.getComputedStyle(xtermRows).paddingTop,
      marginTop: window.getComputedStyle(xtermRows).marginTop,
    } : 'not found',
    firstTextContent: xtermRows ? xtermRows.textContent?.substring(0, 100) : 'no rows'
  };
}

() => {
  const xtermRows = document.querySelector('.xterm-rows');
  if (!xtermRows) return 'xterm-rows not found';

  const allDivs = Array.from(xtermRows.children);
  const divsWithContent = [];
  const emptyDivs = [];

  allDivs.forEach((div, i) => {
    const rect = div.getBoundingClientRect();
    const hasText = div.textContent && div.textContent.trim().length > 0;
    const info = {
      index: i,
      top: rect.top,
      height: rect.height,
      hasText,
      textPreview: div.textContent?.substring(0, 50) || '',
      innerHTML: div.innerHTML.substring(0, 100)
    };

    if (hasText) {
      divsWithContent.push(info);
    } else {
      emptyDivs.push(info);
    }
  });

  return {
    totalDivs: allDivs.length,
    emptyDivCount: emptyDivs.length,
    contentDivCount: divsWithContent.length,
    firstEmptyDivs: emptyDivs.slice(0, 5),
    firstContentDivs: divsWithContent.slice(0, 5)
  };
}

() => {
