import { useParams } from "react-router-dom";
import { useProject } from "../hooks/useProjects";
import { Terminal } from "../components/terminal/Terminal";
import { ShellControls } from "../components/terminal/ShellControls";
import { useShell } from "../contexts/ShellContext";

export default function ProjectShell() {
  const { id } = useParams<{ id: string }>();
  const { data: project } = useProject(id!);
  const { getSession } = useShell();

  const sessionId = `shell-${id}`;
  const session = getSession(sessionId);

  const handleRestart = () => {
    window.location.reload();
  };

  return (
    <div className="flex flex-col h-full">
      <ShellControls
        status={session?.status || 'disconnected'}
        projectName={project?.name || ''}
        onRestart={handleRestart}
      />
      <div className="flex-1 overflow-hidden">
        <Terminal sessionId={sessionId} projectId={id!} />
      </div>
    </div>
  );
}

import { useEffect, useRef, useCallback } from "react";
import { Terminal as XTerm } from "@xterm/xterm";
import { FitAddon } from "@xterm/addon-fit";
import { ClipboardAddon } from "@xterm/addon-clipboard";
import { useShell } from "../../contexts/ShellContext";
import { useShellWebSocket } from "../../hooks/useShellWebSocket";
import "@xterm/xterm/css/xterm.css";

interface TerminalProps {
  sessionId: string;
  projectId: string;
  onConnect?: () => void;
  onDisconnect?: () => void;
}

export function Terminal({
  sessionId,
  projectId,
  onConnect,
  onDisconnect,
}: TerminalProps) {
  const { getSession, addSession, updateSession } = useShell();
  const wrapperRef = useRef<HTMLDivElement>(null);
  const terminalRef = useRef<HTMLDivElement>(null);
  const xtermRef = useRef<XTerm | null>(null);
  const fitAddonRef = useRef<FitAddon | null>(null);
  const resizeObserverRef = useRef<ResizeObserver | null>(null);
  const lastDimensionsRef = useRef<{ cols: number; rows: number } | null>(null);

  // Stable callbacks for WebSocket
  const handleOutput = useCallback((data: string) => {
    xtermRef.current?.write(data);
  }, []);

  const handleExit = useCallback((exitCode: number) => {
    xtermRef.current?.writeln(
      `\r\n\x1b[1;33m[Process exited with code ${exitCode}]\x1b[0m\r\n`
    );
  }, []);

  // WebSocket connection
  const { isConnected, connect, disconnect, sendInput, sendResize } =
    useShellWebSocket({
      sessionId,
      projectId,
      enabled: true,
      onOutput: handleOutput,
      onExit: handleExit,
    });

  // Initialize terminal on mount
  useEffect(() => {
    if (!terminalRef.current) return;

    // Check if we have an existing session
    const existingSession = getSession(sessionId);

    if (existingSession?.terminal && existingSession?.fitAddon) {
      // Reuse existing terminal instance and FitAddon
      xtermRef.current = existingSession.terminal;
      fitAddonRef.current = existingSession.fitAddon;

      // Open terminal in new container
      if (terminalRef.current) {
        xtermRef.current.open(terminalRef.current);
        fitAddonRef.current.fit();
      }
    } else {
      // Create new terminal instance
      const terminal = new XTerm({
        cursorBlink: true,
        fontSize: 14,
        fontFamily: '"Cascadia Code", "Fira Code", "Courier New", monospace',
        scrollback: 10000,
        theme: {
          background: "#1e1e1e",
          foreground: "#d4d4d4",
          cursor: "#ffffff",
          cursorAccent: "#000000",
          selectionBackground: "#264f78",
          // ANSI colors (16-color palette)
          black: "#000000",
          red: "#cd3131",
          green: "#0dbc79",
          yellow: "#e5e510",
          blue: "#2472c8",
          magenta: "#bc3fbc",
          cyan: "#11a8cd",
          white: "#e5e5e5",
          brightBlack: "#666666",
          brightRed: "#f14c4c",
          brightGreen: "#23d18b",
          brightYellow: "#f5f543",
          brightBlue: "#3b8eea",
          brightMagenta: "#d670d6",
          brightCyan: "#29b8db",
          brightWhite: "#ffffff",
        },
      });

      xtermRef.current = terminal;

      // Create FitAddon
      const fitAddon = new FitAddon();
      fitAddonRef.current = fitAddon;
      terminal.loadAddon(fitAddon);

      // Use canvas renderer for better text quality
      // WebGL can appear grainy on some displays
      // try {
      //   const webglAddon = new WebglAddon();
      //   webglAddon.onContextLoss(() => {
      //     console.warn('[Terminal] WebGL context lost, disposing addon');
      //     webglAddon.dispose();
      //   });
      //   terminal.loadAddon(webglAddon);
      //   console.log('[Terminal] WebGL renderer loaded successfully');
      //   } catch (e) {
      //   console.warn('[Terminal] WebGL addon failed to load, using canvas renderer:', e);
      // }

      // Create Clipboard addon
      const clipboardAddon = new ClipboardAddon();
      terminal.loadAddon(clipboardAddon);

      // Open terminal in container
      if (terminalRef.current) {
        terminal.open(terminalRef.current);
        fitAddon.fit();
      }

      // Add session to context
      addSession(sessionId, {
        projectId,
        terminal,
        fitAddon,
        containerElement: null,
        status: "disconnected",
      });

      // Handle user input - send to WebSocket
      terminal.onData((data) => {
        sendInput(data);
      });

      // Keyboard shortcuts
      terminal.attachCustomKeyEventHandler((event) => {
        // Cmd/Ctrl+C for copy (only when text is selected)
        if (
          (event.ctrlKey || event.metaKey) &&
          event.code === "KeyC" &&
          terminal.hasSelection()
        ) {
          return false; // Let browser handle copy
        }

        // Cmd/Ctrl+V for paste
        if ((event.ctrlKey || event.metaKey) && event.code === "KeyV") {
          return false; // Let browser handle paste
        }

        // Allow all other keys to be handled by terminal
        return true;
      });
    }

    // Initial fit - call once after terminal is ready
    const initialFit = () => {
      if (!fitAddonRef.current || !xtermRef.current || !terminalRef.current)
        return;

      try {
        fitAddonRef.current.fit();
        const dims = fitAddonRef.current.proposeDimensions();
        if (dims) {
          lastDimensionsRef.current = { cols: dims.cols, rows: dims.rows };
        }
      } catch (e) {
        console.warn("[Terminal] Initial fit failed:", e);
      }
    };

    // Call fit after a small delay to ensure DOM is ready
    const fitTimeout = setTimeout(initialFit, 50);

    // Cleanup on unmount
    return () => {
      clearTimeout(fitTimeout);

      // Save terminal state in context (don't dispose - we want persistence)
      if (xtermRef.current && fitAddonRef.current) {
        updateSession(sessionId, {
          terminal: xtermRef.current,
          fitAddon: fitAddonRef.current,
          containerElement: null,
        });
      }
    };
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, [sessionId, projectId]); // Only re-run if session/project changes

  // Handle connection
  useEffect(() => {
    if (isConnected && onConnect) {
      onConnect();
    } else if (!isConnected && onDisconnect) {
      onDisconnect();
    }
  }, [isConnected, onConnect, onDisconnect]);

  // Auto-connect on mount (with small delay to ensure terminal is ready)
  useEffect(() => {
    let timer: ReturnType<typeof setTimeout> | null = null;

    timer = setTimeout(() => {
      if (xtermRef.current && fitAddonRef.current) {
        const dims = fitAddonRef.current.proposeDimensions();
        if (dims) {
          connect(dims.cols, dims.rows);
        }
      }
    }, 100);

    return () => {
      if (timer) clearTimeout(timer);
      disconnect();
    };
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, []); // Only run once on mount

  return (
    <div
      ref={wrapperRef}
      className="h-full overflow-hidden relative bg-[#1e1e1e]"
    >
      <div
        ref={terminalRef}
        className="h-full w-full"
        style={{ outline: "none" }}
      />
    </div>
  );
}

import { useState } from 'react';
import { useProjects } from '../hooks/useProjects';
import { Terminal } from '../components/terminal/Terminal';
import { ShellControls } from '../components/terminal/ShellControls';
import { useShell } from '../contexts/ShellContext';
import {
  Select,
  SelectContent,
  SelectItem,
  SelectTrigger,
  SelectValue,
} from '../components/ui/select';
import { Card, CardContent, CardHeader, CardTitle } from '../components/ui/card';
import { Skeleton } from '../components/ui/skeleton';
import { Alert, AlertDescription } from '../components/ui/alert';
import { AlertCircle, Terminal as TerminalIcon } from 'lucide-react';

export default function Shell() {
  const { data: projects, isLoading, error } = useProjects();
  const [selectedProjectId, setSelectedProjectId] = useState<string>('');
  const { getSession } = useShell();

  const selectedProject = projects?.find((p) => p.id === selectedProjectId);
  const sessionId = `shell-${selectedProjectId}`;
  const session = getSession(sessionId);

  const handleRestart = () => {
    // Reload page to restart session
    window.location.reload();
  };

  // Loading state
  if (isLoading) {
    return (
      <div className="flex flex-col h-full">
        <div className="p-4 space-y-4">
          <h1 className="text-3xl font-bold">Shell</h1>
          <Skeleton className="h-10 w-64" />
        </div>
      </div>
    );
  }

  // Error state
  if (error) {
    return (
      <div className="flex flex-col h-full p-4">
        <h1 className="text-3xl font-bold mb-4">Shell</h1>
        <Alert variant="destructive">
          <AlertCircle className="h-4 w-4" />
          <AlertDescription>
            {error.message || 'Failed to load projects. Please try again.'}
          </AlertDescription>
        </Alert>
      </div>
    );
  }

  // Empty state - no projects
  if (!projects || projects.length === 0) {
    return (
      <div className="flex flex-col h-full p-4">
        <h1 className="text-3xl font-bold mb-4">Shell</h1>
        <Card>
          <CardContent className="flex flex-col items-center justify-center py-16 text-center">
            <TerminalIcon className="h-16 w-16 text-muted-foreground mb-4" />
            <h3 className="text-lg font-semibold mb-2">No projects found</h3>
            <p className="text-sm text-muted-foreground max-w-sm">
              Create a project first to use the interactive shell.
            </p>
          </CardContent>
        </Card>
      </div>
    );
  }

  // No project selected
  if (!selectedProjectId) {
    return (
      <div className="flex flex-col h-full">
        <div className="p-4 space-y-4">
          <h1 className="text-3xl font-bold">Shell</h1>
          <Card>
            <CardHeader>
              <CardTitle>Select a Project</CardTitle>
            </CardHeader>
            <CardContent>
              <Select value={selectedProjectId} onValueChange={setSelectedProjectId}>
                <SelectTrigger className="w-full max-w-md">
                  <SelectValue placeholder="Select a project..." />
                </SelectTrigger>
                <SelectContent>
                  {projects.map((project) => (
                    <SelectItem key={project.id} value={project.id}>
                      {project.name}
                    </SelectItem>
                  ))}
                </SelectContent>
              </Select>
              <p className="text-sm text-muted-foreground mt-2">
                Choose a project to start an interactive shell session
              </p>
            </CardContent>
          </Card>
        </div>
      </div>
    );
  }

  // Shell view with terminal
  return (
    <div className="flex flex-col h-full">
      {/* Header with project selector */}
      <div className="p-4 border-b">
        <div className="flex items-center gap-4">
          <h1 className="text-3xl font-bold">Shell</h1>
          <Select value={selectedProjectId} onValueChange={setSelectedProjectId}>
            <SelectTrigger className="w-64">
              <SelectValue />
            </SelectTrigger>
            <SelectContent>
              {projects.map((project) => (
                <SelectItem key={project.id} value={project.id}>
                  {project.name}
                </SelectItem>
              ))}
            </SelectContent>
          </Select>
        </div>
      </div>

      {/* Controls */}
      <ShellControls
        status={session?.status || 'disconnected'}
        projectName={selectedProject?.name}
        onRestart={handleRestart}
      />

      {/* Terminal */}
      <div className="flex-1 overflow-hidden">
        <Terminal sessionId={sessionId} projectId={selectedProjectId} />
      </div>
    </div>
  );
}

import { Button } from '../ui/button';
import type { ConnectionStatus } from '../../contexts/ShellContext';

interface ShellControlsProps {
  status: ConnectionStatus;
  projectName?: string;
  onConnect?: () => void;
  onDisconnect?: () => void;
  onRestart?: () => void;
}

export function ShellControls({
  status,
  projectName,
  onConnect,
  onDisconnect,
  onRestart,
}: ShellControlsProps) {
  const getStatusColor = () => {
    switch (status) {
      case 'connected':
        return 'bg-green-500';
      case 'connecting':
        return 'bg-yellow-500 animate-pulse';
      case 'disconnected':
        return 'bg-gray-500';
      case 'error':
        return 'bg-red-500';
      default:
        return 'bg-gray-500';
    }
  };

  const getStatusText = () => {
    switch (status) {
      case 'connected':
        return 'Connected';
      case 'connecting':
        return 'Connecting...';
      case 'disconnected':
        return 'Disconnected';
      case 'error':
        return 'Error';
      default:
        return 'Unknown';
    }
  };

  return (
    <div className="flex items-center justify-between p-4 bg-muted/30 border-b">
      <div className="flex items-center gap-4">
        {/* Status indicator */}
        <div className="flex items-center gap-2">
          <div className={`w-3 h-3 rounded-full ${getStatusColor()}`} />
          <span className="text-sm font-medium">{getStatusText()}</span>
        </div>

        {/* Project name */}
        {projectName && (
          <>
            <div className="h-4 w-px bg-border" />
            <span className="text-sm text-muted-foreground">
              Project: <span className="font-medium text-foreground">{projectName}</span>
            </span>
          </>
        )}
      </div>

      {/* Action buttons */}
      <div className="flex items-center gap-2">
        {status === 'disconnected' || status === 'error' ? (
          <Button
            size="sm"
            variant="default"
            onClick={onConnect}
            disabled={!onConnect}
          >
            Connect
          </Button>
        ) : (
          <Button
            size="sm"
            variant="outline"
            onClick={onDisconnect}
            disabled={!onDisconnect}
          >
            Disconnect
          </Button>
        )}

        <Button
          size="sm"
          variant="outline"
          onClick={onRestart}
          disabled={!onRestart || status === 'connecting'}
        >
          Restart
        </Button>
      </div>
    </div>
  );
}

import { Navigate, Outlet } from "react-router-dom";
import { useAuth } from "../contexts/AuthContext";
import { AppSidebar } from "@/components/app-sidebar";
import {
  Breadcrumb,
  BreadcrumbItem,
  BreadcrumbLink,
  BreadcrumbList,
  BreadcrumbPage,
  BreadcrumbSeparator,
} from "@/components/ui/breadcrumb";
import { Separator } from "@/components/ui/separator";
import {
  SidebarInset,
  SidebarProvider,
  SidebarTrigger,
} from "@/components/ui/sidebar";

function ProtectedLayout() {
  const { isAuthenticated } = useAuth();

  if (!isAuthenticated) {
    return <Navigate to="/login" replace />;
  }

  return (
    <SidebarProvider
      style={
        {
          "--sidebar-width": "350px",
        } as React.CSSProperties
      }
    >
      <AppSidebar />
      <SidebarInset>
        <div className="flex flex-1 flex-col">
          <Outlet />
        </div>
      </SidebarInset>
    </SidebarProvider>
  );
}

export default ProtectedLayout;

