    </Sidebar>
  )
}

import { BrowserRouter, Routes, Route, Navigate } from "react-router-dom";
import { AuthProvider } from "./contexts/AuthContext";
import { ShellProvider } from "./contexts/ShellContext";
import ProtectedLayout from "./layouts/ProtectedLayout";
import AuthLayout from "./layouts/AuthLayout";
import ProjectDetailLayout from "./layouts/ProjectDetailLayout";
import Dashboard from "./pages/Dashboard";
import Projects from "./pages/Projects";
import ProjectChat from "./pages/ProjectChat";
import ProjectShell from "./pages/ProjectShell";
import ProjectFiles from "./pages/ProjectFiles";
import AboutUs from "./pages/AboutUs";
import Login from "./pages/Login";
import Signup from "./pages/Signup";
import "./App.css";

function App() {
  return (
    <BrowserRouter>
      <AuthProvider>
        <ShellProvider>
          <Routes>
          {/* Auth routes */}
          <Route element={<AuthLayout />}>
            <Route path="/login" element={<Login />} />
            <Route path="/signup" element={<Signup />} />
          </Route>

          {/* Protected routes */}
          <Route element={<ProtectedLayout />}>
            <Route path="/" element={<Dashboard />} />
            <Route path="/projects" element={<Projects />} />

            {/* Project detail with nested routes */}
            <Route path="/projects/:id" element={<ProjectDetailLayout />}>
              <Route index element={<Navigate to="chat" replace />} />
              <Route path="chat" element={<ProjectChat />} />
              <Route path="shell" element={<ProjectShell />} />
              <Route path="files" element={<ProjectFiles />} />
            </Route>

            <Route path="/about" element={<AboutUs />} />
          </Route>
        </Routes>
        </ShellProvider>
      </AuthProvider>
    </BrowserRouter>
  );
}

export default App;

import { NavLink, Outlet, useNavigate, useParams } from "react-router-dom";
import { useProject } from "../hooks/useProjects";
import { Button } from "../components/ui/button";
import { Skeleton } from "../components/ui/skeleton";
import { AlertCircle, ArrowLeft, MessageSquare, Terminal as TerminalIcon, FileText } from "lucide-react";
import { Alert, AlertDescription } from "../components/ui/alert";

export default function ProjectDetailLayout() {
  const { id } = useParams<{ id: string }>();
  const navigate = useNavigate();
  const { data: project, isLoading, error } = useProject(id!);

  // Loading state
  if (isLoading) {
    return (
      <div className="flex flex-col h-full">
        <Skeleton className="h-12 w-full" />
        <div className="flex-1 p-4">
          <Skeleton className="h-full w-full" />
        </div>
      </div>
    );
  }

  // Error state
  if (error) {
    return (
      <div className="space-y-4 p-4">
        <Button variant="ghost" onClick={() => navigate("/projects")}>
          <ArrowLeft className="mr-2 h-4 w-4" />
          Back to Projects
        </Button>
        <Alert variant="destructive">
          <AlertCircle className="h-4 w-4" />
          <AlertDescription>
            {error.message || "Failed to load project. Please try again."}
          </AlertDescription>
        </Alert>
      </div>
    );
  }

  // Not found state
  if (!project) {
    return (
      <div className="space-y-4 p-4">
        <Button variant="ghost" onClick={() => navigate("/projects")}>
          <ArrowLeft className="mr-2 h-4 w-4" />
          Back to Projects
        </Button>
        <Alert>
          <AlertCircle className="h-4 w-4" />
          <AlertDescription>Project not found.</AlertDescription>
        </Alert>
      </div>
    );
  }

  return (
    <div className="flex flex-col h-full">
      {/* Header with project name and tab navigation */}
