'use client';

import React, { createContext, useContext, useState, type ReactNode } from 'react';
import { ChevronLeft, ChevronRight } from 'lucide-react';
import { Button } from '../ui/button';

interface BranchContextType {
  currentBranch: number;
  totalBranches: number;
  goToNext: () => void;
  goToPrevious: () => void;
}

const BranchContext = createContext<BranchContextType | undefined>(undefined);

const useBranchContext = () => {
  const context = useContext(BranchContext);
  if (!context) {
    throw new Error('Branch components must be used within a Branch component');
  }
  return context;
};

interface BranchProps {
  children: ReactNode;
  defaultBranch?: number;
}

export const Branch = ({ children, defaultBranch = 0 }: BranchProps) => {
  const [currentBranch, setCurrentBranch] = useState(defaultBranch);

  const childrenArray = React.Children.toArray(children);
  const branchMessages = childrenArray.find(
    (child) => React.isValidElement(child) && child.type === BranchMessages
  );

  const totalBranches = branchMessages && React.isValidElement(branchMessages)
    ? React.Children.count(branchMessages.props.children)
    : 0;

  const goToNext = () => {
    setCurrentBranch((prev) => Math.min(prev + 1, totalBranches - 1));
  };

  const goToPrevious = () => {
    setCurrentBranch((prev) => Math.max(prev - 1, 0));
  };

  return (
    <BranchContext.Provider value={{ currentBranch, totalBranches, goToNext, goToPrevious }}>
      <div className="space-y-2">
        {children}
      </div>
    </BranchContext.Provider>
  );
};

interface BranchMessagesProps {
  children: ReactNode;
}

export const BranchMessages = ({ children }: BranchMessagesProps) => {
  const { currentBranch } = useBranchContext();
  const childrenArray = React.Children.toArray(children);

  return <>{childrenArray[currentBranch]}</>;
};

interface BranchSelectorProps {
  children: ReactNode;
  from?: 'user' | 'assistant';
}

export const BranchSelector = ({ children, from }: BranchSelectorProps) => {
  const { totalBranches } = useBranchContext();

  if (totalBranches <= 1) {
    return null;
  }

  return (
    <div className={`flex items-center gap-2 text-sm ${from === 'assistant' ? 'justify-start' : 'justify-end'}`}>
      {children}
    </div>
  );
};

export const BranchPrevious = () => {
  const { currentBranch, goToPrevious } = useBranchContext();

  return (
    <Button
      variant="ghost"
      size="sm"
      onClick={goToPrevious}
      disabled={currentBranch === 0}
      className="h-7 w-7 p-0"
    >
      <ChevronLeft className="h-4 w-4" />
    </Button>
  );
};

export const BranchNext = () => {
  const { currentBranch, totalBranches, goToNext } = useBranchContext();

  return (
    <Button
      variant="ghost"
      size="sm"
      onClick={goToNext}
      disabled={currentBranch === totalBranches - 1}
      className="h-7 w-7 p-0"
    >
      <ChevronRight className="h-4 w-4" />
    </Button>
  );
};

export const BranchPage = () => {
  const { currentBranch, totalBranches } = useBranchContext();

  return (
    <span className="text-xs text-muted-foreground">
      {currentBranch + 1} / {totalBranches}
    </span>
  );
};

'use client';

import React, { useRef, useEffect, useState, type ReactNode } from 'react';
import { ChevronDown } from 'lucide-react';
import { Button } from '../ui/button';

interface ConversationProps {
  children: ReactNode;
}

export const Conversation = ({ children }: ConversationProps) => {
  return (
    <div className="relative flex-1 overflow-hidden">
      {children}
    </div>
  );
};

interface ConversationContentProps {
  children: ReactNode;
}

export const ConversationContent = ({ children }: ConversationContentProps) => {
  const scrollRef = useRef<HTMLDivElement>(null);

  useEffect(() => {
    // Auto-scroll to bottom when new content is added
    if (scrollRef.current) {
      scrollRef.current.scrollTop = scrollRef.current.scrollHeight;
    }
  }, [children]);

  return (
    <div
      ref={scrollRef}
      className="h-full overflow-y-auto scroll-smooth px-4 py-6"
      id="conversation-content"
    >
      <div className="mx-auto max-w-3xl space-y-6">
        {children}
      </div>
    </div>
  );
};

export const ConversationScrollButton = () => {
  const [showButton, setShowButton] = useState(false);

  useEffect(() => {
    const container = document.getElementById('conversation-content');
    if (!container) return;

    const handleScroll = () => {
      const { scrollTop, scrollHeight, clientHeight } = container;
      const isNearBottom = scrollHeight - scrollTop - clientHeight < 100;
      setShowButton(!isNearBottom);
    };

    container.addEventListener('scroll', handleScroll);
    return () => container.removeEventListener('scroll', handleScroll);
  }, []);

  const scrollToBottom = () => {
    const container = document.getElementById('conversation-content');
    if (container) {
      container.scrollTo({
        top: container.scrollHeight,
        behavior: 'smooth',
      });
    }
  };

  if (!showButton) {
    return null;
  }

  return (
    <div className="absolute bottom-4 left-1/2 -translate-x-1/2">
      <Button
        onClick={scrollToBottom}
        size="sm"
        variant="secondary"
        className="rounded-full shadow-lg"
      >
        <ChevronDown className="h-4 w-4" />
        <span>Scroll to bottom</span>
      </Button>
    </div>
  );
};

'use client';

import React, { type ReactNode } from 'react';
import { Avatar, AvatarFallback, AvatarImage } from '../ui/avatar';

interface MessageProps {
  children: ReactNode;
  from: 'user' | 'assistant';
}

export const Message = ({ children, from }: MessageProps) => {
  return (
    <div className={`flex gap-4 ${from === 'user' ? 'flex-row-reverse' : 'flex-row'}`}>
      {children}
    </div>
  );
};

interface MessageContentProps {
  children: ReactNode;
}

export const MessageContent = ({ children }: MessageContentProps) => {
  return (
    <div className="flex-1 space-y-2">
      {children}
    </div>
  );
};

interface MessageAvatarProps {
  src?: string;
  name: string;
}

export const MessageAvatar = ({ src, name }: MessageAvatarProps) => {
  const initials = name
    .split(' ')
    .map((n) => n[0])
    .join('')
    .toUpperCase()
    .slice(0, 2);

  return (
    <Avatar className="h-8 w-8 shrink-0">
      <AvatarImage src={src} alt={name} />
      <AvatarFallback>{initials}</AvatarFallback>
    </Avatar>
  );
};

