  PromptInputTextarea,
  PromptInputFooter,
  PromptInputTools,
  PromptInputSubmit,
} from "../../components/ai-elements/prompt-input";

export default function ProjectChat() {
  const { id } = useParams<{ id: string }>();
  const [inputValue, setInputValue] = useState("");

  // Handle message submission
  const handleSubmit = async (message: { text?: string; files?: any[] }) => {
    if (!message.text?.trim()) return;

    // TODO: Implement actual message sending via WebSocket/API
    // This is a placeholder for future implementation
    console.log("Sending message:", message);
    setInputValue("");
  };

  return (
    <div className="flex flex-col h-full relative">
      <ChatInterface projectId={id!} />

      {/* Floating Prompt Input */}
      <div className="absolute bottom-0 left-0 right-0 border-t bg-background/95 backdrop-blur supports-backdrop-filter:bg-background/60 z-10">
        <div className="mx-auto max-w-3xl px-4 py-4">
          <PromptInput onSubmit={handleSubmit}>
            <PromptInputBody>
              <PromptInputTextarea
                onChange={(e) => setInputValue(e.target.value)}
                value={inputValue}
                placeholder="Send a message..."
              />
            </PromptInputBody>
            <PromptInputFooter>
              <PromptInputTools>
                <PromptInputSubmit disabled={!inputValue.trim()} />
              </PromptInputTools>
            </PromptInputFooter>
          </PromptInput>
        </div>
      </div>
    </div>
  );
}

import { NavLink, Outlet, useNavigate, useParams } from "react-router-dom";
import { useProject } from "../hooks/useProjects";
import { Button } from "../components/ui/button";
import { Skeleton } from "../components/ui/skeleton";
import {
  AlertCircle,
  ArrowLeft,
  MessageSquare,
  Terminal as TerminalIcon,
  FileText,
} from "lucide-react";
import { Alert, AlertDescription } from "../components/ui/alert";

export default function ProjectDetailLayout() {
  const { id } = useParams<{ id: string }>();
  const navigate = useNavigate();
  const { data: project, isLoading, error } = useProject(id!);

  // Loading state
  if (isLoading) {
    return (
      <div className="flex flex-col h-full">
        <Skeleton className="h-12 w-full" />
        <div className="flex-1 p-4">
          <Skeleton className="h-full w-full" />
        </div>
      </div>
    );
  }

  // Error state
  if (error) {
    return (
      <div className="space-y-4 p-4">
        <Button variant="ghost" onClick={() => navigate("/projects")}>
          <ArrowLeft className="mr-2 h-4 w-4" />
          Back to Projects
        </Button>
        <Alert variant="destructive">
          <AlertCircle className="h-4 w-4" />
          <AlertDescription>
            {error.message || "Failed to load project. Please try again."}
          </AlertDescription>
        </Alert>
      </div>
    );
  }

  // Not found state
  if (!project) {
    return (
      <div className="space-y-4 p-4">
        <Button variant="ghost" onClick={() => navigate("/projects")}>
          <ArrowLeft className="mr-2 h-4 w-4" />
          Back to Projects
        </Button>
        <Alert>
          <AlertCircle className="h-4 w-4" />
          <AlertDescription>Project not found.</AlertDescription>
        </Alert>
      </div>
    );
  }

  return (
    <div className="flex flex-col h-full">
      {/* Header with project name and tab navigation */}
      <div className="flex items-center justify-between border-b px-6 py-4">
        <div className="flex flex-col gap-1">
          <div className="text-sm font-semibold text-muted-foreground">
            Project
          </div>
          <div className="text-base font-medium">{project.name}</div>
        </div>
        <nav className="flex gap-2">
          <NavLink
            to={`/projects/${id}/chat`}
            className={({ isActive }) =>
              `flex items-center gap-2 px-4 py-2 rounded-md text-sm font-medium transition-colors ${
                isActive
                  ? "bg-secondary text-secondary-foreground"
                  : "text-muted-foreground hover:bg-secondary/50"
              }`
            }
          >
            <MessageSquare className="h-4 w-4" />
            Chat
          </NavLink>
          <NavLink
            to={`/projects/${id}/shell`}
            className={({ isActive }) =>
              `flex items-center gap-2 px-4 py-2 rounded-md text-sm font-medium transition-colors ${
                isActive
                  ? "bg-secondary text-secondary-foreground"
                  : "text-muted-foreground hover:bg-secondary/50"
              }`
            }
          >
            <TerminalIcon className="h-4 w-4" />
            Shell
          </NavLink>
          <NavLink
            to={`/projects/${id}/files`}
            className={({ isActive }) =>
              `flex items-center gap-2 px-4 py-2 rounded-md text-sm font-medium transition-colors ${
                isActive
                  ? "bg-secondary text-secondary-foreground"
                  : "text-muted-foreground hover:bg-secondary/50"
              }`
            }
          >
            <FileText className="h-4 w-4" />
            Files
          </NavLink>
        </nav>
      </div>

      {/* Nested route content */}
      <div className="flex-1 overflow-hidden">
        <Outlet />
      </div>
    </div>
  );
}

/**
 * Main chat interface component
 * Displays conversation history with auto-scroll
 */

import { useEffect, useRef } from "react";
import { MessageCircle, AlertCircle } from "lucide-react";
import { useClaudeSession } from "../../hooks/useClaudeSession";
import { MessageRenderer } from "./MessageRenderer";
import { ChatSkeleton } from "./ChatSkeleton";
import { Alert, AlertDescription } from "../ui/alert";

interface ChatInterfaceProps {
  projectId: string;
  sessionFile?: string;
}

/**
 * Chat interface component for displaying Claude conversations
 *
 * @future WebSocket Integration
 * To enable real-time streaming:
 * 1. Replace useClaudeSession with useAgentWebSocket hook
 * 2. Connect to WebSocket endpoint: `/ws/session/${sessionId}`
 * 3. Listen for StreamEvent messages from agent-cli-sdk
 * 4. Update messages array as events arrive (message_start, content_block_*, message_stop)
 * 5. Enable bi-directional communication (send user messages via WebSocket)
 *
 * The message types and ContentBlock structure are already aligned with
 * agent-cli-sdk's event format, so no type mapping is needed.
 */
export function ChatInterface({ projectId, sessionFile }: ChatInterfaceProps) {
  const { messages, toolResults, isLoading, error } =
    useClaudeSession(sessionFile);
  const messagesEndRef = useRef<HTMLDivElement>(null);
  const containerRef = useRef<HTMLDivElement>(null);
  const previousScrollHeight = useRef(0);

  // Auto-scroll to bottom when messages change
  useEffect(() => {
    if (!containerRef.current || !messagesEndRef.current) return;

    const container = containerRef.current;
    const isNearBottom =
      container.scrollHeight - container.scrollTop - container.clientHeight <
      200;

    // Only auto-scroll if user is already near the bottom
    // This preserves manual scroll position
    if (isNearBottom || previousScrollHeight.current === 0) {
      messagesEndRef.current.scrollIntoView({
        behavior: "smooth",
        block: "end",
      });
    }

    previousScrollHeight.current = container.scrollHeight;
  }, [messages]);

  // Loading state
  if (isLoading) {
    return <ChatSkeleton />;
  }

  // Error state
  if (error) {
    return (
      <div className="flex items-center justify-center h-full p-4">
        <Alert variant="destructive" className="max-w-md">
          <AlertCircle className="h-4 w-4" />
          <AlertDescription>
            <div className="font-medium">Failed to load conversation</div>
            <div className="text-sm mt-1">{error.message}</div>
          </AlertDescription>
        </Alert>
      </div>
    );
  }

  // Empty state
  if (messages.length === 0) {
    return (
      <div className="flex flex-col items-center justify-center h-full text-muted-foreground p-4">
        <MessageCircle className="h-12 w-12 mb-4 opacity-50" />
        <p className="text-lg font-medium">No messages yet</p>
        <p className="text-sm mt-1">Start a conversation with Claude</p>
      </div>
    );
  }

  // Messages list
  return (
    <div
      ref={containerRef}
      className="flex-1 overflow-y-auto p-4 pb-32 space-y-1"
      data-project-id={projectId}
    >
      {messages.map((message) => (
        <MessageRenderer
          key={message.id}
          message={message}
          toolResults={toolResults}
        />
      ))}
      <div ref={messagesEndRef} />
    </div>
  );
}

