/**
 * Router for content block renderers
 * Dispatches to appropriate renderer based on block type
 */

import type { ContentBlock } from '../../shared/types/chat';
import { TextBlock } from './TextBlock';
import { ThinkingBlock } from './ThinkingBlock';
import { ToolUseBlock } from './ToolUseBlock';

interface ContentBlockRendererProps {
  block: ContentBlock;
  toolResults?: Map<string, { content: string; is_error?: boolean }>;
  className?: string;
}

export function ContentBlockRenderer({ block, toolResults, className = '' }: ContentBlockRendererProps) {
  switch (block.type) {
    case 'text':
      return <TextBlock text={block.text} className={className} />;

    case 'thinking':
      return <ThinkingBlock thinking={block.thinking} className={className} />;

    case 'tool_use':
      // Look up the result for this tool use
      const result = toolResults?.get(block.id);
      return (
        <ToolUseBlock
          id={block.id}
          name={block.name}
          input={block.input}
          result={result}
          className={className}
        />
      );

    case 'tool_result':
      // Tool results are handled inline with tool_use blocks
      // We don't render them separately
      return null;

    default:
      // Unknown block type
      console.warn('Unknown content block type:', (block as any).type);
      return (
        <div className="text-sm text-muted-foreground italic">
          Unknown content block type: {(block as any).type}
        </div>
      );
  }
}

/**
 * Text content block with Markdown rendering
 */

import ReactMarkdown from 'react-markdown';
import remarkGfm from 'remark-gfm';

interface TextBlockProps {
  text: string;
  className?: string;
}

export function TextBlock({ text, className = '' }: TextBlockProps) {
  return (
    <div className={`prose prose-sm dark:prose-invert max-w-none ${className}`}>
      <ReactMarkdown
        remarkPlugins={[remarkGfm]}
        components={{
          // Custom heading renderers - compact and minimal
          h1({ children, ...props }) {
            return (
              <h1 className="text-lg font-semibold mb-2 mt-3" {...props}>
                {children}
              </h1>
            );
          },
          h2({ children, ...props }) {
            return (
              <h2 className="text-base font-semibold mb-1.5 mt-3" {...props}>
                {children}
              </h2>
            );
          },
          h3({ children, ...props }) {
            return (
              <h3 className="text-sm font-semibold mb-1 mt-2" {...props}>
                {children}
              </h3>
            );
          },
          h4({ children, ...props }) {
            return (
              <h4 className="text-sm font-medium mb-1 mt-2" {...props}>
                {children}
              </h4>
            );
          },
          h5({ children, ...props }) {
            return (
              <h5 className="text-sm font-medium mb-1 mt-2" {...props}>
                {children}
              </h5>
            );
          },
          h6({ children, ...props }) {
            return (
              <h6 className="text-sm font-medium mb-1 mt-2" {...props}>
                {children}
              </h6>
            );
          },
          // Custom code inline rendering
          code({ className, children, ...props }) {
            const match = /language-(\w+)/.exec(className || '');
            const isInline = !match;

            if (isInline) {
              return (
                <code
                  className="px-1.5 py-0.5 rounded bg-muted font-mono text-sm font-normal"
                  {...props}
                >
                  {children}
                </code>
              );
            }

            // Block code will be handled by parent component or CodeBlock
            return (
              <code className={className} {...props}>
                {children}
              </code>
            );
          },
          // Custom link rendering
          a({ href, children, ...props }) {
            return (
              <a
                href={href}
                target="_blank"
                rel="noopener noreferrer"
                className="text-primary hover:underline"
                {...props}
              >
                {children}
              </a>
            );
          },
          // Custom blockquote rendering
          blockquote({ children, ...props }) {
            return (
              <blockquote
                className="border-l-4 border-muted-foreground/20 pl-4 italic text-muted-foreground"
                {...props}
              >
                {children}
              </blockquote>
            );
          }
        }}
      >
        {text}
      </ReactMarkdown>
    </div>
  );
}

