     1→/* eslint-disable @typescript-eslint/no-explicit-any, @typescript-eslint/no-unused-vars */
     2→/**
     3→ * Parse JSONL session data into ChatMessage array
     4→ * Supports multiple formats via adapter system
     5→ */
     6→
     7→import type {
     8→  ChatMessage,
     9→  ContentBlock,
    10→  ToolUseBlock,
    11→  ToolResultBlock,
    12→  ClaudeSessionRow,
    13→  ClaudeSessionData,
    14→} from "@/shared/types/chat";
    15→import {
    16→  parseJSONLWithAdapter,
    17→  extractToolResultsWithAdapter,
    18→} from "./sessionAdapters";
    19→
    20→interface RawStreamEvent {
    21→  type: string;
    22→  timestamp?: number;
    23→  [key: string]: unknown;
    24→}
    25→
    26→/**
    27→ * Parse JSONL content into an array of ChatMessage objects
    28→ * Auto-detects format (Claude CLI or streaming) and uses appropriate adapter
    29→ *
    30→ * @param jsonlContent - Raw JSONL string content
    31→ * @returns Array of parsed ChatMessage objects
    32→ */
    33→export function parseJSONLSession(jsonlContent: string): ChatMessage[] {
    34→  // Try adapter-based parsing first (handles Claude CLI format)
    35→  const adapterResult = parseJSONLWithAdapter(jsonlContent);
    36→  if (adapterResult.length > 0) {
    37→    return adapterResult;
    38→  }
    39→
    40→  // Fall back to streaming format parsing
    41→  if (!jsonlContent || jsonlContent.trim() === "") {
    42→    return [];
    43→  }
    44→
    45→  const lines = jsonlContent.split("\n").filter((line) => line.trim() !== "");
    46→  const messages: ChatMessage[] = [];
    47→  const toolResults = new Map<string, ToolResultBlock>();
    48→
    49→  // Track current message being built
    50→  let currentMessage: ChatMessage | null = null;
    51→
    52→  for (const line of lines) {
    53→    try {
    54→      const event = JSON.parse(line) as RawStreamEvent;
    55→
    56→      // Handle message_start event
    57→      if (event.type === "message_start") {
    58→        const message = event.message as any;
    59→        currentMessage = {
    60→          id: message?.id || crypto.randomUUID(),
    61→          role: "assistant",
    62→          content: [],
    63→          timestamp: event.timestamp || Date.now(),
    64→          isStreaming: false,
    65→        };
    66→        continue;
    67→      }
    68→
    69→      // Handle content_block_start
    70→      if (event.type === "content_block_start") {
    71→        const block = (event as any).content_block;
    72→        if (!currentMessage) continue;
    73→
    74→        if (block.type === "text") {
    75→          currentMessage.content.push({
    76→            type: "text",
    77→            text: block.text || "",
    78→          } as ContentBlock);
    79→        } else if (block.type === "thinking") {
    80→          currentMessage.content.push({
    81→            type: "thinking",
    82→            thinking: block.thinking || "",
    83→          } as ContentBlock);
    84→        } else if (block.type === "tool_use") {
    85→          currentMessage.content.push({
    86→            type: "tool_use",
    87→            id: block.id,
    88→            name: block.name,
    89→            input: block.input || {},
    90→          } as ToolUseBlock);
    91→        }
    92→        continue;
    93→      }
    94→
    95→      // Handle content_block_delta for streaming content
    96→      if (event.type === "content_block_delta") {
    97→        if (!currentMessage) continue;
    98→        const delta = (event as any).delta;
    99→        const index = (event as any).index || 0;
   100→
   101→        if (delta.type === "text_delta" && currentMessage.content[index]) {
   102→          const textBlock = currentMessage.content[index] as any;
   103→          if (textBlock.type === "text") {
   104→            textBlock.text += delta.text || "";
   105→          }
   106→        } else if (
   107→          delta.type === "thinking_delta" &&
   108→          currentMessage.content[index]
   109→        ) {
