     2→/**
     3→ * Hook to load and parse Claude session JSONL data with WebSocket streaming support
     4→ */
     5→
     6→import { useState, useEffect } from 'react';
     7→import type { ChatMessage } from "@/shared/types/chat";
     8→import { parseJSONLSession, extractToolResults } from "@/client/utils/parseClaudeSession";
     9→import { normalizeMessage } from "@/client/utils/sessionAdapters";
    10→import { useChatWebSocket } from './useChatWebSocket';
    11→import { useAuth } from "@/client/contexts/AuthContext";
    12→
    13→interface UseClaudeSessionOptions {
    14→  sessionId: string;
    15→  projectId: string;
    16→  enableWebSocket?: boolean;
    17→}
    18→
    19→interface UseClaudeSessionReturn {
    20→  messages: ChatMessage[];
    21→  toolResults: Map<string, { content: string; is_error?: boolean }>;
    22→  isLoading: boolean;
    23→  error: Error | null;
    24→  isConnected?: boolean;
    25→  isStreaming?: boolean;
    26→  sendMessage?: (options: { message: string; images?: string[]; config?: Record<string, any> }) => void;
    27→  reconnect?: () => void;
    28→}
    29→
    30→/**
    31→ * Load and parse a Claude session from JSONL file with optional WebSocket streaming
    32→ *
    33→ * @param options - Session options including sessionId, projectId, and enableWebSocket
    34→ * @returns Parsed messages, tool results, loading state, error, and WebSocket controls
    35→ *
    36→ * @example
    37→ * ```tsx
    38→ * const { messages, isLoading, sendMessage } = useClaudeSession({
    39→ *   sessionId: 'abc-123',
    40→ *   projectId: 'project-1',
    41→ *   enableWebSocket: true
    42→ * });
    43→ * ```
    44→ */
    45→export function useClaudeSession(
    46→  options: UseClaudeSessionOptions
    47→): UseClaudeSessionReturn {
    48→  const { sessionId, projectId, enableWebSocket = false } = options;
    49→  const { handleInvalidToken } = useAuth();
    50→
    51→  const [messages, setMessages] = useState<ChatMessage[]>([]);
    52→  const [toolResults, setToolResults] = useState<Map<string, { content: string; is_error?: boolean }>>(
    53→    new Map()
    54→  );
    55→  const [isLoading, setIsLoading] = useState(true);
    56→  const [error, setError] = useState<Error | null>(null);
    57→
    58→  // Always call useChatWebSocket (Rules of Hooks - must be called unconditionally)
    59→  // We'll conditionally use its return values based on enableWebSocket flag
    60→  const webSocket = useChatWebSocket(sessionId, projectId);
    61→
    62→  // Load initial messages from JSONL file
    63→  useEffect(() => {
    64→    let cancelled = false;
    65→
    66→    const loadSessionMessages = async () => {
    67→      try {
    68→        setIsLoading(true);
    69→        setError(null);
    70→
    71→        const token = localStorage.getItem('token');
    72→        const response = await fetch(`/api/projects/${projectId}/sessions/${sessionId}/messages`, {
    73→          headers: {
    74→            'Authorization': `Bearer ${token}`,
    75→          },
    76→        });
    77→
    78→        if (!response.ok) {
    79→          // Handle 401 Unauthorized - invalid or missing token
    80→          if (response.status === 401) {
    81→            handleInvalidToken();
    82→            throw new Error('Session expired');
    83→          }
    84→          // Handle 404 - session doesn't exist yet (new session)
    85→          if (response.status === 404) {
    86→            console.log('Session not found - this is a new session');
    87→            if (cancelled) return;
    88→            setMessages([]);
    89→            setToolResults(new Map());
    90→            if (enableWebSocket && webSocket.setMessages) {
    91→              webSocket.setMessages([]);
    92→            }
    93→            return;
    94→          }
    95→          throw new Error(`Failed to load session messages: ${response.statusText}`);
    96→        }
    97→
    98→        const data = await response.json();
    99→        const messagesArray = data.data || [];
   100→
   101→        if (cancelled) return;
   102→
   103→        // API already returns parsed messages array - normalize them
   104→        const parsedMessages: ChatMessage[] = messagesArray.map((msg: any) => normalizeMessage(msg));
   105→
   106→        // Extract tool results from messages
   107→        const toolResultsMap = new Map<string, { content: string; is_error?: boolean }>();
   108→        messagesArray.forEach((msg: any) => {
   109→          if (msg.role === 'assistant' && Array.isArray(msg.content)) {
