     1→/* eslint-disable @typescript-eslint/no-explicit-any */
     2→import { Prisma } from '@prisma/client';
     3→import { prisma } from '@/shared/prisma';
     4→import fs from 'fs/promises';
     5→import path from 'path';
     6→import os from 'os';
     7→import type {
     8→  AgentSessionMetadata,
     9→  SessionResponse,
    10→  SyncSessionsResponse,
    11→} from '@/shared/types/agent-session.types';
    12→
    13→/**
    14→ * Agent Session Service
    15→ * Handles session management, JSONL file parsing, and sync operations
    16→ */
    17→export class AgentSessionService {
    18→  /**
    19→   * Get the Claude projects directory path
    20→   */
    21→  private getClaudeProjectsDir(): string {
    22→    return path.join(os.homedir(), '.claude', 'projects');
    23→  }
    24→
    25→  /**
    26→   * Encode project path for filesystem storage
    27→   * Replaces `/` with `-` (including leading slash)
    28→   * @param projectPath - Full project path from Project.path
    29→   * @returns Encoded path for filesystem
    30→   * @example "/Users/john/myproject" -> "-Users-john-myproject"
    31→   */
    32→  private encodeProjectPath(projectPath: string): string {
    33→    // Replace all slashes with dashes (Claude CLI keeps the leading dash)
    34→    return projectPath.replace(/\//g, '-');
    35→  }
    36→
    37→  /**
    38→   * Get the session JSONL file path
    39→   * @param projectPath - Project path from database
    40→   * @param sessionId - Session UUID
    41→   * @returns Full path to session JSONL file
    42→   */
    43→  getSessionFilePath(projectPath: string, sessionId: string): string {
    44→    const encodedPath = this.encodeProjectPath(projectPath);
    45→    return path.join(
    46→      this.getClaudeProjectsDir(),
    47→      encodedPath,
    48→      `${sessionId}.jsonl`
    49→    );
    50→  }
    51→
    52→  /**
    53→   * Parse a JSONL file to extract session metadata
    54→   * @param filePath - Path to JSONL file
    55→   * @returns Session metadata extracted from file
    56→   */
    57→  async parseJSONLFile(
    58→    filePath: string
    59→  ): Promise<AgentSessionMetadata> {
    60→    try {
    61→      const content = await fs.readFile(filePath, 'utf-8');
    62→      const lines = content.trim().split('\n').filter(Boolean);
    63→
    64→      let messageCount = 0;
    65→      let totalTokens = 0;
    66→      let lastMessageAt = new Date().toISOString();
    67→      let firstMessagePreview = '';
    68→
    69→      for (const line of lines) {
    70→        try {
    71→          const entry = JSON.parse(line);
    72→
    73→          // Count messages (check both 'type' for Claude CLI format and 'role' for API format)
    74→          const isMessage = entry.type === 'user' || entry.type === 'assistant' || entry.role === 'user' || entry.role === 'assistant';
    75→          if (isMessage) {
    76→            messageCount++;
    77→          }
    78→
    79→          // Extract first user message for preview
    80→          const isUserMessage = entry.type === 'user' || entry.role === 'user';
    81→          if (isUserMessage && !firstMessagePreview) {
    82→            // Handle both Claude CLI format (message.content) and API format (content)
    83→            const content = entry.message?.content ?? entry.content;
    84→            const text =
    85→              typeof content === 'string'
    86→                ? content
    87→                : Array.isArray(content)
    88→                  ? content
    89→                      .filter((c: any) => c.type === 'text')
    90→                      .map((c: any) => c.text)
    91→                      .join(' ')
    92→                  : '';
    93→            firstMessagePreview = text.substring(0, 100);
    94→          }
    95→
    96→          // Sum token usage from assistant messages
    97→          const isAssistantMessage = entry.type === 'assistant' || entry.role === 'assistant';
    98→          if (isAssistantMessage && entry.usage) {
    99→            totalTokens +=
   100→              (entry.usage.input_tokens || 0) +
   101→              (entry.usage.cache_creation_input_tokens || 0) +
   102→              (entry.usage.cache_read_input_tokens || 0) +
   103→              (entry.usage.output_tokens || 0);
   104→          }
   105→
   106→          // Track the timestamp from the latest message
   107→          if (entry.timestamp) {
   108→            lastMessageAt = entry.timestamp;
   109→          }
   110→        } catch (err) {
   111→          // Skip malformed lines
   112→          console.warn(`Failed to parse JSONL line: ${err}`);
   113→        }
   114→      }
   115→
   116→      return {
   117→        messageCount,
   118→        totalTokens,
   119→        lastMessageAt,
   120→        firstMessagePreview: firstMessagePreview || '(No messages)',
   121→      };
   122→    } catch (error) {
   123→      // Return default metadata if file can't be read
   124→      throw new Error(`Failed to parse JSONL file: ${error}`);
   125→    }
   126→  }
