import { useEffect, useRef, useCallback } from "react";
import { Terminal as XTerm } from "@xterm/xterm";
import { FitAddon } from "@xterm/addon-fit";
import { ClipboardAddon } from "@xterm/addon-clipboard";
import { useShell } from "../../contexts/ShellContext";
import { useShellWebSocket } from "../../hooks/useShellWebSocket";
import "@xterm/xterm/css/xterm.css";

interface TerminalProps {
  sessionId: string;
  projectId: string;
  onConnect?: () => void;
  onDisconnect?: () => void;
}

export function Terminal({
  sessionId,
  projectId,
  onConnect,
  onDisconnect,
}: TerminalProps) {
  // ============================================================================
  // Refs and State
  // ============================================================================
  const { addSession } = useShell();
  const terminalRef = useRef<HTMLDivElement>(null);
  const xtermRef = useRef<XTerm | null>(null);
  const fitAddonRef = useRef<FitAddon | null>(null);

  // ============================================================================
  // WebSocket Integration
  // ============================================================================
  // Stable callbacks for WebSocket
  const handleOutput = useCallback((data: string) => {
    xtermRef.current?.write(data);
  }, []);

  const handleExit = useCallback((exitCode: number) => {
    xtermRef.current?.writeln(
      `\r\n\x1b[1;33m[Process exited with code ${exitCode}]\x1b[0m\r\n`
    );
  }, []);

  // WebSocket connection - handles shell I/O and reconnection
  const { isConnected, connect, disconnect, sendInput } =
    useShellWebSocket({
      sessionId,
      projectId,
      enabled: true,
      onOutput: handleOutput,
      onExit: handleExit,
    });

  // ============================================================================
  // Terminal Initialization
  // ============================================================================
  // Create fresh terminal instance on each mount (no caching/persistence).
  // This follows react-xtermjs pattern: simple, focused, easy to reason about.
  useEffect(() => {
    if (!terminalRef.current) return;

    // Create new terminal instance
    const terminal = new XTerm({
      cursorBlink: true,
      fontSize: 14,
      fontFamily: '"Cascadia Code", "Fira Code", "Courier New", monospace',
      scrollback: 10000,
      theme: {
        background: "#1e1e1e",
        foreground: "#d4d4d4",
        cursor: "#ffffff",
        cursorAccent: "#000000",
        selectionBackground: "#264f78",
        // ANSI colors (16-color palette)
        black: "#000000",
        red: "#cd3131",
        green: "#0dbc79",
        yellow: "#e5e510",
        blue: "#2472c8",
        magenta: "#bc3fbc",
        cyan: "#11a8cd",
        white: "#e5e5e5",
        brightBlack: "#666666",
        brightRed: "#f14c4c",
        brightGreen: "#23d18b",
        brightYellow: "#f5f543",
        brightBlue: "#3b8eea",
        brightMagenta: "#d670d6",
        brightCyan: "#29b8db",
        brightWhite: "#ffffff",
      },
    });

    xtermRef.current = terminal;

