  try {
    const dims = fitAddon.proposeDimensions();
    if (dims) {
      connect(dims.cols, dims.rows);
    }
  } catch (error) {
    console.warn('[Terminal] Failed to get dimensions for WebSocket connection:', error);
    // Try again after a delay
    setTimeout(() => {
      try {
        const dims = fitAddon.proposeDimensions();
        if (dims) {
          connect(dims.cols, dims.rows);
        }
      } catch (retryError) {
