import { useState, useEffect, useCallback, useRef } from 'react';
import { useChatContext } from '../contexts/ChatContext';
import type { AgentSessionMetadata } from '../../shared/types';

export interface ChatMessage {
  role: 'user' | 'assistant';
  content: string;
  timestamp?: string;
  images?: string[];
  isStreaming?: boolean;
}

interface WebSocketMessage {
  type: 'stream_event' | 'message_complete' | 'error';
  sessionId?: string;
  event?: {
    type: string;
    data?: any;
  };
  metadata?: AgentSessionMetadata;
  message?: string;
}

interface SendMessageOptions {
  message: string;
  images?: string[];
  config?: Record<string, any>;
}

export function useChatWebSocket(sessionId: string, projectId: string) {
  const [messages, setMessages] = useState<ChatMessage[]>([]);
  const [isConnected, setIsConnected] = useState(false);
  const [error, setError] = useState<string | null>(null);
  const [isStreaming, setIsStreaming] = useState(false);

  const wsRef = useRef<WebSocket | null>(null);
  const reconnectTimeoutRef = useRef<NodeJS.Timeout | null>(null);
  const reconnectAttemptsRef = useRef(0);
  const maxReconnectAttempts = 5;

  const { setWebSocketConnection, removeWebSocketConnection, updateSessionMetadata } = useChatContext();

  const connect = useCallback(() => {
    if (!sessionId || !projectId) return;

    const token = localStorage.getItem('token');
    if (!token) {
      setError('Authentication token not found');
      return;
    }

    // Close existing connection if any
    if (wsRef.current) {
      wsRef.current.close();
    }

    const protocol = window.location.protocol === 'https:' ? 'wss:' : 'ws:';
    const wsUrl = `${protocol}//${window.location.host}/ws/chat/${sessionId}?token=${token}`;

    const ws = new WebSocket(wsUrl);

    ws.onopen = () => {
      console.log('WebSocket connected for session:', sessionId);
      setIsConnected(true);
      setError(null);
      reconnectAttemptsRef.current = 0;
      wsRef.current = ws;
      setWebSocketConnection(sessionId, ws);
    };

    ws.onmessage = (event) => {
      try {
        const data: WebSocketMessage = JSON.parse(event.data);
        console.log('[WebSocket Client] Received message:', data.type, data);

        switch (data.type) {
          case 'stream_event':
            console.log('[WebSocket Client] Stream event:', data.event);
            if (data.event) {
              handleStreamEvent(data.event);
            }
            break;

          case 'stream_output':
            console.log('[WebSocket Client] Stream output:', data);
            // Handle stream output
            break;

          case 'message_complete':
            console.log('[WebSocket Client] Message complete:', data);
            setIsStreaming(false);
            if (data.metadata) {
              updateSessionMetadata(sessionId, data.metadata);
            }
            break;

          case 'error':
            console.error('[WebSocket Client] Error:', data.message);
            setError(data.message || 'An error occurred');
            setIsStreaming(false);
            break;

          default:
            console.log('[WebSocket Client] Unknown message type:', data.type);
        }
      } catch (err) {
        console.error('Error parsing WebSocket message:', err);
      }
    };

    ws.onerror = (event) => {
      console.error('WebSocket error:', event);
      setError('Connection error occurred');
    };

    ws.onclose = () => {
      console.log('WebSocket closed for session:', sessionId);
      setIsConnected(false);
      wsRef.current = null;
      removeWebSocketConnection(sessionId);

      // Attempt to reconnect with exponential backoff
      if (reconnectAttemptsRef.current < maxReconnectAttempts) {
        const delay = Math.min(1000 * Math.pow(2, reconnectAttemptsRef.current), 10000);
        reconnectAttemptsRef.current += 1;

        reconnectTimeoutRef.current = setTimeout(() => {
          console.log(`Reconnecting... (attempt ${reconnectAttemptsRef.current})`);
          connect();
        }, delay);
      }
    };
  }, [sessionId, projectId, setWebSocketConnection, removeWebSocketConnection, updateSessionMetadata]);

  const handleStreamEvent = useCallback((event: { type: string; data?: any }) => {
    if (event.type === 'output' && event.data?.text) {
      // Append streaming text to the last assistant message
      setMessages((prev) => {
        const lastMessage = prev[prev.length - 1];
        if (lastMessage && lastMessage.role === 'assistant' && lastMessage.isStreaming) {
          return [
            ...prev.slice(0, -1),
            {
              ...lastMessage,
              content: lastMessage.content + event.data.text,
            },
          ];
        } else {
          // Create new assistant message
          return [
            ...prev,
            {
              role: 'assistant',
              content: event.data.text,
              isStreaming: true,
              timestamp: new Date().toISOString(),
            },
          ];
        }
      });
    }
  }, []);

  const sendMessage = useCallback(({ message, images, config }: SendMessageOptions) => {
    console.log('[useChatWebSocket] sendMessage called:', {
      sessionId,
      message: message.substring(0, 100),
      imagesCount: images?.length || 0,
      wsState: wsRef.current?.readyState,
      wsOpen: wsRef.current?.readyState === WebSocket.OPEN
    });

    if (!wsRef.current || wsRef.current.readyState !== WebSocket.OPEN) {
      console.error('[useChatWebSocket] WebSocket not connected:', {
        hasWs: !!wsRef.current,
        readyState: wsRef.current?.readyState,
        CONNECTING: WebSocket.CONNECTING,
        OPEN: WebSocket.OPEN,
        CLOSING: WebSocket.CLOSING,
        CLOSED: WebSocket.CLOSED
      });
      setError('WebSocket is not connected');
      return;
    }

    // Add user message to local state immediately
    const userMessage: ChatMessage = {
      role: 'user',
      content: message,
      images,
      timestamp: new Date().toISOString(),
    };
    setMessages((prev) => {
      console.log('[useChatWebSocket] Adding user message to state, current count:', prev.length);
      return [...prev, userMessage];
    });

    setIsStreaming(true);
    setError(null);

    // Send message via WebSocket
    const payload = {
      type: 'send_message',
      sessionId,
      message,
      images,
      config,
    };
    console.log('[useChatWebSocket] Sending WebSocket message:', payload);
    wsRef.current.send(JSON.stringify(payload));
  }, [sessionId]);

  const reconnect = useCallback(() => {
    reconnectAttemptsRef.current = 0;
    connect();
  }, [connect]);

  // Connect on mount and cleanup on unmount
  useEffect(() => {
    connect();

    return () => {
      if (reconnectTimeoutRef.current) {
        clearTimeout(reconnectTimeoutRef.current);
      }
      if (wsRef.current) {
        wsRef.current.close();
        wsRef.current = null;
      }
    };
  }, [connect]);

  return {
    messages,
    isConnected,
    isStreaming,
    error,
    sendMessage,
    reconnect,
    setMessages,
  };
}

import type { FastifyInstance } from "fastify";
import { AgentClient, createClaudeAdapter } from "@repo/agent-cli-sdk";
import { agentSessionService } from "./services/agent-session.service";
import { prisma } from "../shared/prisma";
import fs from "fs/promises";
import path from "path";
import os from "os";

// JWT payload interface (matching auth plugin)
interface JWTPayload {
  userId: string;
  username: string;
}

// WebSocket message types
interface SendMessagePayload {
  type: "send_message";
  sessionId: string;
  message: string;
  images?: string[]; // Array of base64-encoded images or file paths
  config?: Record<string, unknown>;
}

// Active sessions map: sessionId -> { agentClient, session, projectPath, userId }
const activeSessions = new Map<
  string,
  {
    agentClient: AgentClient;
    session: any;
    projectPath: string;
    userId: string;
    tempImageDir?: string;
  }
>();

export async function registerWebSocket(fastify: FastifyInstance) {
  fastify.register(async (fastify) => {
    // Original basic WebSocket endpoint
    fastify.get("/ws", { websocket: true }, (socket) => {
      fastify.log.info("WebSocket client connected");

      // Send welcome message
      socket.send(
        JSON.stringify({
          type: "connected",
          message: "Welcome to Agent Workflows UI",
          timestamp: new Date().toISOString(),
        })
      );

      // Handle incoming messages
      socket.on("message", (message) => {
        const data = JSON.parse(message.toString());
        fastify.log.info({ data }, "Received WebSocket message");

        // Echo back for now
        socket.send(
          JSON.stringify({
            type: "echo",
            data,
            timestamp: new Date().toISOString(),
          })
        );
      });

      // Handle disconnection
      socket.on("close", () => {
        fastify.log.info("WebSocket client disconnected");
      });
    });

    // Chat WebSocket endpoint with JWT authentication
    fastify.get(
      "/ws/chat/:sessionId",
      { websocket: true },
      async (socket, request) => {
        try {
          fastify.log.info("Chat WebSocket connection attempt");

          // Extract sessionId from params
          const { sessionId } = request.params as { sessionId: string };
          let userId: string | null = null;
          let projectPath: string | null = null;

          // Authenticate the WebSocket connection using JWT
          try {
            // Get token from query params (browser WebSocket doesn't support custom headers)
            const query = request.query as { token?: string };
            const token =
              query.token ||
              request.headers.authorization?.replace("Bearer ", "");

            if (!token) {
              socket.send(
                JSON.stringify({
                  type: "error",
                  message: "Authentication required",
                  sessionId,
                })
              );
              socket.close();
              return;
            }

            // Verify JWT token
            const decoded = fastify.jwt.verify<JWTPayload>(token);
            userId = decoded.userId;

            fastify.log.info(
              { userId, sessionId },
              "Chat WebSocket authenticated"
            );

            // Verify session exists and user has access
            const session = await prisma.agentSession.findUnique({
              where: { id: sessionId },
              include: { project: true },
            });

            if (!session) {
              socket.send(
                JSON.stringify({
                  type: "error",
                  message: "Session not found",
                  sessionId,
                })
              );
              socket.close();
              return;
            }

            if (session.userId !== userId) {
              socket.send(
                JSON.stringify({
                  type: "error",
                  message: "Unauthorized access to session",
                  sessionId,
                })
              );
              socket.close();
              return;
            }

            projectPath = session.project.path;

            // Send connection success
            socket.send(
              JSON.stringify({
                type: "connected",
                sessionId,
                timestamp: new Date().toISOString(),
              })
            );
          } catch (err: any) {
            fastify.log.error({ err }, "Chat WebSocket authentication failed");
            socket.send(
              JSON.stringify({
                type: "error",
                message: err.message || "Authentication failed",
                sessionId,
              })
            );
            socket.close();
            return;
          }

          // Handle incoming messages
          socket.on("message", async (message) => {
            try {
              fastify.log.info(
                { sessionId, userId, messageLength: message.toString().length },
                "[WebSocket] Received message from client"
              );

              const data = JSON.parse(message.toString()) as SendMessagePayload;

              fastify.log.info(
                {
                  type: data.type,
                  sessionId,
                  hasMessage: !!data.message,
                  imagesCount: data.images?.length || 0,
                },
                "[WebSocket] Parsed message data"
              );

              if (data.type === "send_message") {
                fastify.log.info(
                  {
                    sessionId,
                    userId,
                    messagePreview: data.message?.substring(0, 100),
                  },
                  "[WebSocket] Processing send_message request"
                );

                // Initialize agent-cli-sdk client if not already active
                let sessionData = activeSessions.get(sessionId);

                if (!sessionData && projectPath && userId) {
                  // Create Claude adapter
                  const claudeAdapter = createClaudeAdapter();

                  // Create agent client
                  const agentClient = new AgentClient({
                    adapter: claudeAdapter,
                    workingDirectory: projectPath,
                  });

                  // Create session with streaming callbacks
                  const agentSession = agentClient.createSession({
                    sessionId,
                    workingDirectory: projectPath,
                    onEvent: (event: any) => {
                      fastify.log.info(
                        { sessionId, eventType: event.type },
                        "[WebSocket] Received event from agent-cli-sdk"
                      );
                      // Stream events back to client
                      socket.send(
                        JSON.stringify({
                          type: "stream_event",
                          sessionId,
                          event,
                        })
                      );
                    },
                    onOutput: (outputData: any) => {
                      fastify.log.info(
                        { sessionId, outputType: typeof outputData },
                        "[WebSocket] Received output from agent-cli-sdk"
                      );
                      // Stream output back to client
                      socket.send(
                        JSON.stringify({
                          type: "stream_output",
                          sessionId,
                          data: outputData,
                        })
                      );
                    },
                  });

                  sessionData = {
                    agentClient,
                    session: agentSession,
                    projectPath,
                    userId,
                  };

                  activeSessions.set(sessionId, sessionData);
                }

                if (!sessionData) {
                  throw new Error("Failed to initialize session");
                }

                // Handle image uploads
                let imagePaths: string[] = [];
                if (data.images && data.images.length > 0) {
                  // Create temp directory for images
                  const timestamp = Date.now();
                  const tempImageDir = path.join(
                    sessionData.projectPath,
                    ".tmp",
                    "images",
                    String(timestamp)
                  );
                  await fs.mkdir(tempImageDir, { recursive: true });

                  sessionData.tempImageDir = tempImageDir;

                  // Save images to temp directory
                  for (let i = 0; i < data.images.length; i++) {
                    const image = data.images[i];

                    // Extract file extension from base64 data URL or use default
                    let ext = ".png";
                    if (image.startsWith("data:image/")) {
                      const mimeType = image.split(";")[0].split("/")[1];
                      ext = "." + mimeType;
                    }

                    const imagePath = path.join(
                      tempImageDir,
                      `image-${i}${ext}`
                    );

                    // If image is base64, decode and save
                    if (image.startsWith("data:")) {
                      const base64Data = image.split(",")[1];
                      await fs.writeFile(
                        imagePath,
                        Buffer.from(base64Data, "base64")
                      );
                    } else {
                      // Assume it's already a file path
                      await fs.copyFile(image, imagePath);
                    }

                    imagePaths.push(imagePath);
                  }
                }

                // Send message via agent-cli-sdk
                try {
                  fastify.log.info(
                    {
                      sessionId,
                      messageLength: data.message.length,
                      imagePaths: imagePaths.length,
                    },
                    "[WebSocket] Sending message to agent-cli-sdk"
                  );

                  const response = await sessionData.session.send(
                    data.message,
                    {
                      images: imagePaths.length > 0 ? imagePaths : undefined,
                      ...data.config,
                    }
                  );

                  console.log("sent:", data.message);

                  fastify.log.info(
                    {
                      sessionId,
                      responseType: typeof response,
                      response: JSON.stringify(response).substring(0, 200),
                    },
                    "[WebSocket] Received response from agent-cli-sdk"
                  );

                  // After message completes, update session metadata
                  let metadata = null;
                  try {
                    const jsonlPath = agentSessionService.getSessionFilePath(
                      sessionData.projectPath,
                      sessionId
                    );
                    metadata =
                      await agentSessionService.parseJSONLFile(jsonlPath);

                    await agentSessionService.updateSessionMetadata(
                      sessionId,
                      metadata
                    );
                  } catch (metadataErr: any) {
                    // JSONL file might not exist yet for new sessions
                    fastify.log.debug(
                      { err: metadataErr, sessionId },
                      "Could not update session metadata (file may not exist yet)"
                    );
                  }

                  // Clean up temporary images
                  if (sessionData.tempImageDir) {
                    try {
                      await fs.rm(sessionData.tempImageDir, {
                        recursive: true,
                        force: true,
                      });
                      sessionData.tempImageDir = undefined;
                    } catch (cleanupErr) {
                      fastify.log.warn(
                        { err: cleanupErr },
                        "Failed to clean up temp images"
                      );
                    }
                  }

                  // Send completion event
                  socket.send(
                    JSON.stringify({
                      type: "message_complete",
                      sessionId,
                      metadata,
                      response,
                    })
                  );
                } catch (err: any) {
                  fastify.log.error({ err }, "Agent CLI SDK error");

                  // Clean up temp images on error
                  if (sessionData.tempImageDir) {
                    try {
                      await fs.rm(sessionData.tempImageDir, {
                        recursive: true,
                        force: true,
                      });
                      sessionData.tempImageDir = undefined;
                    } catch (cleanupErr) {
                      fastify.log.warn(
                        { err: cleanupErr },
                        "Failed to clean up temp images"
                      );
                    }
                  }

                  socket.send(
                    JSON.stringify({
                      type: "error",
                      sessionId,
                      message: err.message || "Failed to send message",
                    })
                  );
                }
              }
            } catch (err: any) {
              fastify.log.error({ err }, "Error processing WebSocket message");
              socket.send(
                JSON.stringify({
                  type: "error",
                  sessionId,
                  message: err.message || "Internal server error",
                })
              );
            }
          });

          // Handle disconnection
          socket.on("close", () => {
            fastify.log.info(
              { sessionId, userId },
              "Chat WebSocket disconnected"
            );

            // Clean up session
            const sessionData = activeSessions.get(sessionId);
            if (sessionData) {
              // Clean up temp images if any
              if (sessionData.tempImageDir) {
                fs.rm(sessionData.tempImageDir, {
                  recursive: true,
                  force: true,
                }).catch((err) => {
                  fastify.log.warn(
                    { err },
                    "Failed to clean up temp images on disconnect"
                  );
                });
              }

              // Remove from active sessions
              activeSessions.delete(sessionId);
            }
          });

          // Handle errors
          socket.on("error", (err) => {
            fastify.log.error({ err, sessionId }, "Chat WebSocket error");

            // Clean up session
            const sessionData = activeSessions.get(sessionId);
            if (sessionData?.tempImageDir) {
              fs.rm(sessionData.tempImageDir, {
                recursive: true,
                force: true,
              }).catch((cleanupErr) => {
                fastify.log.warn(
                  { err: cleanupErr },
                  "Failed to clean up temp images on error"
                );
              });
            }

            activeSessions.delete(sessionId);
          });
        } catch (err) {
          fastify.log.error({ err }, "Fatal error in chat WebSocket handler");
          socket.close();
        }
      }
    );
  });
}

