    14→  signup: (username: string, password: string) => Promise<void>;
    15→  logout: () => void;
    16→  handleInvalidToken: () => void;
    17→}
    18→
    19→const AuthContext = createContext<AuthContextType | undefined>(undefined);
    20→
    21→export function AuthProvider({ children }: { children: ReactNode }) {
    22→  const navigate = useNavigate();
    23→  const [user, setUser] = useState<User | null>(() => {
    24→    // Check if user is stored in localStorage
    25→    const storedUser = localStorage.getItem("user");
    26→    return storedUser ? JSON.parse(storedUser) : null;
    27→  });
