    78→      localStorage.setItem("user", JSON.stringify(data.user));
    79→      setUser(data.user);
    80→
    81→      toast.success("Account created successfully");
    82→    } catch (error) {
    83→      const message = error instanceof Error ? error.message : "Registration failed";
    84→      toast.error(message);
    85→      throw error;
    86→    }
    87→  };
    88→
    89→  const logout = () => {
    90→    setUser(null);
    91→    localStorage.removeItem("user");
    92→    localStorage.removeItem("token");
    93→    toast.success("Logged out successfully");
    94→  };
    95→
    96→  const handleInvalidToken = () => {
    97→    setUser(null);
    98→    localStorage.removeItem("user");
    99→    localStorage.removeItem("token");
   100→    toast.error("Session expired. Please log in again.");
   101→    navigate("/login");
   102→  };
   103→
   104→  return (
   105→    <AuthContext.Provider
