     1→/**
     2→ * Hook to load and parse Claude session JSONL data with WebSocket streaming support
     3→ */
     4→
     5→import { useState, useEffect } from 'react';
     6→import type { ChatMessage } from '../../shared/types/chat';
     7→import { parseJSONLSession, extractToolResults } from '../utils/parseClaudeSession';
     8→import { useChatWebSocket } from './useChatWebSocket';
     9→import { useAuth } from '../contexts/AuthContext';
    10→
    11→interface UseClaudeSessionOptions {
    12→  sessionId: string;
    13→  projectId: string;
    14→  enableWebSocket?: boolean;
    15→}
    16→
    17→interface UseClaudeSessionReturn {
    18→  messages: ChatMessage[];
    19→  toolResults: Map<string, { content: string; is_error?: boolean }>;
    20→  isLoading: boolean;
    21→  error: Error | null;
    22→  isConnected?: boolean;
    23→  isStreaming?: boolean;
    24→  sendMessage?: (options: { message: string; images?: string[]; config?: Record<string, any> }) => void;
    25→  reconnect?: () => void;
    26→}
    27→
    28→/**
    29→ * Load and parse a Claude session from JSONL file with optional WebSocket streaming
    30→ *
    31→ * @param options - Session options including sessionId, projectId, and enableWebSocket
    32→ * @returns Parsed messages, tool results, loading state, error, and WebSocket controls
    33→ *
    34→ * @example
    35→ * ```tsx
    36→ * const { messages, isLoading, sendMessage } = useClaudeSession({
    37→ *   sessionId: 'abc-123',
    38→ *   projectId: 'project-1',
    39→ *   enableWebSocket: true
    40→ * });
    41→ * ```
    42→ */
    43→export function useClaudeSession(
    44→  options: UseClaudeSessionOptions
    45→): UseClaudeSessionReturn {
    46→  const { sessionId, projectId, enableWebSocket = false } = options;
    47→  const { handleInvalidToken } = useAuth();
    48→
    49→  const [messages, setMessages] = useState<ChatMessage[]>([]);
    50→  const [toolResults, setToolResults] = useState<Map<string, { content: string; is_error?: boolean }>>(
    51→    new Map()
    52→  );
    53→  const [isLoading, setIsLoading] = useState(true);
    54→  const [error, setError] = useState<Error | null>(null);
    55→
    56→  // Use WebSocket for real-time streaming if enabled
    57→  const webSocket = enableWebSocket
    58→    ? useChatWebSocket(sessionId, projectId)
    59→    : {
    60→        messages: [],
    61→        isConnected: false,
    62→        isStreaming: false,
    63→        error: null,
    64→        sendMessage: () => {},
    65→        reconnect: () => {},
    66→        setMessages: () => {},
    67→      };
    68→
    69→  // Load initial messages from JSONL file
    70→  useEffect(() => {
    71→    let cancelled = false;
    72→
    73→    const loadSessionMessages = async () => {
    74→      try {
    75→        setIsLoading(true);
    76→        setError(null);
    77→
    78→        const token = localStorage.getItem('token');
    79→        const response = await fetch(`/api/projects/${projectId}/sessions/${sessionId}/messages`, {
    80→          headers: {
    81→            'Authorization': `Bearer ${token}`,
    82→          },
    83→        });
    84→
    85→        if (!response.ok) {
    86→          // Handle 401 Unauthorized - invalid or missing token
    87→          if (response.status === 401) {
    88→            handleInvalidToken();
    89→            throw new Error('Session expired');
    90→          }
    91→          throw new Error(`Failed to load session messages: ${response.statusText}`);
    92→        }
    93→
    94→        const data = await response.json();
    95→        const messagesArray = data.data || [];
    96→
    97→        if (cancelled) return;
    98→
    99→        // API already returns parsed messages array
   100→        const parsedMessages: ChatMessage[] = messagesArray.map((msg: any) => ({
   101→          id: msg.id,
   102→          role: msg.role as 'user' | 'assistant',
   103→          content: typeof msg.content === 'string'
   104→            ? msg.content
   105→            : Array.isArray(msg.content)
   106→              ? msg.content.filter((c: any) => c.type === 'text').map((c: any) => c.text).join(' ')
   107→              : '',
   108→          timestamp: msg.timestamp,
   109→          images: msg.images,
   110→        }));
   111→
   112→        // Extract tool results from messages
   113→        const toolResultsMap = new Map<string, { content: string; is_error?: boolean }>();
   114→        messagesArray.forEach((msg: any) => {
   115→          if (msg.role === 'assistant' && Array.isArray(msg.content)) {
   116→            msg.content.forEach((block: any) => {
   117→              if (block.type === 'tool_result' && block.tool_use_id) {
   118→                toolResultsMap.set(block.tool_use_id, {
   119→                  content: typeof block.content === 'string' ? block.content : JSON.stringify(block.content),
   120→                  is_error: block.is_error,
   121→                });
   122→              }
   123→            });
   124→          }
   125→        });
   126→
   127→        console.log('Loaded session messages:', parsedMessages.length);
   128→
   129→        setMessages(parsedMessages);
   130→        setToolResults(toolResultsMap);
   131→
   132→        // If WebSocket is enabled, initialize it with existing messages
   133→        if (enableWebSocket && webSocket.setMessages) {
   134→          webSocket.setMessages(parsedMessages.map(msg => ({
   135→            role: msg.role as 'user' | 'assistant',
   136→            content: msg.content,
   137→            timestamp: msg.timestamp,
   138→            images: msg.images,
   139→          })));
   140→        }
   141→      } catch (err) {
   142→        if (!cancelled) {
   143→          const errorMessage = err instanceof Error ? err.message : 'Unknown error loading session';
   144→          setError(new Error(errorMessage));
   145→          console.error('Error loading session:', err);
   146→        }
   147→      } finally {
   148→        if (!cancelled) {
   149→          setIsLoading(false);
   150→        }
   151→      }
   152→    };
   153→
   154→    if (sessionId && projectId) {
   155→      loadSessionMessages();
   156→    }
   157→
   158→    return () => {
   159→      cancelled = true;
   160→    };
   161→  }, [sessionId, projectId, enableWebSocket]);
   162→
   163→  // Merge JSONL messages with WebSocket messages when WebSocket is enabled
   164→  const finalMessages = enableWebSocket ? webSocket.messages : messages;
   165→  const finalError = error || (enableWebSocket && webSocket.error ? new Error(webSocket.error) : null);
   166→
   167→  return {
   168→    messages: finalMessages,
   169→    toolResults,
   170→    isLoading,
   171→    error: finalError,
   172→    isConnected: enableWebSocket ? webSocket.isConnected : undefined,
   173→    isStreaming: enableWebSocket ? webSocket.isStreaming : undefined,
   174→    sendMessage: enableWebSocket ? webSocket.sendMessage : undefined,
   175→    reconnect: enableWebSocket ? webSocket.reconnect : undefined,
   176→  };
   177→}
